     1use anyhow::Result;
     2use wasm_bindgen::prelude::*;
     3use wasm_bindgen_futures::spawn_local;
     4use web_sys::{
     5    console, Document, Element, HtmlCanvasElement, HtmlElement, MessageEvent, 
     6    WebSocket, Window, Storage,
     7};
     8use serde::{Deserialize, Serialize};
     9use std::{
    10    cell::RefCell,
    11    collections::HashMap,
    12    rc::Rc,
    13    sync::Arc,
    14};
    15use tokio::sync::{mpsc, RwLock};
    16use js_sys::{Array, Function, Object, Promise, Uint8Array};
    17
    18/// WebAssembly bridge for running Katalyst Terminal in browsers
    19#[wasm_bindgen]
    20pub struct WasmTerminalBridge {
    21    #[wasm_bindgen(skip)]
    22    pub config: BridgeConfig,
    23    
    24    #[wasm_bindgen(skip)]
    25    pub websocket: Option<WebSocket>,
    26    
    27    #[wasm_bindgen(skip)]
    28    pub canvas: Option<HtmlCanvasElement>,
    29    
    30    #[wasm_bindgen(skip)]
    31    pub sessions: Rc<RefCell<HashMap<String, WasmSession>>>,
    32    
    33    #[wasm_bindgen(skip)]
    34    pub message_handlers: Rc<RefCell<HashMap<String, js_sys::Function>>>,
    35    
    36    #[wasm_bindgen(skip)]
    37    pub connection_state: Rc<RefCell<ConnectionState>>,
    38}
    39
    40#[wasm_bindgen]
    41impl WasmTerminalBridge {
    42    #[wasm_bindgen(constructor)]
    43    pub fn new(config_json: &str) -> Result<WasmTerminalBridge, JsValue> {
    44        console_error_panic_hook::set_once();
    45        
    46        let config: BridgeConfig = serde_json::from_str(config_json)
    47            .map_err(|e| JsValue::from_str(&e.to_string()))?;
    48        
    49        Ok(WasmTerminalBridge {
    50            config,
    51            websocket: None,
    52            canvas: None,
    53            sessions: Rc::new(RefCell::new(HashMap::new())),
    54            message_handlers: Rc::new(RefCell::new(HashMap::new())),
    55            connection_state: Rc::new(RefCell::new(ConnectionState::Disconnected)),
    56        })
    57    }
    58    
    59    /// Initialize the terminal in the browser
    60    #[wasm_bindgen]
    61    pub async fn initialize(&mut self, container_id: &str) -> Result<(), JsValue> {
    62        console::log_1(&"Initializing Katalyst Terminal in browser".into());
    63        
    64        // Get the container element
    65        let window = web_sys::window().unwrap();
    66        let document = window.document().unwrap();
    67        let container = document.get_element_by_id(container_id)
    68            .ok_or_else(|| JsValue::from_str("Container element not found"))?;
    69        
    70        // Create canvas for terminal rendering
    71        let canvas = document.create_element("canvas")
    72            .map_err(|e| JsValue::from_str(&format!("Failed to create canvas: {:?}", e)))?
    73            .dyn_into::<HtmlCanvasElement>()
    74            .map_err(|_| JsValue::from_str("Failed to cast to canvas"))?;
    75        
    76        canvas.set_width(self.config.width);
    77        canvas.set_height(self.config.height);
    78        canvas.set_id("katalyst-terminal-canvas");
    79        canvas.style().set_property("width", "100%")?;
    80        canvas.style().set_property("height", "100%")?;
    81        canvas.style().set_property("display", "block")?;
    82        
    83        container.append_child(&canvas)?;
    84        self.canvas = Some(canvas);
    85        
    86        // Set up event handlers
    87        self.setup_event_handlers()?;
    88        
    89        // Initialize local storage for settings
    90        self.initialize_storage()?;
    91        
    92        console::log_1(&"Terminal initialized successfully".into());
    93        Ok(())
    94    }
    95    
    96    /// Connect to a remote Katalyst server
    97    #[wasm_bindgen]
    98    pub async fn connect_to_server(&mut self, url: &str) -> Result<(), JsValue> {
    99        console::log_1(&format!("Connecting to server: {}", url).into());
   100        
   101        // Update connection state
   102        *self.connection_state.borrow_mut() = ConnectionState::Connecting;
   103        
   104        // Create WebSocket connection
   105        let ws = WebSocket::new(url)
   106            .map_err(|e| JsValue::from_str(&format!("Failed to create WebSocket: {:?}", e)))?;
   107        
   108        // Set up WebSocket event handlers
   109        self.setup_websocket_handlers(&ws)?;
   110        
   111        self.websocket = Some(ws);
   112        
   113        Ok(())
   114    }
   115    
   116    /// Create a new development session
   117    #[wasm_bindgen]
   118    pub async fn create_dev_session(&mut self, repo_url: &str) -> Result<String, JsValue> {
   119        console::log_1(&format!("Creating dev session for: {}", repo_url).into());
   120        
   121        let session_id = format!("wasm-session-{}", uuid::Uuid::new_v4());
   122        
   123        // Send request to server to create container
   124        let request = ServerRequest::CreateSession {
   125            session_id: session_id.clone(),
   126            repo_url: repo_url.to_string(),
   127            session_type: SessionType::Development,
   128        };
   129        
   130        self.send_to_server(&request)?;
   131        
   132        // Create local session tracking
   133        let session = WasmSession {
   134            id: session_id.clone(),
   135            repo_url: Some(repo_url.to_string()),
   136            session_type: SessionType::Development,
   137            created_at: js_sys::Date::now(),
   138            active: true,
   139        };
   140        
   141        self.sessions.borrow_mut().insert(session_id.clone(), session);
   142        
   143        Ok(session_id)
   144    }
   145    
   146    /// Send terminal input to the server
   147    #[wasm_bindgen]
   148    pub fn send_input(&self, session_id: &str, input: &str) -> Result<(), JsValue> {
   149        let request = ServerRequest::TerminalInput {
   150            session_id: session_id.to_string(),
   151            input: input.to_string(),
   152        };
   153        
   154        self.send_to_server(&request)?;
   155        Ok(())
   156    }
   157    
   158    /// Execute a command in the terminal
   159    #[wasm_bindgen]
   160    pub fn execute_command(&self, session_id: &str, command: &str) -> Result<(), JsValue> {
   161        let request = ServerRequest::ExecuteCommand {
   162            session_id: session_id.to_string(),
   163            command: command.to_string(),
   164        };
   165        
   166        self.send_to_server(&request)?;
   167        Ok(())
   168    }
   169    
   170    /// Resize the terminal
   171    #[wasm_bindgen]
   172    pub fn resize(&mut self, width: u32, height: u32) -> Result<(), JsValue> {
   173        self.config.width = width;
   174        self.config.height = height;
   175        
   176        if let Some(canvas) = &self.canvas {
   177            canvas.set_width(width);
   178            canvas.set_height(height);
   179        }
   180        
   181        // Notify server of resize
   182        let request = ServerRequest::Resize {
   183            width,
   184            height,
   185        };
   186        
   187        self.send_to_server(&request)?;
   188        Ok(())
   189    }
   190    
   191    /// Register a JavaScript callback for events
   192    #[wasm_bindgen]
   193    pub fn on(&mut self, event: &str, callback: js_sys::Function) {
   194        self.message_handlers.borrow_mut().insert(event.to_string(), callback);
   195    }
   196    
   197    /// Get list of active sessions
   198    #[wasm_bindgen]
   199    pub fn get_sessions(&self) -> JsValue {
   200        let sessions: Vec<SessionInfo> = self.sessions.borrow()
   201            .values()
   202            .map(|s| SessionInfo {
   203                id: s.id.clone(),
   204                repo_url: s.repo_url.clone(),
   205                session_type: s.session_type.clone(),
   206                active: s.active,
   207            })
   208            .collect();
   209        
   210        JsValue::from_serde(&sessions).unwrap_or(JsValue::NULL)
   211    }
   212    
   213    /// Close a session
   214    #[wasm_bindgen]
   215    pub fn close_session(&self, session_id: &str) -> Result<(), JsValue> {
   216        self.sessions.borrow_mut().remove(session_id);
   217        
   218        let request = ServerRequest::CloseSession {
   219            session_id: session_id.to_string(),
   220        };
   221        
   222        self.send_to_server(&request)?;
   223        Ok(())
   224    }
   225    
   226    /// Disconnect from server
   227    #[wasm_bindgen]
   228    pub fn disconnect(&mut self) {
   229        if let Some(ws) = &self.websocket {
   230            let _ = ws.close();
   231        }
   232        
   233        self.websocket = None;
   234        *self.connection_state.borrow_mut() = ConnectionState::Disconnected;
   235    }
   236    
   237    // Private helper methods
   238    fn send_to_server(&self, request: &ServerRequest) -> Result<(), JsValue> {
   239        if let Some(ws) = &self.websocket {
   240            let message = serde_json::to_string(request)
   241                .map_err(|e| JsValue::from_str(&e.to_string()))?;
   242            
   243            ws.send_with_str(&message)
   244                .map_err(|e| JsValue::from_str(&format!("Failed to send message: {:?}", e)))?;
   245        } else {
   246            return Err(JsValue::from_str("Not connected to server"));
   247        }
   248        
   249        Ok(())
   250    }
   251    
   252    fn setup_event_handlers(&self) -> Result<(), JsValue> {
   253        let window = web_sys::window().unwrap();
   254        let document = window.document().unwrap();
   255        
   256        // Keyboard events
   257        let sessions = self.sessions.clone();
   258        let websocket = self.websocket.clone();
   259        
   260        let keydown_closure = Closure::wrap(Box::new(move |event: web_sys::KeyboardEvent| {
   261            // Handle keyboard input
   262            console::log_1(&format!("Key pressed: {}", event.key()).into());
   263            
   264            // Send to active session
   265            if let Some(active_session) = sessions.borrow().values().find(|s| s.active) {
   266                // Send key to server
   267                console::log_1(&format!("Sending key to session: {}", active_session.id).into());
   268            }
   269        }) as Box<dyn FnMut(_)>);
   270        
   271        document.add_event_listener_with_callback(
   272            "keydown",
   273            keydown_closure.as_ref().unchecked_ref()
   274        )?;
   275        keydown_closure.forget();
   276        
   277        // Mouse events for terminal selection
   278        if let Some(canvas) = &self.canvas {
   279            let mouse_closure = Closure::wrap(Box::new(move |event: web_sys::MouseEvent| {
   280                console::log_1(&format!("Mouse click at: {}, {}", event.offset_x(), event.offset_y()).into());
   281            }) as Box<dyn FnMut(_)>);
   282            
   283            canvas.add_event_listener_with_callback(
   284                "click",
   285                mouse_closure.as_ref().unchecked_ref()
   286            )?;
   287            mouse_closure.forget();
   288        }
   289        
   290        // Window resize
   291        let resize_closure = Closure::wrap(Box::new(move |_event: web_sys::Event| {
   292            console::log_1(&"Window resized".into());
   293        }) as Box<dyn FnMut(_)>);
   294        
   295        window.add_event_listener_with_callback(
   296            "resize",
   297            resize_closure.as_ref().unchecked_ref()
   298        )?;
   299        resize_closure.forget();
   300        
   301        Ok(())
   302    }
   303    
   304    fn setup_websocket_handlers(&self, ws: &WebSocket) -> Result<(), JsValue> {
   305        let connection_state = self.connection_state.clone();
   306        let sessions = self.sessions.clone();
   307        let handlers = self.message_handlers.clone();
   308        
   309        // On open
   310        let on_open = Closure::wrap(Box::new(move |_| {
   311            console::log_1(&"WebSocket connected".into());
   312            *connection_state.borrow_mut() = ConnectionState::Connected;
   313            
   314            // Trigger connected callback
   315            if let Some(handler) = handlers.borrow().get("connected") {
   316                let _ = handler.call0(&JsValue::NULL);
   317            }
   318        }) as Box<dyn FnMut(_)>);
   319        
   320        ws.set_onopen(Some(on_open.as_ref().unchecked_ref()));
   321        on_open.forget();
   322        
   323        // On message
   324        let handlers = self.message_handlers.clone();
   325        let sessions = self.sessions.clone();
   326        
   327        let on_message = Closure::wrap(Box::new(move |event: MessageEvent| {
   328            if let Ok(text) = event.data().dyn_into::<js_sys::JsString>() {
   329                let message_str: String = text.into();
   330                
   331                // Parse server response
   332                if let Ok(response) = serde_json::from_str::<ServerResponse>(&message_str) {
   333                    match response {
   334                        ServerResponse::SessionCreated { session_id } => {
   335                            console::log_1(&format!("Session created: {}", session_id).into());
   336                            
   337                            if let Some(handler) = handlers.borrow().get("session_created") {
   338                                let _ = handler.call1(&JsValue::NULL, &JsValue::from_str(&session_id));
   339                            }
   340                        }
   341                        ServerResponse::TerminalOutput { session_id, output } => {
   342                            if let Some(handler) = handlers.borrow().get("terminal_output") {
   343                                let args = js_sys::Object::new();
   344                                js_sys::Reflect::set(&args, &"session_id".into(), &session_id.into()).unwrap();
   345                                js_sys::Reflect::set(&args, &"output".into(), &output.into()).unwrap();
   346                                let _ = handler.call1(&JsValue::NULL, &args);
   347                            }
   348                        }
   349                        ServerResponse::Error { message } => {
   350                            console::error_1(&format!("Server error: {}", message).into());
   351                            
   352                            if let Some(handler) = handlers.borrow().get("error") {
   353                                let _ = handler.call1(&JsValue::NULL, &JsValue::from_str(&message));
   354                            }
   355                        }
   356                        _ => {}
   357                    }
   358                }
   359            }
   360        }) as Box<dyn FnMut(_)>);
   361        
   362        ws.set_onmessage(Some(on_message.as_ref().unchecked_ref()));
   363        on_message.forget();
   364        
   365        // On error
   366        let handlers = self.message_handlers.clone();
   367        let on_error = Closure::wrap(Box::new(move |_| {
   368            console::error_1(&"WebSocket error occurred".into());
   369            
   370            if let Some(handler) = handlers.borrow().get("error") {
   371                let _ = handler.call1(&JsValue::NULL, &JsValue::from_str("WebSocket error"));
   372            }
   373        }) as Box<dyn FnMut(_)>);
   374        
   375        ws.set_onerror(Some(on_error.as_ref().unchecked_ref()));
   376        on_error.forget();
   377        
   378        // On close
