        console_log!("Running terminal command: {}", command);

        let mut terminal = self.terminal.write().await;
        let output = terminal.execute_command(&command).await
            .map_err(|e| JsValue::from_str(&format!("Terminal command failed: {}", e)))?;

        Ok(output)
    }

