{
  "name": "Pack-a-Punched Framework Development",
  "dockerComposeFile": "docker-compose.yml",
  "service": "pack-a-punched-dev",
  "workspaceFolder": "/workspace",
  "shutdownAction": "stopCompose",

  "features": {
    "ghcr.io/devcontainers/features/docker-outside-of-docker:1": {
      "version": "latest",
      "enableNonRootDocker": "true"
    },
    "ghcr.io/devcontainers/features/github-cli:1": {
      "version": "latest"
    },
    "ghcr.io/devcontainers/features/rust:1": {
      "version": "latest",
      "profile": "default"
    },
    "ghcr.io/devcontainers/features/node:1": {
      "version": "lts"
    }
  },

  "forwardPorts": [8080, 8081, 3000],
  "portsAttributes": {
    "8080": {
      "label": "GitHub Copilot Extension Server",
      "onAutoForward": "notify",
      "visibility": "public"
    },
    "8081": {
      "label": "Zed Agent Server",
      "onAutoForward": "silent",
      "visibility": "private"
    },
    "3000": {
      "label": "Development Server",
      "onAutoForward": "silent",
      "visibility": "private"
    }
  },

  "containerEnv": {
    "GITHUB_APP_ID": "${localEnv:GITHUB_APP_ID}",
    "GITHUB_APP_INSTALLATION_ID": "${localEnv:GITHUB_APP_INSTALLATION_ID}",
    "GITHUB_APP_CLIENT_SECRET": "68cb57250064ccd8ffadc7cedd806f0c6f1e1a23",
    "GITHUB_TOKEN": "${localEnv:GITHUB_TOKEN}",
    "ENVIRONMENT": "development",
    "SERVER_HOST": "0.0.0.0",
    "SERVER_PORT": "8080",
    "LOG_LEVEL": "info",
    "RUST_LOG": "info",
    "COPILOT_EXTENSION_ID": "pack-a-punched-github-copilot"
  },

  "mounts": [
    "source=${localWorkspaceFolder}/.secrets,target=/workspace/.secrets,type=bind,consistency=cached"
  ],

  "customizations": {
    "vscode": {
      "settings": {
        "terminal.integrated.defaultProfile.linux": "bash",
        "terminal.integrated.profiles.linux": {
          "bash": {
            "path": "/bin/bash",
            "args": ["--login"]
          }
        },

        "rust-analyzer.checkOnSave.command": "clippy",
        "rust-analyzer.cargo.allFeatures": true,
        "rust-analyzer.procMacro.enable": true,
        "rust-analyzer.completion.addCallParentheses": false,
        "rust-analyzer.completion.addCallArgumentSnippets": false,

        "elixirLS.dialyzerEnabled": true,
        "elixirLS.fetchDeps": true,
        "elixirLS.suggestSpecs": true,
        "elixirLS.mixEnv": "dev",
        "elixirLS.projectDir": "perks",

        "typescript.preferences.includePackageJsonAutoImports": "on",
        "typescript.suggest.autoImports": true,
        "javascript.suggest.autoImports": true,

        "github.copilot.enable": {
          "*": true,
          "yaml": false,
          "plaintext": false,
          "markdown": false
        },

        "files.associations": {
          "*.toml": "toml",
          "Dockerfile*": "dockerfile",
          "*.dockerfile": "dockerfile"
        },

        "remote.containers.copyGitConfig": true,
        "remote.containers.gitCredentialHelper": true,

        "editor.formatOnSave": true,
        "editor.codeActionsOnSave": {
          "source.fixAll": true,
          "source.organizeImports": true
        },

        "terminal.integrated.env.linux": {
          "GITHUB_COPILOT_EXTENSION_DEV": "true",
          "LAPDEB_CLOUD_DEPLOYMENT": "true"
        }
      },

      "extensions": [
        "rust-lang.rust-analyzer",
        "vadimcn.vscode-lldb",
        "serayuzgur.crates",
        "tamasfe.even-better-toml",
        "ms-vscode.vscode-json",
        "bradlc.vscode-tailwindcss",
        "phoenixframework.phoenix",
        "jakebecker.elixir-ls",
        "github.copilot",
        "github.copilot-chat",
        "ms-vscode.makefile-tools",
        "ms-azuretools.vscode-docker",
        "ms-vscode-remote.remote-containers",
        "github.vscode-github-actions"
      ]
    }
  },

  "postCreateCommand": "bash .devcontainer/post-create.sh",

  "remoteUser": "vscode",

  "secrets": {
    "GITHUB_TOKEN": {
      "description": "GitHub Personal Access Token for API access"
    },
    "GITHUB_APP_ID": {
      "description": "GitHub App ID for Copilot Extension"
    },
    "GITHUB_APP_INSTALLATION_ID": {
      "description": "GitHub App Installation ID"
    }
  },

  "otherPortsAttributes": {
    "onAutoForward": "silent"
  },

  "hostRequirements": {
    "memory": "4gb",
    "storage": "32gb"
  }
}

