//! vNomad-rs BAML Integration Module
//! 
//! State-of-the-art Computer Use Agent with UI-TARS-1.5 integration
//! Advanced prompt engineering patterns and multi-OS automation

use std::collections::HashMap;
use std::path::PathBuf;
use std::sync::Arc;
use tokio::sync::RwLock;
use serde::{Deserialize, Serialize};
use anyhow::{Result, Context};
use tracing::{info, warn, error, debug, instrument};

// BAML Client Integration
baml_client::client!(BAMLClient);

/// Core BAML integration client for vNomad-rs
pub struct VNomadBAMLClient {
    client: BAMLClient,
    config: VNomadConfig,
    ui_tars: Option<Arc<UITARSIntegration>>,
    performance_monitor: Arc<RwLock<PerformanceMonitor>>,
    safety_validator: SafetyValidator,
}

/// Configuration for vNomad BAML integration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VNomadConfig {
    pub environment: String,
    pub debug_mode: bool,
    pub telemetry_enabled: bool,
    pub ui_tars_config: UITARSConfig,
    pub security_config: SecurityConfig,
    pub performance_config: PerformanceConfig,
    pub multi_os_config: MultiOSConfig,
}

/// UI-TARS-1.5 integration configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct UITARSConfig {
    pub model_size: UITARSModelSize,
    pub precision: UITARSPrecision,
    pub backend: UITARSBackend,
    pub model_path: PathBuf,
    pub device_id: i32,
    pub memory_optimization: bool,
    pub quantization_enabled: bool,
    pub thread_count: usize,
}

