     1use anyhow::{anyhow, Result};
     2use oqs::{kem, sig};
     3use rand::{rngs::OsRng, RngCore};
     4use serde::{Deserialize, Serialize};
     5use std::collections::HashMap;
     6use zeroize::{Zeroize, ZeroizeOnDrop};
     7
     8/// Post-Quantum Cryptography manager for defense-grade quantum resistance
     9pub struct PQCManager {
    10    key_encapsulation: HashMap<PQCAlgorithm, kem::Kem>,
    11    digital_signature: HashMap<PQCAlgorithm, sig::Sig>,
    12    hybrid_mode: bool,
    13}
    14
    15#[derive(Debug, Clone, Hash, Eq, PartialEq, Serialize, Deserialize)]
    16pub enum PQCAlgorithm {
    17    // NIST standardized KEMs
    18    Kyber512,
    19    Kyber768,
    20    Kyber1024,
    21    
    22    // NIST standardized signatures
    23    Dilithium2,
    24    Dilithium3,
    25    Dilithium5,
    26    
    27    // Alternative candidates
    28    Falcon512,
    29    Falcon1024,
    30    SPHINCS_SHA256_128s,
    31    SPHINCS_SHAKE256_128s,
    32    
    33    // Hybrid classical+post-quantum
    34    HybridRSA4096_Kyber768,
    35    HybridECDSA384_Dilithium3,
    36}
    37
    38#[derive(Debug, Clone, Serialize, Deserialize, ZeroizeOnDrop)]
    39pub struct PQCKeyPair {
    40    pub algorithm: PQCAlgorithm,
    41    pub public_key: Vec<u8>,
    42    #[zeroize(skip)]
    43    pub private_key: SecretKey,
    44    pub created_at: std::time::SystemTime,
    45    pub metadata: KeyMetadata,
    46}
    47
    48#[derive(Debug, Clone, Serialize, Deserialize, ZeroizeOnDrop)]
    49pub struct SecretKey {
    50    #[zeroize(skip)]
    51    inner: Vec<u8>,
    52}
    53
    54impl SecretKey {
    55    pub fn new(key_data: Vec<u8>) -> Self {
    56        Self { inner: key_data }
    57    }
    58    
    59    pub fn as_bytes(&self) -> &[u8] {
    60        &self.inner
    61    }
    62}
    63
    64#[derive(Debug, Clone, Serialize, Deserialize)]
    65pub struct KeyMetadata {
    66    pub purpose: KeyPurpose,
    67    pub security_level: SecurityLevel,
    68    pub rotation_policy: RotationPolicy,
    69    pub compliance_frameworks: Vec<String>,
    70}
    71
    72#[derive(Debug, Clone, Serialize, Deserialize)]
    73pub enum KeyPurpose {
    74    KeyEncapsulation,
    75    DigitalSignature,
    76    KeyDerivation,
    77    Attestation,
    78}
    79
    80#[derive(Debug, Clone, Serialize, Deserialize)]
    81pub enum SecurityLevel {
    82    NIST1,  // 128-bit classical security
    83    NIST3,  // 192-bit classical security  
    84    NIST5,  // 256-bit classical security
    85}
    86
    87#[derive(Debug, Clone, Serialize, Deserialize)]
    88pub struct RotationPolicy {
    89    pub max_age: std::time::Duration,
    90    pub max_operations: Option<u64>,
    91    pub auto_rotate: bool,
    92}
    93
    94#[derive(Debug, Serialize, Deserialize, ZeroizeOnDrop)]
    95pub struct EncapsulatedSecret {
    96    pub algorithm: PQCAlgorithm,
    97    pub ciphertext: Vec<u8>,
    98    #[zeroize(skip)]
    99    pub shared_secret: SecretKey,
   100}
   101
   102#[derive(Debug, Serialize, Deserialize)]
   103pub struct PQCSignature {
   104    pub algorithm: PQCAlgorithm,
   105    pub signature: Vec<u8>,
   106    pub message_hash: Vec<u8>,
   107    pub timestamp: std::time::SystemTime,
   108}
   109
   110#[derive(Debug, Serialize, Deserialize, ZeroizeOnDrop)]
   111pub struct HybridSharedSecret {
   112    #[zeroize(skip)]
   113    pub classical_secret: Vec<u8>,
   114    #[zeroize(skip)]
   115    pub quantum_secret: Vec<u8>,
   116    #[zeroize(skip)]
   117    pub combined_secret: Vec<u8>,
   118    pub algorithms: (String, PQCAlgorithm),
   119}
   120
   121impl PQCManager {
   122    /// Create new PQC manager with recommended algorithms
   123    pub fn new(hybrid_mode: bool) -> Result<Self> {
   124        let mut key_encapsulation = HashMap::new();
   125        let mut digital_signature = HashMap::new();
   126        
   127        // Initialize NIST standardized KEMs
   128        key_encapsulation.insert(
   129            PQCAlgorithm::Kyber512,
   130            kem::Kem::new(kem::Algorithm::Kyber512)
   131                .map_err(|e| anyhow!("Failed to initialize Kyber512: {:?}", e))?,
   132        );
   133        
   134        key_encapsulation.insert(
   135            PQCAlgorithm::Kyber768,
   136            kem::Kem::new(kem::Algorithm::Kyber768)
   137                .map_err(|e| anyhow!("Failed to initialize Kyber768: {:?}", e))?,
   138        );
   139        
   140        key_encapsulation.insert(
   141            PQCAlgorithm::Kyber1024,
   142            kem::Kem::new(kem::Algorithm::Kyber1024)
   143                .map_err(|e| anyhow!("Failed to initialize Kyber1024: {:?}", e))?,
   144        );
   145        
   146        // Initialize NIST standardized signature schemes
   147        digital_signature.insert(
   148            PQCAlgorithm::Dilithium2,
   149            sig::Sig::new(sig::Algorithm::Dilithium2)
   150                .map_err(|e| anyhow!("Failed to initialize Dilithium2: {:?}", e))?,
   151        );
   152        
   153        digital_signature.insert(
   154            PQCAlgorithm::Dilithium3,
   155            sig::Sig::new(sig::Algorithm::Dilithium3)
   156                .map_err(|e| anyhow!("Failed to initialize Dilithium3: {:?}", e))?,
   157        );
   158        
   159        digital_signature.insert(
   160            PQCAlgorithm::Dilithium5,
   161            sig::Sig::new(sig::Algorithm::Dilithium5)
   162                .map_err(|e| anyhow!("Failed to initialize Dilithium5: {:?}", e))?,
   163        );
   164        
   165        // Initialize Falcon signatures
   166        digital_signature.insert(
   167            PQCAlgorithm::Falcon512,
   168            sig::Sig::new(sig::Algorithm::Falcon512)
   169                .map_err(|e| anyhow!("Failed to initialize Falcon512: {:?}", e))?,
   170        );
   171        
   172        digital_signature.insert(
   173            PQCAlgorithm::Falcon1024,
   174            sig::Sig::new(sig::Algorithm::Falcon1024)
   175                .map_err(|e| anyhow!("Failed to initialize Falcon1024: {:?}", e))?,
   176        );
   177        
   178        // Initialize SPHINCS+
   179        digital_signature.insert(
   180            PQCAlgorithm::SPHINCS_SHA256_128s,
   181            sig::Sig::new(sig::Algorithm::SphincsShake256128sSimple)
   182                .map_err(|e| anyhow!("Failed to initialize SPHINCS: {:?}", e))?,
   183        );
   184        
   185        Ok(Self {
   186            key_encapsulation,
   187            digital_signature,
   188            hybrid_mode,
   189        })
   190    }
   191    
   192    /// Generate quantum-resistant key pair
   193    pub fn generate_keypair(&self, algorithm: PQCAlgorithm) -> Result<PQCKeyPair> {
   194        let metadata = KeyMetadata {
   195            purpose: self.get_key_purpose(&algorithm),
   196            security_level: self.get_security_level(&algorithm),
   197            rotation_policy: RotationPolicy {
   198                max_age: std::time::Duration::from_secs(30 * 24 * 60 * 60), // 30 days
   199                max_operations: Some(1_000_000),
   200                auto_rotate: true,
   201            },
   202            compliance_frameworks: vec![
   203                "NIST-PQC".to_string(),
   204                "CNSS-CNSSP-15".to_string(),
   205                "ETSI-TS-103-823".to_string(),
   206            ],
   207        };
   208        
   209        match algorithm {
   210            // KEM algorithms
   211            PQCAlgorithm::Kyber512 | PQCAlgorithm::Kyber768 | PQCAlgorithm::Kyber1024 => {
   212                let kem = self.key_encapsulation.get(&algorithm)
   213                    .ok_or_else(|| anyhow!("KEM algorithm not initialized"))?;
   214                
   215                let (public_key, private_key) = kem.keypair()
   216                    .map_err(|e| anyhow!("Failed to generate keypair: {:?}", e))?;
   217                
   218                Ok(PQCKeyPair {
   219                    algorithm,
   220                    public_key,
   221                    private_key: SecretKey::new(private_key),
   222                    created_at: std::time::SystemTime::now(),
   223                    metadata,
   224                })
   225            }
   226            
   227            // Signature algorithms
   228            _ => {
   229                let sig_scheme = self.digital_signature.get(&algorithm)
   230                    .ok_or_else(|| anyhow!("Signature algorithm not initialized"))?;
   231                
   232                let (public_key, private_key) = sig_scheme.keypair()
   233                    .map_err(|e| anyhow!("Failed to generate keypair: {:?}", e))?;
   234                
   235                Ok(PQCKeyPair {
   236                    algorithm,
   237                    public_key,
   238                    private_key: SecretKey::new(private_key),
   239                    created_at: std::time::SystemTime::now(),
   240                    metadata,
   241                })
   242            }
   243        }
   244    }
   245    
   246    /// Perform key encapsulation (KEM)
   247    pub fn encapsulate(
   248        &self,
   249        public_key: &[u8],
   250        algorithm: PQCAlgorithm,
   251    ) -> Result<EncapsulatedSecret> {
   252        let kem = self.key_encapsulation.get(&algorithm)
   253            .ok_or_else(|| anyhow!("KEM algorithm not available"))?;
   254        
   255        let (ciphertext, shared_secret) = kem.encapsulate(public_key)
   256            .map_err(|e| anyhow!("Encapsulation failed: {:?}", e))?;
   257        
   258        Ok(EncapsulatedSecret {
   259            algorithm,
   260            ciphertext,
   261            shared_secret: SecretKey::new(shared_secret),
   262        })
