   311    ) -> Result<bool> {
   312        let sig_scheme = self.digital_signature.get(&signature.algorithm)
   313            .ok_or_else(|| anyhow!("Signature algorithm not available"))?;
   314        
   315        // Verify message hash integrity
   316        let expected_hash = self.hash_message(message);
   317        if expected_hash != signature.message_hash {
   318            return Ok(false);
   319        }
   320        
   321        // Check signature timestamp (prevent replay attacks)
   322        let now = std::time::SystemTime::now();
   323        let max_age = std::time::Duration::from_secs(5 * 60); // 5 minutes
   324        if now.duration_since(signature.timestamp).unwrap_or_default() > max_age {
   325            return Ok(false);
   326        }
   327        
   328        let is_valid = sig_scheme.verify(message, &signature.signature, public_key)
   329            .map_err(|e| anyhow!("Verification failed: {:?}", e))?;
   330        
   331        Ok(is_valid)
   332    }
