use anyhow::{anyhow, Result};
use oqs::{kem, sig};
use rand::{rngs::OsRng, RngCore};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use zeroize::{Zeroize, ZeroizeOnDrop};

/// Post-Quantum Cryptography manager for defense-grade quantum resistance
pub struct PQCManager {
    key_encapsulation: HashMap<PQCAlgorithm, kem::Kem>,
    digital_signature: HashMap<PQCAlgorithm, sig::Sig>,
    hybrid_mode: bool,
}

#[derive(Debug, Clone, Hash, Eq, PartialEq, Serialize, Deserialize)]
pub enum PQCAlgorithm {
    // NIST standardized KEMs
    Kyber512,
    Kyber768,
    Kyber1024,

    // NIST standardized signatures
    Dilithium2,
    Dilithium3,
    Dilithium5,

    // Alternative candidates
    Falcon512,
    Falcon1024,
    SPHINCS_SHA256_128s,
    SPHINCS_SHAKE256_128s,

    // Hybrid classical+post-quantum
    HybridRSA4096_Kyber768,
    HybridECDSA384_Dilithium3,
}

#[derive(Debug, Clone, Serialize, Deserialize, ZeroizeOnDrop)]
pub struct PQCKeyPair {
    pub algorithm: PQCAlgorithm,
    pub public_key: Vec<u8>,
    #[zeroize(skip)]
    pub private_key: SecretKey,
    pub created_at: std::time::SystemTime,
    pub metadata: KeyMetadata,
}

#[derive(Debug, Clone, Serialize, Deserialize, ZeroizeOnDrop)]
pub struct SecretKey {
    #[zeroize(skip)]
    inner: Vec<u8>,
}

impl SecretKey {
    pub fn new(key_data: Vec<u8>) -> Self {
        Self { inner: key_data }
    }

    pub fn as_bytes(&self) -> &[u8] {
        &self.inner
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct KeyMetadata {
    pub purpose: KeyPurpose,
    pub security_level: SecurityLevel,
    pub rotation_policy: RotationPolicy,
    pub compliance_frameworks: Vec<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum KeyPurpose {
    KeyEncapsulation,
    DigitalSignature,
    KeyDerivation,
    Attestation,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SecurityLevel {
    NIST1,  // 128-bit classical security
    NIST3,  // 192-bit classical security  
    NIST5,  // 256-bit classical security
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RotationPolicy {
    pub max_age: std::time::Duration,
    pub max_operations: Option<u64>,
    pub auto_rotate: bool,
}

#[derive(Debug, Serialize, Deserialize, ZeroizeOnDrop)]
pub struct EncapsulatedSecret {
    pub algorithm: PQCAlgorithm,
    pub ciphertext: Vec<u8>,
    #[zeroize(skip)]
    pub shared_secret: SecretKey,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct PQCSignature {
    pub algorithm: PQCAlgorithm,
    pub signature: Vec<u8>,
    pub message_hash: Vec<u8>,
    pub timestamp: std::time::SystemTime,
}

#[derive(Debug, Serialize, Deserialize, ZeroizeOnDrop)]
pub struct HybridSharedSecret {
    #[zeroize(skip)]
    pub classical_secret: Vec<u8>,
    #[zeroize(skip)]
    pub quantum_secret: Vec<u8>,
    #[zeroize(skip)]
    pub combined_secret: Vec<u8>,
    pub algorithms: (String, PQCAlgorithm),
}

impl PQCManager {
    /// Create new PQC manager with recommended algorithms
    pub fn new(hybrid_mode: bool) -> Result<Self> {
        let mut key_encapsulation = HashMap::new();
        let mut digital_signature = HashMap::new();

        // Initialize NIST standardized KEMs
        key_encapsulation.insert(
            PQCAlgorithm::Kyber512,
            kem::Kem::new(kem::Algorithm::Kyber512)
                .map_err(|e| anyhow!("Failed to initialize Kyber512: {:?}", e))?,
        );

        key_encapsulation.insert(
            PQCAlgorithm::Kyber768,
            kem::Kem::new(kem::Algorithm::Kyber768)
                .map_err(|e| anyhow!("Failed to initialize Kyber768: {:?}", e))?,
        );

        key_encapsulation.insert(
            PQCAlgorithm::Kyber1024,
            kem::Kem::new(kem::Algorithm::Kyber1024)
                .map_err(|e| anyhow!("Failed to initialize Kyber1024: {:?}", e))?,
        );

        // Initialize NIST standardized signature schemes
        digital_signature.insert(
            PQCAlgorithm::Dilithium2,
            sig::Sig::new(sig::Algorithm::Dilithium2)
                .map_err(|e| anyhow!("Failed to initialize Dilithium2: {:?}", e))?,
        );

        digital_signature.insert(
            PQCAlgorithm::Dilithium3,
            sig::Sig::new(sig::Algorithm::Dilithium3)
                .map_err(|e| anyhow!("Failed to initialize Dilithium3: {:?}", e))?,
        );

        digital_signature.insert(
            PQCAlgorithm::Dilithium5,
            sig::Sig::new(sig::Algorithm::Dilithium5)
                .map_err(|e| anyhow!("Failed to initialize Dilithium5: {:?}", e))?,
        );

        // Initialize Falcon signatures
        digital_signature.insert(
            PQCAlgorithm::Falcon512,
            sig::Sig::new(sig::Algorithm::Falcon512)
                .map_err(|e| anyhow!("Failed to initialize Falcon512: {:?}", e))?,
        );

        digital_signature.insert(
            PQCAlgorithm::Falcon1024,
            sig::Sig::new(sig::Algorithm::Falcon1024)
                .map_err(|e| anyhow!("Failed to initialize Falcon1024: {:?}", e))?,
        );

        // Initialize SPHINCS+
        digital_signature.insert(
            PQCAlgorithm::SPHINCS_SHA256_128s,
            sig::Sig::new(sig::Algorithm::SphincsShake256128sSimple)
                .map_err(|e| anyhow!("Failed to initialize SPHINCS: {:?}", e))?,
        );

        Ok(Self {
            key_encapsulation,
            digital_signature,
            hybrid_mode,
        })
    }

    /// Generate quantum-resistant key pair
    pub fn generate_keypair(&self, algorithm: PQCAlgorithm) -> Result<PQCKeyPair> {
        let metadata = KeyMetadata {
            purpose: self.get_key_purpose(&algorithm),
            security_level: self.get_security_level(&algorithm),
            rotation_policy: RotationPolicy {
                max_age: std::time::Duration::from_secs(30 * 24 * 60 * 60), // 30 days
                max_operations: Some(1_000_000),
                auto_rotate: true,
            },
            compliance_frameworks: vec![
                "NIST-PQC".to_string(),
                "CNSS-CNSSP-15".to_string(),
                "ETSI-TS-103-823".to_string(),
            ],
        };

        match algorithm {
            // KEM algorithms
            PQCAlgorithm::Kyber512 | PQCAlgorithm::Kyber768 | PQCAlgorithm::Kyber1024 => {
                let kem = self.key_encapsulation.get(&algorithm)
                    .ok_or_else(|| anyhow!("KEM algorithm not initialized"))?;

                let (public_key, private_key) = kem.keypair()
                    .map_err(|e| anyhow!("Failed to generate keypair: {:?}", e))?;

                Ok(PQCKeyPair {
                    algorithm,
                    public_key,
                    private_key: SecretKey::new(private_key),
                    created_at: std::time::SystemTime::now(),
                    metadata,
                })
            }

            // Signature algorithms
            _ => {
                let sig_scheme = self.digital_signature.get(&algorithm)
                    .ok_or_else(|| anyhow!("Signature algorithm not initialized"))?;

                let (public_key, private_key) = sig_scheme.keypair()
                    .map_err(|e| anyhow!("Failed to generate keypair: {:?}", e))?;

                Ok(PQCKeyPair {
                    algorithm,
                    public_key,
                    private_key: SecretKey::new(private_key),
                    created_at: std::time::SystemTime::now(),
                    metadata,
                })
            }
        }
    }

    /// Perform key encapsulation (KEM)
    pub fn encapsulate(
        &self,
        public_key: &[u8],
        algorithm: PQCAlgorithm,
    ) -> Result<EncapsulatedSecret> {
        let kem = self.key_encapsulation.get(&algorithm)
            .ok_or_else(|| anyhow!("KEM algorithm not available"))?;

        let (ciphertext, shared_secret) = kem.encapsulate(public_key)
            .map_err(|e| anyhow!("Encapsulation failed: {:?}", e))?;

        Ok(EncapsulatedSecret {
            algorithm,
            ciphertext,
            shared_secret: SecretKey::new(shared_secret),
        })
    }

    /// Perform key decapsulation
    pub fn decapsulate(
        &self,
        private_key: &SecretKey,
        ciphertext: &[u8],
        algorithm: PQCAlgorithm,
    ) -> Result<SecretKey> {
        let kem = self.key_encapsulation.get(&algorithm)
            .ok_or_else(|| anyhow!("KEM algorithm not available"))?;

        let shared_secret = kem.decapsulate(private_key.as_bytes(), ciphertext)
            .map_err(|e| anyhow!("Decapsulation failed: {:?}", e))?;

        Ok(SecretKey::new(shared_secret))
    }

    /// Sign message with post-quantum signature
    pub fn sign(
        &self,
        message: &[u8],
        private_key: &SecretKey,
        algorithm: PQCAlgorithm,
    ) -> Result<PQCSignature> {
        let sig_scheme = self.digital_signature.get(&algorithm)
            .ok_or_else(|| anyhow!("Signature algorithm not available"))?;

        // Hash the message for integrity
        let message_hash = self.hash_message(message);

        let signature = sig_scheme.sign(message, private_key.as_bytes())
            .map_err(|e| anyhow!("Signing failed: {:?}", e))?;

        Ok(PQCSignature {
            algorithm,
            signature,
            message_hash,
            timestamp: std::time::SystemTime::now(),
        })
    }

    /// Verify post-quantum signature
    pub fn verify(
        &self,
        message: &[u8],
        signature: &PQCSignature,
        public_key: &[u8],
    ) -> Result<bool> {
        let sig_scheme = self.digital_signature.get(&signature.algorithm)
            .ok_or_else(|| anyhow!("Signature algorithm not available"))?;

        // Verify message hash integrity
        let expected_hash = self.hash_message(message);
        if expected_hash != signature.message_hash {
            return Ok(false);
        }

        // Check signature timestamp (prevent replay attacks)
        let now = std::time::SystemTime::now();
        let max_age = std::time::Duration::from_secs(5 * 60); // 5 minutes
        if now.duration_since(signature.timestamp).unwrap_or_default() > max_age {
            return Ok(false);
        }

        let is_valid = sig_scheme.verify(message, &signature.signature, public_key)
            .map_err(|e| anyhow!("Verification failed: {:?}", e))?;

        Ok(is_valid)
    }

    /// Perform hybrid classical+post-quantum key exchange
    pub fn hybrid_key_exchange(
        &self,
        classical_public: &[u8],
        pqc_public: &[u8],
        pqc_algorithm: PQCAlgorithm,
    ) -> Result<HybridSharedSecret> {
        if !self.hybrid_mode {
            return Err(anyhow!("Hybrid mode not enabled"));
        }

        // Classical key exchange (ECDH P-384)
        let classical_secret = self.classical_ecdh(classical_public)?;

        // Post-quantum key exchange
        let pqc_result = self.encapsulate(pqc_public, pqc_algorithm.clone())?;

        // Combine secrets using HKDF-SHA3-256
        let combined_secret = self.combine_secrets(
            &classical_secret,
            pqc_result.shared_secret.as_bytes(),
        )?;

        Ok(HybridSharedSecret {
            classical_secret,
            quantum_secret: pqc_result.shared_secret.as_bytes().to_vec(),
            combined_secret,
            algorithms: ("ECDH-P384".to_string(), pqc_algorithm),
        })
    }

    /// Generate cryptographically secure random bytes
    pub fn generate_random(&self, length: usize) -> Vec<u8> {
        let mut random_bytes = vec![0u8; length];
        OsRng.fill_bytes(&mut random_bytes);
        random_bytes
    }

    /// Derive key using post-quantum KDF
    pub fn derive_key(
        &self,
        master_key: &[u8],
        salt: &[u8],
        info: &[u8],
        length: usize,
    ) -> Result<Vec<u8>> {
        use sha3::{Sha3_256, digest::{Update, ExtendableOutput, XofReader}};
        use sha3::Shake256;

        // Use SHAKE256 for quantum-resistant key derivation
        let mut hasher = Shake256::default();
        hasher.update(master_key);
        hasher.update(salt);
        hasher.update(info);
        hasher.update(&(length as u64).to_be_bytes());

        let mut output = vec![0u8; length];
        hasher.finalize_xof().read(&mut output);

        Ok(output)
    }

    /// Check if key needs rotation
    pub fn needs_rotation(&self, keypair: &PQCKeyPair) -> bool {
        let age = std::time::SystemTime::now()
            .duration_since(keypair.created_at)
            .unwrap_or_default();

        age > keypair.metadata.rotation_policy.max_age
    }

