    fn get_key_purpose(&self, algorithm: &PQCAlgorithm) -> KeyPurpose {
        match algorithm {
            PQCAlgorithm::Kyber512 | PQCAlgorithm::Kyber768 | PQCAlgorithm::Kyber1024 => {
                KeyPurpose::KeyEncapsulation
            }
            _ => KeyPurpose::DigitalSignature,
        }
    }

    fn get_security_level(&self, algorithm: &PQCAlgorithm) -> SecurityLevel {
        match algorithm {
            PQCAlgorithm::Kyber512 | PQCAlgorithm::Dilithium2 => SecurityLevel::NIST1,
            PQCAlgorithm::Kyber768 | PQCAlgorithm::Dilithium3 | PQCAlgorithm::Falcon512 => {
                SecurityLevel::NIST3
            }
            PQCAlgorithm::Kyber1024 | PQCAlgorithm::Dilithium5 | PQCAlgorithm::Falcon1024 => {
                SecurityLevel::NIST5
            }
            _ => SecurityLevel::NIST3,
        }
    }

    fn hash_message(&self, message: &[u8]) -> Vec<u8> {
        use sha3::{Sha3_256, Digest};
        let mut hasher = Sha3_256::new();
        hasher.update(message);
        hasher.finalize().to_vec()
    }

    fn classical_ecdh(&self, _public_key: &[u8]) -> Result<Vec<u8>> {
        // Placeholder for classical ECDH implementation
        // In real implementation, would use a proper ECDH library
        Ok(vec![0u8; 48]) // P-384 produces 48-byte shared secret
    }

    fn combine_secrets(&self, classical: &[u8], quantum: &[u8]) -> Result<Vec<u8>> {
        use sha3::{Sha3_256, Digest};

        let mut combined = Vec::new();
        combined.extend_from_slice(b"CryptoBox-Hybrid-KDF");
        combined.extend_from_slice(classical);
        combined.extend_from_slice(quantum);
        combined.extend_from_slice(&(classical.len() as u64).to_be_bytes());
        combined.extend_from_slice(&(quantum.len() as u64).to_be_bytes());

        let mut hasher = Sha3_256::new();
        hasher.update(&combined);
        Ok(hasher.finalize().to_vec())
    }
}

#[derive(Debug, Clone)]
pub struct SecurityParameters {
    pub security_level: SecurityLevel,
    pub key_size: usize,
    pub signature_size: Option<usize>,
    pub ciphertext_overhead: usize,
    pub performance_tier: PerformanceTier,
}

#[derive(Debug, Clone)]
pub enum PerformanceTier {
    Fast,     // Optimized for speed
    Balanced, // Balance of security and performance
    Secure,   // Maximum security
}

impl Drop for SecretKey {
    fn drop(&mut self) {
        // Securely zero out the secret key data
        self.inner.zeroize();
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_pqc_manager_initialization() {
        let manager = PQCManager::new(false).expect("Failed to create PQC manager");
        assert!(!manager.hybrid_mode);
        assert!(!manager.key_encapsulation.is_empty());
        assert!(!manager.digital_signature.is_empty());
    }

    #[test]
    fn test_keypair_generation() {
        let manager = PQCManager::new(false).expect("Failed to create PQC manager");
        let keypair = manager.generate_keypair(PQCAlgorithm::Kyber768)
            .expect("Failed to generate keypair");

        assert_eq!(keypair.algorithm, PQCAlgorithm::Kyber768);
        assert!(!keypair.public_key.is_empty());
        assert!(!keypair.private_key.as_bytes().is_empty());
    }

    #[test]
    fn test_signature_generation_and_verification() {
        let manager = PQCManager::new(false).expect("Failed to create PQC manager");
        let keypair = manager.generate_keypair(PQCAlgorithm::Dilithium3)
            .expect("Failed to generate keypair");

        let message = b"Hello, quantum-resistant world!";
        let signature = manager.sign(message, &keypair.private_key, keypair.algorithm.clone())
            .expect("Failed to sign message");

        let is_valid = manager.verify(message, &signature, &keypair.public_key)
            .expect("Failed to verify signature");

        assert!(is_valid);
    }

    #[test]
    fn test_key_encapsulation() {
        let manager = PQCManager::new(false).expect("Failed to create PQC manager");
        let keypair = manager.generate_keypair(PQCAlgorithm::Kyber768)
            .expect("Failed to generate keypair");

        let encapsulated = manager.encapsulate(&keypair.public_key, keypair.algorithm.clone())
            .expect("Failed to encapsulate");

        let decapsulated = manager.decapsulate(
            &keypair.private_key,
            &encapsulated.ciphertext,
            keypair.algorithm
        ).expect("Failed to decapsulate");

        assert_eq!(encapsulated.shared_secret.as_bytes(), decapsulated.as_bytes());
    }

    #[test]
    fn test_key_rotation_detection() {
        let manager = PQCManager::new(false).expect("Failed to create PQC manager");
        let mut keypair = manager.generate_keypair(PQCAlgorithm::Kyber768)
            .expect("Failed to generate keypair");

        assert!(!manager.needs_rotation(&keypair));

        // Simulate old key
        keypair.created_at = std::time::SystemTime::now() - 
            std::time::Duration::from_secs(31 * 24 * 60 * 60);

        assert!(manager.needs_rotation(&keypair));
    }
