/**
 * Katalyst Core Types
 */

import type { ReactNode, TransitionStartFunction, MutableRefObject } from 'react';

export interface KatalystConfig {
  enableConcurrent?: boolean;
  enableWorkers?: boolean;
  workerPoolSize?: number;
  performanceMonitoring?: boolean;
  debugMode?: boolean;
  telemetry?: TelemetryConfig;
}

export interface TelemetryConfig {
  enabled: boolean;
  endpoint?: string;
  sampleRate?: number;
  includeMetrics?: string[];
}

export interface WorkerConfig {
  name: string;
  script: string | URL;
  options?: WorkerOptions;
  maxInstances?: number;
  timeout?: number;
}

export interface WorkerTask<T = any, R = any> {
  id: string;
  type: string;
  data: T;
  priority?: number;
  timeout?: number;
  callback?: (result: R) => void;
  onError?: (error: Error) => void;
}

export interface WorkerResult<R = any> {
  id: string;
  success: boolean;
  data?: R;
  error?: Error;
  duration: number;
}

export interface PerformanceMetrics {
  renderTime: number;
  interactionTime: number;
  workerExecutionTime: number;
  memoryUsage: number;
  fps: number;
  timestamp: number;
}

export interface ConcurrentState<T> {
  value: T;
  isPending: boolean;
  error?: Error;
  startTransition: TransitionStartFunction;
  setValue: (value: T | ((prev: T) => T)) => void;
}

export interface OptimisticState<T> {
  value: T;
  optimisticValue: T;
  isPending: boolean;
  addOptimistic: (value: T) => void;
  revert: () => void;
}

export interface ActionState<T, P = any> {
  state: T;
  isPending: boolean;
  error?: Error;
  formAction: (payload: P) => Promise<void>;
}

export interface KatalystContextValue {
  config: KatalystConfig;
  workerManager: WorkerManager | null;
  performanceMonitor: PerformanceMonitor | null;
  isHydrated: boolean;
}

export interface WorkerManager {
  execute<T, R>(task: WorkerTask<T, R>): Promise<R>;
  terminate(workerId?: string): void;
  getPoolStatus(): WorkerPoolStatus;
}

export interface WorkerPoolStatus {
  active: number;
  idle: number;
  queued: number;
  completed: number;
  failed: number;
}

export interface PerformanceMonitor {
  startMeasure(name: string): void;
  endMeasure(name: string): number;
  getMetrics(): PerformanceMetrics;
  subscribe(callback: (metrics: PerformanceMetrics) => void): () => void;
}

export type TransitionFunction = (callback: () => void) => void;

export interface DeferredOptions {
  timeoutMs?: number;
}

export interface UseConcurrentOptions {
  mode?: 'transition' | 'deferred' | 'immediate';
  timeoutMs?: number;
  onError?: (error: Error) => void;
}

export interface UseWorkerOptions {
  name?: string;
  timeout?: number;
  retries?: number;
  onError?: (error: Error) => void;
}

export interface CreateWorkerOptions {
  name: string;
  handler: (data: any) => any | Promise<any>;
  dependencies?: string[];
  transferable?: boolean;
}

