/**
 * KatalystProvider - Root provider for Katalyst framework
 * Enables React 19 concurrent features and worker management
 */

import React, { ReactNode, useEffect, useState, useMemo, Suspense } from 'react';
import { KatalystContext } from './KatalystContext';
import { WorkerManager } from '../workers/WorkerManager';
import { PerformanceMonitor } from '../performance/PerformanceMonitor';
import type { KatalystConfig, KatalystContextValue } from '../types';

interface KatalystProviderProps {
  children: ReactNode;
  config?: KatalystConfig;
  fallback?: ReactNode;
}

const defaultConfig: KatalystConfig = {
  enableConcurrent: true,
  enableWorkers: true,
  workerPoolSize: navigator.hardwareConcurrency || 4,
  performanceMonitoring: true,
  debugMode: false,
  telemetry: {
    enabled: false,
    sampleRate: 0.1,
    includeMetrics: ['renderTime', 'interactionTime', 'workerExecutionTime']
  }
};

export function KatalystProvider({ 
  children, 
  config = {}, 
  fallback = <KatalystLoadingFallback />
}: KatalystProviderProps) {
  const [isHydrated, setIsHydrated] = useState(false);

  const mergedConfig = useMemo(() => ({
    ...defaultConfig,
    ...config,
    telemetry: {
      ...defaultConfig.telemetry,
      ...config.telemetry
    }
  }), [config]);

  const workerManager = useMemo(() => {
    if (typeof window === 'undefined' || !mergedConfig.enableWorkers) {
      return null;
    }
    return new WorkerManager(mergedConfig);
  }, [mergedConfig]);

  const performanceMonitor = useMemo(() => {
    if (typeof window === 'undefined' || !mergedConfig.performanceMonitoring) {
      return null;
    }
    return new PerformanceMonitor(mergedConfig);
  }, [mergedConfig]);

  useEffect(() => {
    setIsHydrated(true);

    if (mergedConfig.debugMode) {
      console.log('[Katalyst] Framework initialized with config:', mergedConfig);
    }

    // Initialize performance monitoring
    if (performanceMonitor) {
      performanceMonitor.startMeasure('app-init');

      // Report Web Vitals
      if ('PerformanceObserver' in window) {
        const observer = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            performanceMonitor.reportMetric(entry.name, entry.duration);
          }
        });

        observer.observe({ entryTypes: ['measure', 'navigation', 'resource'] });

        return () => observer.disconnect();
      }
    }

    return () => {
      workerManager?.terminate();
      performanceMonitor?.destroy();
    };
  }, [mergedConfig, workerManager, performanceMonitor]);

  const contextValue: KatalystContextValue = useMemo(() => ({
    config: mergedConfig,
    workerManager,
    performanceMonitor,
    isHydrated
  }), [mergedConfig, workerManager, performanceMonitor, isHydrated]);

  return (
    <KatalystContext.Provider value={contextValue}>
      {mergedConfig.enableConcurrent ? (
        <Suspense fallback={fallback}>
          {children}
        </Suspense>
      ) : (
        children
      )}
    </KatalystContext.Provider>
  );
}

function KatalystLoadingFallback() {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    }}>
      <div style={{ textAlign: 'center' }}>
        <div style={{
          width: '48px',
          height: '48px',
          margin: '0 auto 16px',
          border: '3px solid #e5e7eb',
          borderTopColor: '#3b82f6',
          borderRadius: '50%',
          animation: 'katalyst-spin 1s linear infinite'
        }} />
        <style dangerouslySetInnerHTML={{
          __html: `
            @keyframes katalyst-spin {
              to { transform: rotate(360deg); }
            }
          `
        }} />
        <p style={{ color: '#6b7280', margin: 0 }}>Loading Katalyst...</p>
      </div>
    </div>
  );
}

