/**
 * KatalystContext - Core context for Katalyst framework
 */

import { createContext, useContext } from 'react';
import type { KatalystContextValue } from '../types';

export const KatalystContext = createContext<KatalystContextValue | null>(null);

export function useKatalyst() {
  const context = useContext(KatalystContext);

  if (!context) {
    throw new Error(
      'useKatalyst must be used within a KatalystProvider. ' +
      'Please wrap your application with <KatalystProvider> at the root level.'
    );
  }

  return context;
}

export function useKatalystConfig() {
  const { config } = useKatalyst();
  return config;
}

export function useKatalystWorkers() {
  const { workerManager } = useKatalyst();

  if (!workerManager) {
    throw new Error(
      'Worker management is not enabled. ' +
      'Please set enableWorkers: true in your KatalystProvider config.'
    );
  }

  return workerManager;
}

export function useKatalystPerformance() {
  const { performanceMonitor } = useKatalyst();

  if (!performanceMonitor) {
    throw new Error(
      'Performance monitoring is not enabled. ' +
      'Please set performanceMonitoring: true in your KatalystProvider config.'
    );
  }

  return performanceMonitor;
}

