/**
 * useConcurrent - Enhanced React 19 concurrent state management
 */

import { useState, useTransition, useDeferredValue, useCallback, useEffect } from 'react';
import type { ConcurrentState, UseConcurrentOptions } from '../types';
import { useKatalyst } from '../core/KatalystContext';

export function useConcurrent<T>(
  initialValue: T,
  options: UseConcurrentOptions = {}
): ConcurrentState<T> {
  const { mode = 'transition', timeoutMs = 5000, onError } = options;
  const { performanceMonitor } = useKatalyst();

  const [value, setValueInternal] = useState<T>(initialValue);
  const [error, setError] = useState<Error | undefined>();
  const [isPending, startTransition] = useTransition();
  const deferredValue = useDeferredValue(value);

  const setValue = useCallback((newValue: T | ((prev: T) => T)) => {
    setError(undefined);

    try {
      performanceMonitor?.startMeasure(`concurrent-update-${mode}`);

      if (mode === 'transition') {
        startTransition(() => {
          setValueInternal(newValue);
        });
      } else if (mode === 'deferred') {
        setValueInternal(newValue);
      } else {
        // immediate mode
        setValueInternal(newValue);
      }

      performanceMonitor?.endMeasure(`concurrent-update-${mode}`);
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));
      setError(error);
      onError?.(error);
    }
  }, [mode, startTransition, performanceMonitor, onError]);

  // Timeout handling for long transitions
  useEffect(() => {
    if (!isPending || mode !== 'transition') return;

    const timeoutId = setTimeout(() => {
      const timeoutError = new Error(`Transition timed out after ${timeoutMs}ms`);
      setError(timeoutError);
      onError?.(timeoutError);
    }, timeoutMs);

    return () => clearTimeout(timeoutId);
  }, [isPending, mode, timeoutMs, onError]);

  return {
    value: mode === 'deferred' ? deferredValue : value,
    isPending,
    error,
    startTransition,
    setValue
  };
}

