/**
 * useWorker - Hook for executing tasks in Web Workers
 */

import { useState, useCallback, useRef, useEffect } from 'react';
import { useKatalystWorkers } from '../core/KatalystContext';
import type { WorkerTask, WorkerResult, UseWorkerOptions } from '../types';

interface UseWorkerReturn<T = any, R = any> {
  execute: (data: T) => Promise<R>;
  isExecuting: boolean;
  result: R | null;
  error: Error | null;
  reset: () => void;
}

export function useWorker<T = any, R = any>(
  taskType: string,
  options: UseWorkerOptions = {}
): UseWorkerReturn<T, R> {
  const workerManager = useKatalystWorkers();
  const [isExecuting, setIsExecuting] = useState(false);
  const [result, setResult] = useState<R | null>(null);
  const [error, setError] = useState<Error | null>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  const execute = useCallback(async (data: T): Promise<R> => {
    // Cancel any ongoing execution
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    // Create new abort controller
    abortControllerRef.current = new AbortController();

    setIsExecuting(true);
    setError(null);
    setResult(null);

    const task: WorkerTask<T, R> = {
      id: `${taskType}-${Date.now()}-${Math.random()}`,
      type: taskType,
      data,
      timeout: options.timeout,
      priority: 1
    };

    try {
      const taskResult = await workerManager.execute<T, R>(task);

      // Check if the request was aborted
      if (!abortControllerRef.current?.signal.aborted) {
        setResult(taskResult);
        setIsExecuting(false);
        return taskResult;
      }

      throw new Error('Task execution was cancelled');
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));

      if (!abortControllerRef.current?.signal.aborted) {
        setError(error);
        setIsExecuting(false);
        options.onError?.(error);
      }

      throw error;
    }
  }, [taskType, workerManager, options]);

  const reset = useCallback(() => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    setIsExecuting(false);
    setResult(null);
    setError(null);
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return {
    execute,
    isExecuting,
    result,
    error,
    reset
  };
}

/**
 * useWorkerCallback - Memoized worker execution with dependency tracking
 */
export function useWorkerCallback<T = any, R = any>(
  taskType: string,
  handler: (data: T) => R | Promise<R>,
  deps: React.DependencyList,
  options: UseWorkerOptions = {}
): (data: T) => Promise<R> {
  const workerManager = useKatalystWorkers();

  return useCallback(async (data: T): Promise<R> => {
    const task: WorkerTask<T, R> = {
      id: `${taskType}-${Date.now()}`,
      type: taskType,
      data,
      timeout: options.timeout
    };

    try {
      return await workerManager.execute<T, R>(task);
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));
      options.onError?.(error);
      throw error;
    }
  }, [workerManager, taskType, options, ...deps]);
}

