/**
 * WorkerManager - Manages Web Worker pool and task execution
 */

import { WorkerPool } from './WorkerPool';
import type { 
  KatalystConfig, 
  WorkerTask, 
  WorkerResult, 
  WorkerPoolStatus,
  WorkerManager as IWorkerManager 
} from '../types';

export class WorkerManager implements IWorkerManager {
  private pool: WorkerPool;
  private taskQueue: Map<string, WorkerTask>;
  private resultCallbacks: Map<string, (result: any) => void>;
  private config: KatalystConfig;

  constructor(config: KatalystConfig) {
    this.config = config;
    this.pool = new WorkerPool(config.workerPoolSize || 4);
    this.taskQueue = new Map();
    this.resultCallbacks = new Map();

    if (config.debugMode) {
      console.log('[WorkerManager] Initialized with pool size:', config.workerPoolSize);
    }
  }

  async execute<T, R>(task: WorkerTask<T, R>): Promise<R> {
    return new Promise((resolve, reject) => {
      const startTime = performance.now();

      // Store task in queue
      this.taskQueue.set(task.id, task);

      // Store callback for result handling
      this.resultCallbacks.set(task.id, (result: WorkerResult<R>) => {
        const duration = performance.now() - startTime;

        if (this.config.debugMode) {
          console.log(`[WorkerManager] Task ${task.id} completed in ${duration}ms`);
        }

        // Clean up
        this.taskQueue.delete(task.id);
        this.resultCallbacks.delete(task.id);

        if (result.success && result.data !== undefined) {
          resolve(result.data);
        } else if (result.error) {
          reject(result.error);
        } else {
          reject(new Error('Unknown worker error'));
        }
      });

      // Handle timeout
      if (task.timeout) {
        setTimeout(() => {
          if (this.taskQueue.has(task.id)) {
            this.taskQueue.delete(task.id);
            this.resultCallbacks.delete(task.id);
            reject(new Error(`Task ${task.id} timed out after ${task.timeout}ms`));
          }
        }, task.timeout);
      }

      // Execute task in worker pool
      this.pool.execute(task).then((result) => {
        const callback = this.resultCallbacks.get(task.id);
        if (callback) {
          callback(result);
        }
      }).catch((error) => {
        const callback = this.resultCallbacks.get(task.id);
        if (callback) {
          callback({
            id: task.id,
            success: false,
            error,
            duration: performance.now() - startTime
          });
        }
      });
    });
  }

  terminate(workerId?: string): void {
    if (workerId) {
      this.pool.terminateWorker(workerId);
    } else {
      this.pool.terminateAll();
      this.taskQueue.clear();
      this.resultCallbacks.clear();
    }

    if (this.config.debugMode) {
      console.log('[WorkerManager] Terminated', workerId ? `worker ${workerId}` : 'all workers');
    }
  }

  getPoolStatus(): WorkerPoolStatus {
    return this.pool.getStatus();
  }

  // Performance metrics collection
  reportMetric(name: string, value: number): void {
    if (this.config.performanceMonitoring && this.config.telemetry?.enabled) {
      // Send metrics to telemetry endpoint
      this.sendTelemetry({
        type: 'worker-metric',
        name,
        value,
        timestamp: Date.now()
      });
    }
  }

  private sendTelemetry(data: any): void {
    if (!this.config.telemetry?.endpoint) return;

    // Sample based on configured rate
    if (Math.random() > (this.config.telemetry.sampleRate || 1)) return;

    // Send telemetry data (non-blocking)
    fetch(this.config.telemetry.endpoint, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    }).catch(() => {
      // Silently fail telemetry
    });
  }

  destroy(): void {
    this.terminate();
  }
}

