      // Register default handlers
      handlers.set('echo', (data) => data);
      handlers.set('compute', (data) => {
        // Example compute-intensive task
        let result = 0;
        for (let i = 0; i < data.iterations || 1000000; i++) {
          result += Math.sqrt(i);
        }
        return result;
      });

      // Message handler
      self.addEventListener('message', async (event) => {
        const { id, type, data } = event.data;

        try {
          let result;

          if (handlers.has(type)) {
            const handler = handlers.get(type);
            result = await handler(data);
          } else {
            // Dynamic code execution (be careful with security)
            if (type === 'eval' && data.code) {
              result = await eval(data.code)(data.args);
            } else {
              throw new Error(\`Unknown task type: \${type}\`);
            }
          }

          self.postMessage({
            id,
            success: true,
            data: result,
            duration: 0
          });
        } catch (error) {
          self.postMessage({
            id,
            success: false,
            error: {
              message: error.message,
              stack: error.stack
            },
            duration: 0
          });
        }
      });

      // Custom handler registration
      self.registerHandler = (type, handler) => {
        handlers.set(type, handler);
      };
    `;
  }

  private initializeWorkers(): void {
    for (let i = 0; i < Math.ceil(this.maxWorkers / 2); i++) {
      this.createWorker();
    }
  }

  private createWorker(): PooledWorker {
    const id = `worker-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const blob = new Blob([this.workerScript], { type: 'application/javascript' });
    const workerUrl = URL.createObjectURL(blob);
    const worker = new Worker(workerUrl);

    const pooledWorker: PooledWorker = {
      id,
      worker,
      busy: false,
      taskCount: 0
    };

    // Set up message handler
    worker.addEventListener('message', (event) => {
      this.handleWorkerMessage(pooledWorker, event.data);
    });

    worker.addEventListener('error', (error) => {
      console.error(`[WorkerPool] Worker ${id} error:`, error);
      this.handleWorkerError(pooledWorker, error);
    });

    this.workers.set(id, pooledWorker);
    return pooledWorker;
  }

  async execute<T, R>(task: WorkerTask<T, R>): Promise<WorkerResult<R>> {
    return new Promise((resolve, reject) => {
      // Find available worker or create new one
      const worker = this.getAvailableWorker();

      if (!worker) {
        // Queue task if no workers available
        this.taskQueue.push({
          ...task,
          callback: resolve as any,
          onError: reject
        });
        return;
      }

      // Execute task
      this.executeOnWorker(worker, task, resolve, reject);
    });
  }

  private getAvailableWorker(): PooledWorker | null {
    // Find idle worker
    for (const worker of this.workers.values()) {
      if (!worker.busy) {
        return worker;
      }
    }

    // Create new worker if under limit
    if (this.workers.size < this.maxWorkers) {
      return this.createWorker();
    }

    return null;
  }

  private executeOnWorker<T, R>(
    pooledWorker: PooledWorker,
    task: WorkerTask<T, R>,
    resolve: (result: WorkerResult<R>) => void,
    reject: (error: Error) => void
  ): void {
    const startTime = performance.now();
    pooledWorker.busy = true;
    pooledWorker.taskCount++;

    // Store resolver for this task
    (pooledWorker as any).currentTask = {
      id: task.id,
      resolve: (result: WorkerResult<R>) => {
        result.duration = performance.now() - startTime;
        this.stats.completed++;
        this.stats.totalExecutionTime += result.duration;
        resolve(result);
      },
      reject
    };

    // Send task to worker
    pooledWorker.worker.postMessage({
      id: task.id,
      type: task.type,
      data: task.data
    });
  }

  private handleWorkerMessage(pooledWorker: PooledWorker, message: WorkerResult): void {
    const task = (pooledWorker as any).currentTask;

    if (task && task.id === message.id) {
      pooledWorker.busy = false;
      delete (pooledWorker as any).currentTask;

      if (message.success) {
        task.resolve(message);
      } else {
        this.stats.failed++;
        task.reject(new Error(message.error?.message || 'Worker execution failed'));
      }

      // Process queued tasks
      this.processQueue();
    }
  }

  private handleWorkerError(pooledWorker: PooledWorker, error: ErrorEvent): void {
    const task = (pooledWorker as any).currentTask;

    if (task) {
      pooledWorker.busy = false;
      delete (pooledWorker as any).currentTask;
      this.stats.failed++;
      task.reject(new Error(error.message));
    }

    // Restart worker
    this.terminateWorker(pooledWorker.id);
    this.createWorker();

    // Process queued tasks
    this.processQueue();
  }

  private processQueue(): void {
    while (this.taskQueue.length > 0) {
      const worker = this.getAvailableWorker();
      if (!worker) break;

      const task = this.taskQueue.shift()!;
      this.executeOnWorker(worker, task, task.callback!, task.onError!);
    }
  }

  terminateWorker(workerId: string): void {
    const pooledWorker = this.workers.get(workerId);
    if (pooledWorker) {
      pooledWorker.worker.terminate();
      this.workers.delete(workerId);
    }
  }

  terminateAll(): void {
    for (const [id] of this.workers) {
      this.terminateWorker(id);
    }
    this.taskQueue = [];
  }

  getStatus(): WorkerPoolStatus {
    let active = 0;
    let idle = 0;

    for (const worker of this.workers.values()) {
      if (worker.busy) {
        active++;
      } else {
        idle++;
      }
    }

    return {
      active,
      idle,
      queued: this.taskQueue.length,
      completed: this.stats.completed,
      failed: this.stats.failed
    };
  }

  getStats() {
    return {
      ...this.stats,
      averageExecutionTime: this.stats.completed > 0 
        ? this.stats.totalExecutionTime / this.stats.completed 
        : 0,
      poolStatus: this.getStatus()
    };
  }
}

