/**
 * MermaidDiagram - Interactive diagram component with ASCII to Mermaid conversion
 */

import React, { useEffect, useRef, useState, useMemo } from 'react';
import mermaid from 'mermaid';
import { cn } from '../../utils/cn';
import { convertAsciiToMermaid } from '../../utils/diagram';
import { useKatalyst } from '@wikidocs/katalyst-core';

interface MermaidDiagramProps {
  content: string;
  className?: string;
  theme?: 'light' | 'dark' | 'auto';
  isAscii?: boolean;
  showSource?: boolean;
  editable?: boolean;
  onEdit?: (newContent: string) => void;
}

export function MermaidDiagram({
  content,
  className,
  theme = 'auto',
  isAscii = false,
  showSource = false,
  editable = false,
  onEdit
}: MermaidDiagramProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [error, setError] = useState<string | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editContent, setEditContent] = useState(content);
  const [renderedDiagram, setRenderedDiagram] = useState<string>('');
  const { performanceMonitor } = useKatalyst();

  // Convert ASCII to Mermaid if needed
  const mermaidContent = useMemo(() => {
    if (isAscii) {
      try {
        performanceMonitor?.startMeasure('ascii-to-mermaid');
        const converted = convertAsciiToMermaid(content);
        performanceMonitor?.endMeasure('ascii-to-mermaid');
        return converted;
      } catch (err) {
        console.error('Failed to convert ASCII to Mermaid:', err);
        return content; // Fallback to original
      }
    }
    return content;
  }, [content, isAscii, performanceMonitor]);

  useEffect(() => {
    if (!containerRef.current) return;

    const renderDiagram = async () => {
      try {
        setError(null);

        // Configure mermaid
        mermaid.initialize({
          startOnLoad: false,
          theme: theme === 'auto' 
            ? window.matchMedia('(prefers-color-scheme: dark)').matches 
              ? 'dark' 
              : 'default'
            : theme === 'dark' 
              ? 'dark' 
              : 'default',
          securityLevel: 'loose',
          fontFamily: 'system-ui, -apple-system, sans-serif',
          flowchart: {
            useMaxWidth: true,
            htmlLabels: true,
            curve: 'basis'
          }
        });

        // Generate unique ID for diagram
        const id = `mermaid-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

        // Render the diagram
        const { svg } = await mermaid.render(id, mermaidContent);
        setRenderedDiagram(svg);

        // Insert SVG into container
        if (containerRef.current) {
          containerRef.current.innerHTML = svg;
        }
      } catch (err) {
        console.error('Mermaid rendering error:', err);
        setError(err instanceof Error ? err.message : 'Failed to render diagram');
      }
    };

    renderDiagram();
  }, [mermaidContent, theme]);

  const handleSave = () => {
    if (onEdit) {
      onEdit(editContent);
    }
    setIsEditing(false);
  };

  const handleCancel = () => {
    setEditContent(content);
    setIsEditing(false);
  };

  const handleCopyDiagram = () => {
    navigator.clipboard.writeText(renderedDiagram);
  };

  const handleDownloadSVG = () => {
    const blob = new Blob([renderedDiagram], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'diagram.svg';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className={cn('mermaid-diagram-container', className)}>
      {/* Toolbar */}
      <div className="diagram-toolbar flex items-center justify-between p-2 bg-gray-50 dark:bg-gray-800 rounded-t-lg">
        <div className="flex items-center gap-2">
          {isAscii && (
            <span className="text-xs px-2 py-1 bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300 rounded">
              ASCII ‚Üí Mermaid
            </span>
          )}
          {showSource && !isEditing && (
            <button
              onClick={() => setIsEditing(true)}
              className="text-sm px-3 py-1 hover:bg-gray-200 dark:hover:bg-gray-700 rounded"
            >
              View Source
            </button>
          )}
          {editable && !isEditing && (
            <button
              onClick={() => setIsEditing(true)}
              className="text-sm px-3 py-1 hover:bg-gray-200 dark:hover:bg-gray-700 rounded"
            >
              Edit
            </button>
          )}
        </div>

        <div className="flex items-center gap-2">
          <button
            onClick={handleCopyDiagram}
            className="text-sm px-3 py-1 hover:bg-gray-200 dark:hover:bg-gray-700 rounded"
            title="Copy SVG"
          >
            Copy
          </button>
          <button
            onClick={handleDownloadSVG}
            className="text-sm px-3 py-1 hover:bg-gray-200 dark:hover:bg-gray-700 rounded"
            title="Download SVG"
          >
            Download
          </button>
        </div>
      </div>

      {/* Diagram or Editor */}
      {isEditing ? (
        <div className="diagram-editor p-4 border border-gray-200 dark:border-gray-700 rounded-b-lg">
          <textarea
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            className="w-full h-64 p-3 font-mono text-sm bg-gray-50 dark:bg-gray-900 border border-gray-300 dark:border-gray-600 rounded"
            placeholder="Enter Mermaid diagram code..."
          />
          <div className="flex gap-2 mt-3">
            <button
              onClick={handleSave}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Save
            </button>
            <button
              onClick={handleCancel}
              className="px-4 py-2 bg-gray-300 dark:bg-gray-700 rounded hover:bg-gray-400 dark:hover:bg-gray-600"
            >
              Cancel
            </button>
          </div>
        </div>
      ) : error ? (
        <div className="diagram-error p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-b-lg">
          <p className="text-red-600 dark:text-red-400 text-sm">{error}</p>
          {isAscii && (
            <details className="mt-2">
              <summary className="cursor-pointer text-sm text-gray-600 dark:text-gray-400">
                View original ASCII
              </summary>
              <pre className="mt-2 p-2 bg-gray-100 dark:bg-gray-800 rounded text-xs overflow-x-auto">
                {content}
              </pre>
            </details>
          )}
        </div>
      ) : (
        <div 
          ref={containerRef}
          className="diagram-content p-4 bg-white dark:bg-gray-900 border border-gray-200 dark:border-gray-700 rounded-b-lg overflow-x-auto"
        />
      )}
    </div>
  );
}

