/**
 * Diagram utilities - ASCII to Mermaid conversion
 */

interface DiagramNode {
  id: string;
  label: string;
  type?: 'box' | 'circle' | 'diamond' | 'hexagon';
}

interface DiagramEdge {
  from: string;
  to: string;
  label?: string;
  type?: 'solid' | 'dotted' | 'thick';
}

export function convertAsciiToMermaid(ascii: string): string {
  const lines = ascii.split('\n');
  const nodes = new Map<string, DiagramNode>();
  const edges: DiagramEdge[] = [];

  // Patterns for ASCII diagram elements
  const patterns = {
    // Box: [text] or +---+
    box: /\[(.*?)\]|\+[-]+\+/,
    // Circle: (text) or O
    circle: /\((.*?)\)|^\s*O\s*$/,
    // Diamond: <text> or <>
    diamond: /<(.*?)>|<>/,
    // Arrow: -->, ->, ===>, ..>
    arrow: /(.*?)\s*(-->|->|==>|\.\.>)\s*(.*)/,
    // Vertical line: |
    vertical: /^\s*\|\s*$/,
    // Label on arrow: --[label]-->
    labeledArrow: /(.*?)\s*--\[(.*?)\]-->\s*(.*)/
  };

  // First pass: identify nodes
  lines.forEach((line, index) => {
    // Check for box nodes
    const boxMatch = line.match(/\[(.*?)\]/g);
    if (boxMatch) {
      boxMatch.forEach(match => {
        const label = match.slice(1, -1);
        const id = `node_${nodes.size + 1}`;
        nodes.set(label, { id, label, type: 'box' });
      });
    }

    // Check for circle nodes
    const circleMatch = line.match(/\((.*?)\)/g);
    if (circleMatch) {
      circleMatch.forEach(match => {
        const label = match.slice(1, -1);
        if (label && !nodes.has(label)) {
          const id = `node_${nodes.size + 1}`;
          nodes.set(label, { id, label, type: 'circle' });
        }
      });
    }

    // Check for diamond nodes
    const diamondMatch = line.match(/<(.*?)>/g);
    if (diamondMatch) {
      diamondMatch.forEach(match => {
        const label = match.slice(1, -1);
        if (label && !nodes.has(label)) {
          const id = `node_${nodes.size + 1}`;
          nodes.set(label, { id, label, type: 'diamond' });
        }
      });
    }
  });

  // Second pass: identify edges
  lines.forEach(line => {
    // Check for labeled arrows
    const labeledArrowMatch = line.match(patterns.labeledArrow);
    if (labeledArrowMatch) {
      const from = labeledArrowMatch[1].trim();
      const label = labeledArrowMatch[2].trim();
      const to = labeledArrowMatch[3].trim();

      if (nodes.has(from) && nodes.has(to)) {
        edges.push({
          from: nodes.get(from)!.id,
          to: nodes.get(to)!.id,
          label
        });
      }
      return;
    }

    // Check for simple arrows
    const arrowMatch = line.match(patterns.arrow);
    if (arrowMatch) {
      const from = arrowMatch[1].trim();
      const to = arrowMatch[3].trim();
      const arrowType = arrowMatch[2];

      // Clean up node labels
      const fromLabel = from.replace(/[\[\](){}]/g, '');
      const toLabel = to.replace(/[\[\](){}]/g, '');

      // Ensure nodes exist
      if (!nodes.has(fromLabel)) {
        const id = `node_${nodes.size + 1}`;
        nodes.set(fromLabel, { id, label: fromLabel });
      }
      if (!nodes.has(toLabel)) {
        const id = `node_${nodes.size + 1}`;
        nodes.set(toLabel, { id, label: toLabel });
      }

      const edgeType = arrowType === '..>' ? 'dotted' : 
                      arrowType === '==>' ? 'thick' : 'solid';

      edges.push({
        from: nodes.get(fromLabel)!.id,
        to: nodes.get(toLabel)!.id,
        type: edgeType
      });
    }
  });

  // Generate Mermaid syntax
  return generateMermaidSyntax(nodes, edges);
}

function generateMermaidSyntax(
  nodes: Map<string, DiagramNode>,
  edges: DiagramEdge[]
): string {
  let mermaid = 'graph TD\n';

  // Add nodes with their shapes
  nodes.forEach(node => {
    const { id, label, type } = node;

    switch (type) {
      case 'box':
        mermaid += `    ${id}[${label}]\n`;
        break;
      case 'circle':
        mermaid += `    ${id}((${label}))\n`;
        break;
      case 'diamond':
        mermaid += `    ${id}{${label}}\n`;
        break;
      case 'hexagon':
        mermaid += `    ${id}{{${label}}}\n`;
        break;
      default:
        mermaid += `    ${id}[${label}]\n`;
    }
  });

  // Add edges
  edges.forEach(edge => {
    const { from, to, label, type } = edge;

    let arrow = '-->';
    if (type === 'dotted') {
      arrow = '-..->';
    } else if (type === 'thick') {
      arrow = '==>';
    }

    if (label) {
      mermaid += `    ${from} ${arrow}|${label}| ${to}\n`;
    } else {
      mermaid += `    ${from} ${arrow} ${to}\n`;
    }
  });

  return mermaid;
}

/**
 * Enhanced ASCII to Mermaid converter with more complex patterns
 */
export function convertComplexAsciiToMermaid(ascii: string): string {
  // Handle more complex ASCII art patterns
  const enhancedAscii = preprocessAsciiArt(ascii);
  return convertAsciiToMermaid(enhancedAscii);
}

function preprocessAsciiArt(ascii: string): string {
  const lines = ascii.split('\n');
  const processed: string[] = [];

  // State tracking for multi-line boxes
  let inBox = false;
  let boxContent = '';
  let boxStartCol = -1;

  lines.forEach((line, index) => {
    // Detect box boundaries
    if (line.match(/\+[-]+\+/)) {
      if (!inBox) {
        inBox = true;
        boxStartCol = line.indexOf('+');
      } else {
        // End of box
        if (boxContent) {
          processed.push(`[${boxContent.trim()}]`);
        }
        inBox = false;
        boxContent = '';
        boxStartCol = -1;
      }
    } else if (inBox && line.includes('|')) {
      // Extract content from box
      const content = line.substring(boxStartCol + 1, line.lastIndexOf('|')).trim();
      if (content) {
        boxContent = content;
      }
    } else {
      // Process regular lines
      processed.push(line);
    }
  });

  return processed.join('\n');
}

/**
 * Validate Mermaid syntax
 */
export function validateMermaidSyntax(mermaid: string): {
  valid: boolean;
  errors: string[];
} {
  const errors: string[] = [];

  // Check for required graph declaration
  if (!mermaid.match(/^(graph|flowchart|sequenceDiagram|classDiagram)/m)) {
    errors.push('Missing graph type declaration');
  }

  // Check for balanced brackets
  const openBrackets = (mermaid.match(/[\[\{(]/g) || []).length;
  const closeBrackets = (mermaid.match(/[\]\})]/g) || []).length;
  if (openBrackets !== closeBrackets) {
    errors.push('Unbalanced brackets in diagram');
  }

  // Check for valid node syntax
  const nodePattern = /^\s*(\w+)[\[\(\{]/gm;
  const nodes = mermaid.match(nodePattern);
  if (!nodes || nodes.length === 0) {
    errors.push('No valid nodes found in diagram');
  }

  return {
    valid: errors.length === 0,
    errors
  };
}

/**
 * Convert Mermaid back to simplified ASCII (for fallback)
 */
export function convertMermaidToAscii(mermaid: string): string {
  const lines = mermaid.split('\n');
  const ascii: string[] = [];

  lines.forEach(line => {
    // Convert node definitions
    const nodeMatch = line.match(/(\w+)\[(.*?)\]/);
    if (nodeMatch) {
      ascii.push(`[${nodeMatch[2]}]`);
    }

    // Convert edges
    const edgeMatch = line.match(/(\w+)\s*-->\s*(\w+)/);
    if (edgeMatch) {
      // Find node labels from previous definitions
      ascii.push(`${edgeMatch[1]} --> ${edgeMatch[2]}`);
    }
  });

  return ascii.join('\n');
}

