    } else {
      // Process regular lines
      processed.push(line);
    }
  });

  return processed.join('\n');
}

/**
 * Validate Mermaid syntax
 */
export function validateMermaidSyntax(mermaid: string): {
  valid: boolean;
  errors: string[];
} {
  const errors: string[] = [];

  // Check for required graph declaration
  if (!mermaid.match(/^(graph|flowchart|sequenceDiagram|classDiagram)/m)) {
    errors.push('Missing graph type declaration');
  }

  // Check for balanced brackets
  const openBrackets = (mermaid.match(/[\[\{(]/g) || []).length;
  const closeBrackets = (mermaid.match(/[\]\})]/g) || []).length;
  if (openBrackets !== closeBrackets) {
    errors.push('Unbalanced brackets in diagram');
  }

  // Check for valid node syntax
  const nodePattern = /^\s*(\w+)[\[\(\{]/gm;
  const nodes = mermaid.match(nodePattern);
  if (!nodes || nodes.length === 0) {
    errors.push('No valid nodes found in diagram');
  }

  return {
    valid: errors.length === 0,
    errors
  };
}

/**
 * Convert Mermaid back to simplified ASCII (for fallback)
 */
export function convertMermaidToAscii(mermaid: string): string {
  const lines = mermaid.split('\n');
  const ascii: string[] = [];

  lines.forEach(line => {
    // Convert node definitions
    const nodeMatch = line.match(/(\w+)\[(.*?)\]/);
    if (nodeMatch) {
      ascii.push(`[${nodeMatch[2]}]`);
    }

    // Convert edges
    const edgeMatch = line.match(/(\w+)\s*-->\s*(\w+)/);
    if (edgeMatch) {
      // Find node labels from previous definitions
      ascii.push(`${edgeMatch[1]} --> ${edgeMatch[2]}`);
    }
  });

  return ascii.join('\n');
}

