job "sandbox-executor" {
  datacenters = ["dc1"]
  type        = "service"
  priority    = 90

  # Constraints for security
  constraint {
    attribute = "${node.class}"
    value     = "sandbox"
  }

  # Update strategy
  update {
    max_parallel      = 1
    health_check      = "checks"
    min_healthy_time  = "30s"
    healthy_deadline  = "5m"
    progress_deadline = "10m"
    auto_revert       = true
    canary            = 1
  }

  # Sandbox executor group
  group "sandbox" {
    count = 3

    # Spread across nodes for resilience
    spread {
      attribute = "${node.unique.id}"
      weight    = 100
    }

    # Network configuration
    network {
      port "http" {
        to = 8080
      }
      port "grpc" {
        to = 9090
      }
    }

    # Restart policy
    restart {
      attempts = 3
      interval = "5m"
      delay    = "15s"
      mode     = "delay"
    }

    # Main sandbox executor task
    task "executor" {
      driver = "docker"

      # Security constraints
      constraint {
        attribute = "${attr.kernel.name}"
        value     = "linux"
      }

      config {
        image = "wikidocs/sandbox-executor:latest"

        # Security configurations
        privileged = false
        readonly_rootfs = true

        # Network isolation
        network_mode = "none"

        # Resource limits
        memory_hard_limit = 2048
        cpu_hard_limit    = true

        # Security options
        security_opt = [
          "no-new-privileges:true",
          "apparmor:docker-default",
          "seccomp:default"
        ]

        # Capabilities (minimal set)
        cap_drop = ["ALL"]
        cap_add  = ["NET_BIND_SERVICE"]

        # Mounts
        mounts = [
          {
            type     = "tmpfs"
            target   = "/tmp"
            readonly = false
            tmpfs_options = {
              size = 104857600  # 100MB
              mode = 1777
            }
          }
        ]

        # Ports
        ports = ["http", "grpc"]

        # Environment variables
        env = {
          SANDBOX_MODE = "strict"
          MAX_EXECUTION_TIME = "30s"
          MAX_MEMORY = "512MB"
          ALLOWED_LANGUAGES = "python,javascript,julia"
        }
      }

      # Vault integration for secrets
      vault {
        policies = ["sandbox-executor"]

        change_mode   = "restart"
        change_signal = "SIGUSR1"
      }

      # Template for configuration
      template {
        data = <<EOH
VAULT_TOKEN={{ env "VAULT_TOKEN" }}
SANDBOX_CONFIG=/local/sandbox.json
LOG_LEVEL=info
METRICS_ENABLED=true
EOH
        destination = "secrets/env"
        env         = true
      }

      # Sandbox configuration
      template {
        data = <<EOH
{
  "version": "1.0",
  "sandbox": {
    "type": "gvisor",
    "runtime": "runsc",
    "network": "none",
    "filesystem": "readonly",
    "limits": {
      "cpu": "500m",
      "memory": "512Mi",
      "storage": "100Mi",
      "time": "30s",
      "processes": 10,
      "files": 100
    },
    "allowed": {
      "syscalls": ["read", "write", "open", "close", "stat", "fstat"],
      "languages": ["python3", "node", "deno", "julia"],
      "packages": []
    },
    "denied": {
      "network": true,
      "fork": false,
      "exec": false
    }
  },
  "monitoring": {
    "enabled": true,
    "metrics_port": 9090,
    "health_check": "/health"
  }
}
EOH
        destination = "local/sandbox.json"
      }

      # Resources
      resources {
        cpu    = 500
        memory = 512

        device "nvidia/gpu" {
          count = 0  # No GPU access for sandboxes
        }
      }

      # Service registration
      service {
        name = "sandbox-executor"
        port = "http"

        tags = [
          "sandbox",
          "executor",
          "secure",
          "traefik.enable=false"  # Not exposed via Traefik
        ]

        check {
          type     = "http"
          path     = "/health"
          interval = "10s"
          timeout  = "2s"
        }
      }

      # Logs
      logs {
        max_files     = 10
        max_file_size = 10
      }
    }

    # Sidecar for monitoring
    task "monitor" {
      driver = "docker"

      lifecycle {
        hook    = "prestart"
        sidecar = true
      }

      config {
        image = "wikidocs/sandbox-monitor:latest"

        # Read-only filesystem
        readonly_rootfs = true

        # Security
        security_opt = [
          "no-new-privileges:true"
        ]

        cap_drop = ["ALL"]
      }

      resources {
        cpu    = 100
        memory = 128
      }
    }
  }

  # Claude Code testing sandbox group
  group "claude-sandbox" {
    count = 1

    network {
      port "api" {
        to = 8081
      }
    }

    task "claude-executor" {
      driver = "docker"

      # User for Claude Code
      user = "claude-code"

      config {
        image = "wikidocs/claude-sandbox:latest"

        # Enhanced privileges for Claude Code admin
        privileged = false
        readonly_rootfs = false

        # Isolated network
        network_mode = "bridge"

        # Security options
        security_opt = [
          "no-new-privileges:true",
          "apparmor:claude-sandbox",
          "seccomp:claude-sandbox"
        ]

        # Capabilities for Claude Code
        cap_drop = ["ALL"]
        cap_add  = [
          "NET_BIND_SERVICE",
          "CHOWN",
          "SETUID",
          "SETGID"
        ]

        ports = ["api"]

        # Environment
        env = {
          CLAUDE_CODE_USER = "user-CVVK7bKKZCyP"
          SANDBOX_MODE = "claude"
          EXECUTION_TIMEOUT = "60s"
          MAX_MEMORY = "2GB"
          NETWORK_ACCESS = "restricted"
        }
      }

      # Vault secrets for Claude Code
      vault {
        policies = ["claude-code-sandbox"]
      }

      # Resources for Claude Code
      resources {
        cpu    = 2000
        memory = 2048
      }

      # Service registration
      service {
        name = "claude-sandbox"
        port = "api"

        tags = [
          "claude",
          "sandbox",
          "admin",
          "traefik.enable=true",
          "traefik.http.routers.claude-sandbox.rule=Host(`sandbox.claude.wikidocs.io`)",
          "traefik.http.routers.claude-sandbox.middlewares=claude-code-auth"
        ]

        check {
          type     = "http"
          path     = "/health"
          interval = "10s"
          timeout  = "2s"
        }
      }
    }
  }
}

