   117‚Üí#[tokio::main]
   118‚Üíasync fn main() -> Result<()> {
   119‚Üí    let cli = Cli::parse();
   120‚Üí    
   121‚Üí    // Initialize logging
   122‚Üí    init_logging(&cli)?;
   123‚Üí    
   124‚Üí    // Load configuration
   125‚Üí    let config = load_config(cli.config).await?;
   126‚Üí    
   127‚Üí    // Change working directory if specified
   128‚Üí    if let Some(ref dir) = cli.directory {
   129‚Üí        std::env::set_current_dir(dir)?;
   130‚Üí        info!("Changed working directory to: {:?}", dir);
   131‚Üí    }
   132‚Üí    
   133‚Üí    // Execute command or start interactive mode
   134‚Üí    match cli.command {
   135‚Üí        Some(Commands::Init { force }) => {
   136‚Üí            handle_init(force).await
   137‚Üí        }
   138‚Üí        Some(Commands::Start { session, editor, files }) => {
   139‚Üí            handle_start(config, session, editor, files).await
   140‚Üí        }
   141‚Üí        Some(Commands::Session { action }) => {
   142‚Üí            handle_session_command(config, action).await
   143‚Üí        }
   144‚Üí        Some(Commands::Project { action }) => {
   145‚Üí            handle_project_command(action).await
   146‚Üí        }
   147‚Üí        Some(Commands::Info) => {
   148‚Üí            handle_info().await
   149‚Üí        }
   150‚Üí        Some(Commands::Security { action }) => {
   151‚Üí            handle_security_command(action).await
   152‚Üí        }
   153‚Üí        None => {
   154‚Üí            // Default: start interactive mode
   155‚Üí            handle_start(config, None, None, Vec::new()).await
   156‚Üí        }
   157‚Üí    }
   158‚Üí}
