   160‚Üí/// Initialize logging based on CLI options
   161‚Üífn init_logging(cli: &Cli) -> Result<()> {
   162‚Üí    let log_level = if cli.debug {
   163‚Üí        "debug"
   164‚Üí    } else if cli.verbose {
   165‚Üí        "info"
   166‚Üí    } else {
   167‚Üí        "warn"
   168‚Üí    };
   169‚Üí    
   170‚Üí    let filter = tracing_subscriber::EnvFilter::try_from_default_env()
   171‚Üí        .unwrap_or_else(|_| tracing_subscriber::EnvFilter::new(log_level));
   172‚Üí    
   173‚Üí    tracing_subscriber::registry()
   174‚Üí        .with(filter)
   175‚Üí        .with(
   176‚Üí            tracing_subscriber::fmt::layer()
   177‚Üí                .with_target(cli.debug)
   178‚Üí                .with_thread_ids(cli.debug)
   179‚Üí                .with_file(cli.debug)
   180‚Üí                .with_line_number(cli.debug)
   181‚Üí        )
   182‚Üí        .try_init()?;
   183‚Üí    
   184‚Üí    info!("Vimmacs starting with log level: {}", log_level);
   185‚Üí    Ok(())
   186‚Üí}
