   202‚Üí/// Handle initialization command
   203‚Üíasync fn handle_init(force: bool) -> Result<()> {
   204‚Üí    info!("Initializing Vimmacs configuration");
   205‚Üí    
   206‚Üí    let config = Config::default();
   207‚Üí    let config_dir = &config.app.config_dir;
   208‚Üí    
   209‚Üí    // Check if configuration already exists
   210‚Üí    if config_dir.exists() && !force {
   211‚Üí        return Err(anyhow::anyhow!(
   212‚Üí            "Configuration directory already exists: {:?}\nUse --force to overwrite",
   213‚Üí            config_dir
   214‚Üí        ));
   215‚Üí    }
   216‚Üí    
   217‚Üí    // Create configuration directories
   218‚Üí    let dirs = [
   219‚Üí        config_dir.as_path(),
   220‚Üí        &config.app.data_dir,
   221‚Üí        &config.app.cache_dir,
   222‚Üí        &config.app.log_dir,
   223‚Üí    ];
   224‚Üí    
   225‚Üí    FileSystemUtils::ensure_directory_structure(&dirs).await?;
   226‚Üí    
   227‚Üí    // Save default configuration
   228‚Üí    config.save(None).await?;
   229‚Üí    
   230‚Üí    println!("‚úÖ Vimmacs initialized successfully!");
   231‚Üí    println!("üìÅ Configuration directory: {:?}", config_dir);
   232‚Üí    println!("üìù Edit configuration: {:?}", config_dir.join("config.toml"));
   233‚Üí    println!("üöÄ Start Vimmacs: vimmacs start");
   234‚Üí    
   235‚Üí    Ok(())
   236‚Üí}
