//! # Manifolded OVH Cloud Integration
//! 
//! This crate provides comprehensive OVH Cloud integration for the Manifolded platform,
//! offering automated provisioning, quantum workload optimization, and enterprise-grade
//! infrastructure management.
//! 
//! ## Features
//! 
//! - **Automated VM Provisioning**: Create and manage OVH VMs with optimal configurations
//! - **Quantum Workload Scheduling**: Intelligent placement of quantum computing workloads
//! - **Multi-Region Deployment**: Global deployment with data sovereignty compliance
//! - **Cost Optimization**: Dynamic resource allocation based on workload requirements
//! - **Security Integration**: Enterprise security with HSM and compliance frameworks
//! - **Real-time Monitoring**: Advanced telemetry and performance metrics
//! 
//! ## Quick Start
//! 
//! ```rust
//! use manifolded_ovh::{OvhClient, Region, VmSpec};
//! 
//! #[tokio::main]
//! async fn main() -> anyhow::Result<()> {
//!     let client = OvhClient::new()
//!         .with_credentials_from_env()?
//!         .with_region(Region::EuropeWest)?;
//!     
//!     let vm_spec = VmSpec::new()
//!         .with_flavor("quantum-optimized-large")
//!         .with_quantum_capabilities(true)
//!         .with_security_hardening(true);
//!     
//!     let vm = client.create_vm("quantum-manifold-1", vm_spec).await?;
//!     println!("Created quantum-enabled VM: {}", vm.id());
//!     
//!     Ok(())
//! }
//! ```

pub mod client;
pub mod config;
pub mod credentials;
pub mod networking;
pub mod provisioning;
pub mod quantum_scheduling;
pub mod regions;
pub mod security;
pub mod monitoring;
pub mod cost_optimization;
pub mod errors;

#[cfg(feature = "cli")]
pub mod cli;

// Re-exports for convenience
pub use client::OvhClient;
pub use config::{OvhConfig, VmSpec, NetworkSpec};
pub use credentials::OvhCredentials;
pub use errors::{OvhError, Result};
pub use regions::Region;
pub use provisioning::{VmInstance, ProvisioningManager};
pub use quantum_scheduling::{QuantumScheduler, WorkloadType};
pub use monitoring::{MetricsCollector, PerformanceMetrics};

/// Version information for the OVH integration
pub const VERSION: &str = env!("CARGO_PKG_VERSION");

/// Default timeout for OVH API operations (in seconds)
pub const DEFAULT_TIMEOUT: u64 = 300;

/// Maximum number of retry attempts for failed operations
pub const MAX_RETRY_ATTEMPTS: u32 = 3;

/// Default quantum coherence time for workload scheduling (in milliseconds)
pub const DEFAULT_QUANTUM_COHERENCE_TIME: u64 = 1000;

/// Supported OVH instance flavors optimized for Manifolded workloads
pub const QUANTUM_OPTIMIZED_FLAVORS: &[&str] = &[
    "quantum-nano",      // 1 vCPU, 2GB RAM, quantum-ready
    "quantum-small",     // 2 vCPU, 4GB RAM, quantum-ready
    "quantum-medium",    // 4 vCPU, 8GB RAM, quantum-ready
    "quantum-large",     // 8 vCPU, 16GB RAM, quantum-ready
    "quantum-xlarge",    // 16 vCPU, 32GB RAM, quantum-ready
    "quantum-gpu",       // GPU-accelerated quantum computing
    "quantum-hpc",       // High-performance computing cluster node
];

/// Enterprise security compliance levels
#[derive(Debug, Clone, Copy, PartialEq, Eq)]
pub enum ComplianceLevel {
    /// Basic security hardening
    Basic,
    /// SOC2 Type II compliance
    Soc2,
    /// ISO27001 compliance
    Iso27001,
    /// GDPR compliance with data residency
    Gdpr,
    /// HIPAA compliance for healthcare workloads
    Hipaa,
    /// Maximum security with HSM integration
    Maximum,
}

/// Quantum workload optimization strategies
#[derive(Debug, Clone, Copy, PartialEq, Eq)]
pub enum OptimizationStrategy {
    /// Optimize for quantum coherence time
    Coherence,
    /// Optimize for computational throughput
    Throughput,
    /// Optimize for cost efficiency
    Cost,
    /// Balanced optimization across all factors
    Balanced,
    /// Custom optimization based on specific requirements
    Custom,
}

/// Initialize the OVH integration with default settings
/// 
/// This function sets up logging, configures default timeouts,
/// and validates OVH credentials if available.
pub async fn initialize() -> Result<()> {
    tracing::info!("Initializing Manifolded OVH Cloud integration v{}", VERSION);

    // Validate environment and credentials
    if let Err(e) = OvhCredentials::from_environment() {
        tracing::warn!("OVH credentials not found in environment: {}", e);
        tracing::info!("Use `manifolded ovh configure` to set up credentials");
    }

    // Initialize performance monitoring
    monitoring::initialize_metrics().await?;

    tracing::info!("OVH integration initialized successfully");
    Ok(())
}

/// Health check for OVH integration
/// 
/// Performs comprehensive health checks including API connectivity,
/// authentication validation, and service availability.
pub async fn health_check() -> Result<HealthStatus> {
    use std::collections::HashMap;

    let mut checks = HashMap::new();

    // Check API connectivity
    match client::ping_api().await {
        Ok(_) => checks.insert("api_connectivity", true),
        Err(e) => {
            tracing::error!("OVH API connectivity failed: {}", e);
            checks.insert("api_connectivity", false)
        }
    };

    // Check authentication
    match OvhCredentials::from_environment() {
        Ok(creds) => {
            match creds.validate().await {
                Ok(_) => checks.insert("authentication", true),
                Err(e) => {
                    tracing::error!("OVH authentication failed: {}", e);
                    checks.insert("authentication", false)
                }
            }
        },
        Err(_) => checks.insert("authentication", false)
    };

    // Check quota availability
    match client::check_quota().await {
        Ok(quota) => {
            let healthy = quota.available_instances > 0 && quota.available_vcpus > 0;
            checks.insert("quota_available", healthy);
        },
        Err(e) => {
            tracing::error!("OVH quota check failed: {}", e);
            checks.insert("quota_available", false)
        }
    };

    let healthy = checks.values().all(|&v| v);

    Ok(HealthStatus {
        healthy,
        checks,
        timestamp: chrono::Utc::now(),
    })
}

/// Health status information
#[derive(Debug, serde::Serialize)]
pub struct HealthStatus {
    pub healthy: bool,
    pub checks: std::collections::HashMap<&'static str, bool>,
    pub timestamp: chrono::DateTime<chrono::Utc>,
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_initialize() {
        // Test that initialization doesn't panic
        let result = initialize().await;
        // Should succeed even without credentials in test environment
        assert!(result.is_ok() || matches!(result, Err(OvhError::Authentication(_))));
    }

    #[test]
    fn test_quantum_flavors() {
        assert!(!QUANTUM_OPTIMIZED_FLAVORS.is_empty());
        assert!(QUANTUM_OPTIMIZED_FLAVORS.contains(&"quantum-large"));
    }

    #[test]
    fn test_compliance_levels() {
        assert_eq!(ComplianceLevel::Basic as u8, 0);
        assert!(matches!(ComplianceLevel::Maximum, ComplianceLevel::Maximum));
    }
}

