            .context("Failed to read Vault configuration file")?;

        let config: toml::Value = toml::from_str(&config_content)
            .context("Failed to parse Vault configuration")?;

        let secret_mappings = config
            .get("secrets")
            .and_then(|s| s.as_table())
            .map(|table| {
                table
                    .iter()
                    .filter_map(|(k, v)| v.as_str().map(|s| (k.clone(), s.to_string())))
                    .collect()
            })
            .unwrap_or_default();

        Ok(Self {
            vault_client,
            secret_mappings,
        })
    }

    /// Load all configured secrets into environment variables
    pub async fn load_secrets_to_env(&self) -> Result<()> {
        for (env_var, vault_path) in &self.secret_mappings {
            match self.vault_client.get_secret_field(vault_path, "value").await {
                Ok(value) => {
                    std::env::set_var(env_var.to_uppercase(), value);
                    info!("Loaded secret for environment variable: {}", env_var.to_uppercase());
                }
                Err(e) => {
                    error!("Failed to load secret for {}: {}", env_var, e);
                    return Err(e);
                }
            }
        }

        Ok(())
    }

    /// Get a specific secret value
    pub async fn get_secret_value(&self, env_var: &str) -> Result<String> {
        let vault_path = self
            .secret_mappings
            .get(&env_var.to_lowercase())
            .ok_or_else(|| anyhow::anyhow!("No mapping found for environment variable: {}", env_var))?;

        self.vault_client.get_secret_field(vault_path, "value").await
    }
}

