use anyhow::{Context, Result};
use reqwest::Client;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::fs;
use std::path::Path;
use std::sync::Arc;
use tokio::sync::RwLock;
use tracing::{error, info, warn};

/// VaultClient provides secure access to HashiCorp Vault for secret management
#[derive(Clone)]
pub struct VaultClient {
    client: Client,
    config: VaultConfig,
    token: Arc<RwLock<Option<String>>>,
}

#[derive(Debug, Clone, Deserialize)]
pub struct VaultConfig {
    pub address: String,
    pub ca_cert_path: String,
    pub auth_method: String,
    pub role_id_file: String,
    pub secret_id_file: String,
    pub token_file: String,
    pub renew_token: bool,
}

#[derive(Debug, Deserialize)]
struct AppRoleAuth {
    role_id: String,
    secret_id: String,
}

#[derive(Debug, Serialize)]
struct AppRoleAuthRequest {
    role_id: String,
    secret_id: String,
}

#[derive(Debug, Deserialize)]
struct AuthResponse {
    auth: AuthData,
}

#[derive(Debug, Deserialize)]
struct AuthData {
    client_token: String,
    lease_duration: u64,
    renewable: bool,
}

#[derive(Debug, Deserialize)]
struct SecretResponse {
    data: SecretData,
}

#[derive(Debug, Deserialize)]
struct SecretData {
    data: HashMap<String, String>,
    metadata: SecretMetadata,
}

#[derive(Debug, Deserialize)]
struct SecretMetadata {
    created_time: String,
    deletion_time: Option<String>,
    destroyed: bool,
    version: u64,
}

impl VaultClient {
    /// Create a new VaultClient from configuration file
    pub async fn new(config_path: &str) -> Result<Self> {
        let config_content = fs::read_to_string(config_path)
            .context("Failed to read Vault configuration file")?;

        let config: VaultConfig = toml::from_str(&config_content)
            .context("Failed to parse Vault configuration")?;

        // Build HTTP client with custom CA certificate if provided
        let mut client_builder = Client::builder();

        if !config.ca_cert_path.is_empty() && Path::new(&config.ca_cert_path).exists() {
            let ca_cert = fs::read(&config.ca_cert_path)
                .context("Failed to read CA certificate")?;

            let cert = reqwest::Certificate::from_pem(&ca_cert)
                .context("Failed to parse CA certificate")?;

            client_builder = client_builder.add_root_certificate(cert);
        }

        let client = client_builder
            .build()
            .context("Failed to build HTTP client")?;

        let vault_client = Self {
            client,
            config,
            token: Arc::new(RwLock::new(None)),
        };

        // Authenticate if no token exists
        if !vault_client.has_valid_token().await {
            vault_client.authenticate().await?;
        }

        Ok(vault_client)
    }

    /// Check if we have a valid token
    async fn has_valid_token(&self) -> bool {
        // Check in-memory token first
        if self.token.read().await.is_some() {
            return true;
        }

        // Check token file
        if Path::new(&self.config.token_file).exists() {
            if let Ok(token_content) = fs::read_to_string(&self.config.token_file) {
                let token = token_content.trim().to_string();
                if !token.is_empty() {
                    // Verify token is still valid
                    if self.verify_token(&token).await.is_ok() {
                        *self.token.write().await = Some(token);
                        return true;
                    }
                }
            }
        }

        false
    }

    /// Authenticate with Vault using AppRole
    async fn authenticate(&self) -> Result<()> {
        if self.config.auth_method != "approle" {
            return Err(anyhow::anyhow!(
                "Unsupported auth method: {}. Only 'approle' is currently supported.",
                self.config.auth_method
            ));
        }

        info!("Authenticating with Vault using AppRole");

        // Read AppRole credentials
        let auth_content = fs::read_to_string(&self.config.role_id_file)
            .context("Failed to read AppRole credentials file")?;

        let auth_data: AppRoleAuth = serde_json::from_str(&auth_content)
            .context("Failed to parse AppRole credentials")?;

        // Authenticate with Vault
        let auth_request = AppRoleAuthRequest {
            role_id: auth_data.role_id,
            secret_id: auth_data.secret_id,
        };

        let response = self
            .client
            .post(&format!("{}/v1/auth/approle/login", self.config.address))
            .json(&auth_request)
            .send()
            .await
            .context("Failed to send authentication request")?;

        if !response.status().is_success() {
            let status = response.status();
            let body = response.text().await.unwrap_or_default();
            return Err(anyhow::anyhow!(
                "Authentication failed with status {}: {}",
                status,
                body
            ));
        }

        let auth_response: AuthResponse = response
            .json()
            .await
            .context("Failed to parse authentication response")?;

        let token = auth_response.auth.client_token;

        // Store token in memory and file
        *self.token.write().await = Some(token.clone());

        if let Err(e) = fs::write(&self.config.token_file, &token) {
            warn!("Failed to write token to file: {}", e);
        }

        info!(
            "Successfully authenticated with Vault. Token expires in {} seconds",
            auth_response.auth.lease_duration
        );

        Ok(())
    }

    /// Verify that a token is still valid
    async fn verify_token(&self, token: &str) -> Result<()> {
        let response = self
            .client
            .get(&format!("{}/v1/auth/token/lookup-self", self.config.address))
            .header("X-Vault-Token", token)
            .send()
            .await
            .context("Failed to verify token")?;

        if response.status().is_success() {
            Ok(())
        } else {
            Err(anyhow::anyhow!("Token verification failed"))
        }
    }

    /// Get a secret from Vault
    pub async fn get_secret(&self, path: &str) -> Result<HashMap<String, String>> {
        // Ensure we have a valid token
        if !self.has_valid_token().await {
            self.authenticate().await?;
        }

        let token = self
            .token
            .read()
            .await
            .as_ref()
            .ok_or_else(|| anyhow::anyhow!("No authentication token available"))?
            .clone();

        let response = self
            .client
            .get(&format!("{}/v1/{}", self.config.address, path))
            .header("X-Vault-Token", &token)
            .send()
            .await
            .context("Failed to retrieve secret")?;

        if !response.status().is_success() {
            let status = response.status();
            let body = response.text().await.unwrap_or_default();

            // Try to re-authenticate if we get 403 (token expired)
            if status == 403 {
                warn!("Token appears to be expired, re-authenticating");
                self.authenticate().await?;
                return self.get_secret(path).await;
            }

            return Err(anyhow::anyhow!(
                "Failed to retrieve secret with status {}: {}",
                status,
                body
            ));
        }

        let secret_response: SecretResponse = response
            .json()
            .await
            .context("Failed to parse secret response")?;

        info!("Successfully retrieved secret from path: {}", path);

        Ok(secret_response.data.data)
    }

    /// Get a specific field from a secret
    pub async fn get_secret_field(&self, path: &str, field: &str) -> Result<String> {
        let secret = self.get_secret(path).await?;

        secret
            .get(field)
            .cloned()
            .ok_or_else(|| anyhow::anyhow!("Field '{}' not found in secret at path '{}'", field, path))
    }

    /// Put a secret into Vault
    pub async fn put_secret(&self, path: &str, data: HashMap<String, String>) -> Result<()> {
        // Ensure we have a valid token
        if !self.has_valid_token().await {
            self.authenticate().await?;
        }

        let token = self
            .token
            .read()
            .await
            .as_ref()
            .ok_or_else(|| anyhow::anyhow!("No authentication token available"))?
            .clone();

        let mut request_data = HashMap::new();
        request_data.insert("data", data);

        let response = self
            .client
            .post(&format!("{}/v1/{}", self.config.address, path))
            .header("X-Vault-Token", &token)
            .json(&request_data)
            .send()
            .await
            .context("Failed to store secret")?;

        if !response.status().is_success() {
            let status = response.status();
            let body = response.text().await.unwrap_or_default();

            // Try to re-authenticate if we get 403 (token expired)
            if status == 403 {
                warn!("Token appears to be expired, re-authenticating");
                self.authenticate().await?;
                return self.put_secret(path, request_data.remove("data").unwrap()).await;
            }

            return Err(anyhow::anyhow!(
                "Failed to store secret with status {}: {}",
                status,
                body
            ));
        }

        info!("Successfully stored secret at path: {}", path);

        Ok(())
    }
}

/// Environment variable manager that retrieves secrets from Vault
pub struct VaultEnvManager {
    vault_client: VaultClient,
