use std::collections::HashMap;
use std::sync::Arc;
use tokio::sync::RwLock;
use serde::{Deserialize, Serialize};
use uuid::Uuid;
use chrono::{DateTime, Utc};
use anyhow::{Result, Context, anyhow};

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct QuantumWorkloadSpec {
    pub id: Uuid,
    pub name: String,
    pub algorithm_type: QuantumAlgorithmType,
    pub qubit_requirements: u32,
    pub gate_depth: u32,
    pub coherence_time_ms: f64,
    pub fidelity_threshold: f64,
    pub resource_limits: QuantumResourceLimits,
    pub security_level: SecurityLevel,
    pub compliance_frameworks: Vec<ComplianceFramework>,
    pub created_at: DateTime<Utc>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum QuantumAlgorithmType {
    VQE,        // Variational Quantum Eigensolver
    QAOA,       // Quantum Approximate Optimization Algorithm
    Shor,       // Shor's Algorithm
    Grover,     // Grover's Search
    QML,        // Quantum Machine Learning
    HPQC,       // High-Performance Quantum Computing
    Custom(String),
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct QuantumResourceLimits {
    pub max_qubits: u32,
    pub max_gate_depth: u32,
    pub max_execution_time_ms: u64,
    pub max_memory_mb: u32,
    pub max_classical_compute_cores: u16,
    pub priority_level: PriorityLevel,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SecurityLevel {
    Development,
    Staging,
    Production,
    Enterprise,
    QuantumSecure,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ComplianceFramework {
    SOC2,
    ISO27001,
    GDPR,
    HIPAA,
    NIST,
    QuantumReadiness,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum PriorityLevel {
    Low,
    Normal,
    High,
    Critical,
    Emergency,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidationResult {
    pub workload_id: Uuid,
    pub is_valid: bool,
    pub validation_score: f64,
    pub errors: Vec<ValidationError>,
    pub warnings: Vec<ValidationWarning>,
    pub resource_allocation: Option<QuantumResourceAllocation>,
    pub estimated_execution_time_ms: Option<u64>,
    pub quantum_advantage_factor: Option<f64>,
    pub validated_at: DateTime<Utc>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidationError {
    pub code: String,
    pub message: String,
    pub severity: ErrorSeverity,
    pub field: Option<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidationWarning {
    pub code: String,
    pub message: String,
    pub recommendation: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ErrorSeverity {
    Low,
    Medium,
    High,
    Critical,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct QuantumResourceAllocation {
    pub allocated_qubits: u32,
    pub allocated_gates: u32,
    pub allocated_memory_mb: u32,
    pub allocated_compute_cores: u16,
    pub execution_slot: DateTime<Utc>,
    pub estimated_completion: DateTime<Utc>,
    pub quantum_backend: QuantumBackend,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum QuantumBackend {
    Simulator,
    IonTrap,
    Superconducting,
    PhotonicQuantum,
    TopologicalQuantum,
    AnnealingQuantum,
}

pub struct QuantumWorkloadValidator {
    config: QuantumValidatorConfig,
    resource_pool: Arc<RwLock<QuantumResourcePool>>,
    compliance_checker: ComplianceChecker,
    security_analyzer: QuantumSecurityAnalyzer,
}

#[derive(Debug, Clone)]
pub struct QuantumValidatorConfig {
    pub max_concurrent_workloads: u32,
    pub default_timeout_ms: u64,
    pub enable_quantum_advantage_analysis: bool,
    pub strict_compliance_mode: bool,
    pub allowed_algorithms: Vec<QuantumAlgorithmType>,
    pub resource_limits: QuantumResourceLimits,
}

#[derive(Debug)]
pub struct QuantumResourcePool {
    pub total_qubits: u32,
    pub available_qubits: u32,
    pub total_memory_mb: u32,
    pub available_memory_mb: u32,
    pub total_compute_cores: u16,
    pub available_compute_cores: u16,
    pub active_workloads: HashMap<Uuid, QuantumWorkloadSpec>,
    pub backend_availability: HashMap<QuantumBackend, bool>,
}

pub struct ComplianceChecker {
    required_frameworks: Vec<ComplianceFramework>,
}

pub struct QuantumSecurityAnalyzer {
    security_policies: Vec<QuantumSecurityPolicy>,
}

#[derive(Debug, Clone)]
pub struct QuantumSecurityPolicy {
    pub name: String,
    pub min_security_level: SecurityLevel,
    pub allowed_algorithms: Vec<QuantumAlgorithmType>,
    pub max_qubit_limit: u32,
    pub requires_encryption: bool,
    pub audit_required: bool,
}

impl QuantumWorkloadValidator {
    pub fn new(config: QuantumValidatorConfig) -> Result<Self> {
        let resource_pool = Arc::new(RwLock::new(QuantumResourcePool {
            total_qubits: 1000, // Enterprise quantum system
            available_qubits: 1000,
            total_memory_mb: 16384, // 16GB as per requirements
            available_memory_mb: 16384,
            total_compute_cores: 4, // 4 cores as per requirements
            available_compute_cores: 4,
            active_workloads: HashMap::new(),
            backend_availability: [
                (QuantumBackend::Simulator, true),
                (QuantumBackend::IonTrap, true),
                (QuantumBackend::Superconducting, true),
                (QuantumBackend::PhotonicQuantum, false),
                (QuantumBackend::TopologicalQuantum, false),
                (QuantumBackend::AnnealingQuantum, true),
            ].iter().cloned().collect(),
        }));

        let compliance_checker = ComplianceChecker {
            required_frameworks: vec![
                ComplianceFramework::SOC2,
                ComplianceFramework::ISO27001,
                ComplianceFramework::GDPR,
                ComplianceFramework::QuantumReadiness,
            ],
        };

        let security_analyzer = QuantumSecurityAnalyzer {
            security_policies: vec![
                QuantumSecurityPolicy {
                    name: "Enterprise Production".to_string(),
                    min_security_level: SecurityLevel::Enterprise,
                    allowed_algorithms: vec![
                        QuantumAlgorithmType::VQE,
                        QuantumAlgorithmType::QAOA,
                        QuantumAlgorithmType::QML,
                        QuantumAlgorithmType::HPQC,
                    ],
                    max_qubit_limit: 100,
                    requires_encryption: true,
                    audit_required: true,
                },
                QuantumSecurityPolicy {
                    name: "Quantum Secure".to_string(),
                    min_security_level: SecurityLevel::QuantumSecure,
                    allowed_algorithms: vec![
                        QuantumAlgorithmType::VQE,
                        QuantumAlgorithmType::QAOA,
                        QuantumAlgorithmType::HPQC,
                    ],
                    max_qubit_limit: 50,
                    requires_encryption: true,
                    audit_required: true,
                },
            ],
        };

        Ok(Self {
            config,
            resource_pool,
            compliance_checker,
            security_analyzer,
        })
    }

    pub async fn validate_workload(&self, workload: &QuantumWorkloadSpec) -> Result<ValidationResult> {
        let mut result = ValidationResult {
            workload_id: workload.id,
            is_valid: true,
            validation_score: 1.0,
            errors: Vec::new(),
            warnings: Vec::new(),
            resource_allocation: None,
            estimated_execution_time_ms: None,
            quantum_advantage_factor: None,
            validated_at: Utc::now(),
        };

        // Basic validation checks
        self.validate_basic_requirements(workload, &mut result)?;

        // Resource availability validation
        self.validate_resource_availability(workload, &mut result).await?;

        // Security validation
        self.validate_security_requirements(workload, &mut result)?;

        // Compliance validation
        self.validate_compliance_requirements(workload, &mut result)?;

        // Algorithm-specific validation
        self.validate_algorithm_specific(workload, &mut result)?;

        // Performance estimation
        if result.is_valid {
            self.estimate_performance(workload, &mut result)?;
            self.allocate_resources(workload, &mut result).await?;
        }

        // Calculate final validation score
        result.validation_score = self.calculate_validation_score(&result);
        result.is_valid = result.errors.is_empty() && result.validation_score >= 0.7;

        Ok(result)
    }

    fn validate_basic_requirements(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        // Validate qubit requirements
        if workload.qubit_requirements == 0 {
            result.errors.push(ValidationError {
                code: "INVALID_QUBITS".to_string(),
                message: "Qubit requirements must be greater than 0".to_string(),
                severity: ErrorSeverity::High,
                field: Some("qubit_requirements".to_string()),
            });
        }

        if workload.qubit_requirements > self.config.resource_limits.max_qubits {
            result.errors.push(ValidationError {
                code: "EXCEEDS_QUBIT_LIMIT".to_string(),
                message: format!("Requested {} qubits exceeds limit of {}", 
                    workload.qubit_requirements, self.config.resource_limits.max_qubits),
                severity: ErrorSeverity::Critical,
                field: Some("qubit_requirements".to_string()),
            });
        }

        // Validate gate depth
        if workload.gate_depth > self.config.resource_limits.max_gate_depth {
            result.errors.push(ValidationError {
                code: "EXCEEDS_GATE_DEPTH".to_string(),
                message: format!("Gate depth {} exceeds limit of {}", 
                    workload.gate_depth, self.config.resource_limits.max_gate_depth),
                severity: ErrorSeverity::High,
                field: Some("gate_depth".to_string()),
            });
        }

        // Validate coherence time
        if workload.coherence_time_ms <= 0.0 {
            result.errors.push(ValidationError {
                code: "INVALID_COHERENCE_TIME".to_string(),
                message: "Coherence time must be positive".to_string(),
                severity: ErrorSeverity::Medium,
                field: Some("coherence_time_ms".to_string()),
            });
        }

        // Validate fidelity threshold
        if workload.fidelity_threshold < 0.0 || workload.fidelity_threshold > 1.0 {
            result.errors.push(ValidationError {
                code: "INVALID_FIDELITY".to_string(),
                message: "Fidelity threshold must be between 0.0 and 1.0".to_string(),
                severity: ErrorSeverity::Medium,
                field: Some("fidelity_threshold".to_string()),
            });
        }

        // Check algorithm allowlist
        if !self.config.allowed_algorithms.contains(&workload.algorithm_type) {
            result.errors.push(ValidationError {
                code: "ALGORITHM_NOT_ALLOWED".to_string(),
                message: format!("Algorithm {:?} is not in the allowed list", workload.algorithm_type),
                severity: ErrorSeverity::High,
                field: Some("algorithm_type".to_string()),
            });
        }

        Ok(())
    }

    async fn validate_resource_availability(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        let pool = self.resource_pool.read().await;

        if workload.qubit_requirements > pool.available_qubits {
            result.errors.push(ValidationError {
                code: "INSUFFICIENT_QUBITS".to_string(),
                message: format!("Requested {} qubits, only {} available", 
                    workload.qubit_requirements, pool.available_qubits),
                severity: ErrorSeverity::Critical,
                field: Some("qubit_requirements".to_string()),
            });
        }

        if workload.resource_limits.max_memory_mb > pool.available_memory_mb {
            result.errors.push(ValidationError {
                code: "INSUFFICIENT_MEMORY".to_string(),
                message: format!("Requested {} MB memory, only {} MB available", 
                    workload.resource_limits.max_memory_mb, pool.available_memory_mb),
                severity: ErrorSeverity::High,
                field: Some("resource_limits.max_memory_mb".to_string()),
            });
        }

        if workload.resource_limits.max_classical_compute_cores > pool.available_compute_cores {
            result.errors.push(ValidationError {
                code: "INSUFFICIENT_COMPUTE".to_string(),
                message: format!("Requested {} cores, only {} available", 
                    workload.resource_limits.max_classical_compute_cores, pool.available_compute_cores),
                severity: ErrorSeverity::High,
                field: Some("resource_limits.max_classical_compute_cores".to_string()),
            });
        }

        Ok(())
    }

    fn validate_security_requirements(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        for policy in &self.security_analyzer.security_policies {
            if self.security_level_meets_requirement(&workload.security_level, &policy.min_security_level) {
                // Check algorithm restrictions
                if !policy.allowed_algorithms.contains(&workload.algorithm_type) {
                    result.errors.push(ValidationError {
                        code: "SECURITY_ALGORITHM_RESTRICTION".to_string(),
                        message: format!("Algorithm {:?} not allowed under security policy '{}'", 
                            workload.algorithm_type, policy.name),
                        severity: ErrorSeverity::High,
                        field: Some("algorithm_type".to_string()),
                    });
                }

                // Check qubit limits
                if workload.qubit_requirements > policy.max_qubit_limit {
                    result.errors.push(ValidationError {
                        code: "SECURITY_QUBIT_LIMIT".to_string(),
                        message: format!("Qubit requirement {} exceeds security policy limit of {}", 
                            workload.qubit_requirements, policy.max_qubit_limit),
                        severity: ErrorSeverity::Critical,
                        field: Some("qubit_requirements".to_string()),
                    });
                }

                break;
            }
        }

        Ok(())
    }

    fn validate_compliance_requirements(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        for required_framework in &self.compliance_checker.required_frameworks {
            if !workload.compliance_frameworks.contains(required_framework) {
                if self.config.strict_compliance_mode {
                    result.errors.push(ValidationError {
                        code: "MISSING_COMPLIANCE_FRAMEWORK".to_string(),
                        message: format!("Required compliance framework {:?} not specified", required_framework),
                        severity: ErrorSeverity::Critical,
                        field: Some("compliance_frameworks".to_string()),
                    });
                } else {
                    result.warnings.push(ValidationWarning {
                        code: "MISSING_COMPLIANCE_FRAMEWORK".to_string(),
                        message: format!("Compliance framework {:?} not specified", required_framework),
                        recommendation: "Add required compliance frameworks for enterprise deployment".to_string(),
                    });
                }
            }
        }

        Ok(())
    }

    fn validate_algorithm_specific(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        match &workload.algorithm_type {
            QuantumAlgorithmType::Shor => {
                if workload.qubit_requirements < 10 {
                    result.warnings.push(ValidationWarning {
                        code: "SHOR_MINIMUM_QUBITS".to_string(),
                        message: "Shor's algorithm typically requires at least 10 qubits for meaningful results".to_string(),
                        recommendation: "Consider increasing qubit count for better performance".to_string(),
                    });
                }
            },
            QuantumAlgorithmType::HPQC => {
                if workload.qubit_requirements < 50 {
                    result.warnings.push(ValidationWarning {
                        code: "HPQC_MINIMUM_QUBITS".to_string(),
                        message: "HPQC algorithms typically require at least 50 qubits for quantum advantage".to_string(),
                        recommendation: "Consider increasing qubit count for quantum advantage".to_string(),
                    });
                }

                if workload.fidelity_threshold < 0.99 {
                    result.warnings.push(ValidationWarning {
                        code: "HPQC_FIDELITY_RECOMMENDATION".to_string(),
                        message: "HPQC algorithms perform best with fidelity > 0.99".to_string(),
                        recommendation: "Consider increasing fidelity threshold for optimal results".to_string(),
                    });
                }
            },
            _ => {}
        }

        Ok(())
    }

    fn estimate_performance(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        // Estimate execution time based on algorithm type and parameters
        let base_time_ms = match workload.algorithm_type {
            QuantumAlgorithmType::VQE => workload.qubit_requirements as u64 * 100,
            QuantumAlgorithmType::QAOA => workload.qubit_requirements as u64 * 150,
            QuantumAlgorithmType::Shor => workload.qubit_requirements as u64 * workload.qubit_requirements as u64 * 50,
            QuantumAlgorithmType::Grover => (workload.qubit_requirements as f64).sqrt() as u64 * 200,
            QuantumAlgorithmType::QML => workload.qubit_requirements as u64 * 300,
            QuantumAlgorithmType::HPQC => workload.qubit_requirements as u64 * 500,
            QuantumAlgorithmType::Custom(_) => workload.qubit_requirements as u64 * 200,
        };

        // Apply gate depth factor
        let gate_depth_factor = 1.0 + (workload.gate_depth as f64 / 100.0);

        result.estimated_execution_time_ms = Some((base_time_ms as f64 * gate_depth_factor) as u64);

        // Calculate quantum advantage factor if enabled
        if self.config.enable_quantum_advantage_analysis {
            result.quantum_advantage_factor = Some(self.calculate_quantum_advantage(workload));
        }

        Ok(())
    }

    async fn allocate_resources(&self, workload: &QuantumWorkloadSpec, result: &mut ValidationResult) -> Result<()> {
        let execution_slot = Utc::now() + chrono::Duration::seconds(30); // 30 second delay for preparation
        let estimated_completion = execution_slot + chrono::Duration::milliseconds(
            result.estimated_execution_time_ms.unwrap_or(60000) as i64
        );

        let backend = self.select_optimal_backend(workload).await?;

        result.resource_allocation = Some(QuantumResourceAllocation {
            allocated_qubits: workload.qubit_requirements,
            allocated_gates: workload.gate_depth,
            allocated_memory_mb: workload.resource_limits.max_memory_mb,
            allocated_compute_cores: workload.resource_limits.max_classical_compute_cores,
            execution_slot,
            estimated_completion,
            quantum_backend: backend,
        });

        Ok(())
    }

    async fn select_optimal_backend(&self, workload: &QuantumWorkloadSpec) -> Result<QuantumBackend> {
        let pool = self.resource_pool.read().await;

        // Select backend based on algorithm requirements and availability
        let preferred_backend = match workload.algorithm_type {
            QuantumAlgorithmType::VQE | QuantumAlgorithmType::QAOA => QuantumBackend::Superconducting,
            QuantumAlgorithmType::Shor | QuantumAlgorithmType::Grover => QuantumBackend::IonTrap,
            QuantumAlgorithmType::QML => QuantumBackend::Simulator,
            QuantumAlgorithmType::HPQC => QuantumBackend::Superconducting,
            QuantumAlgorithmType::Custom(_) => QuantumBackend::Simulator,
        };

        if pool.backend_availability.get(&preferred_backend).unwrap_or(&false) {
            Ok(preferred_backend)
        } else {
            // Fallback to simulator if preferred backend is not available
            Ok(QuantumBackend::Simulator)
        }
    }

    fn calculate_quantum_advantage(&self, workload: &QuantumWorkloadSpec) -> f64 {
        match workload.algorithm_type {
            QuantumAlgorithmType::Shor => {
                // Exponential advantage for factoring
                2_f64.powf(workload.qubit_requirements as f64 / 2.0)
            },
            QuantumAlgorithmType::Grover => {
                // Quadratic speedup for search
                (2_f64.powf(workload.qubit_requirements as f64 / 2.0)).sqrt()
            },
            QuantumAlgorithmType::VQE | QuantumAlgorithmType::QAOA => {
                // Moderate advantage for optimization
                workload.qubit_requirements as f64
            },
            QuantumAlgorithmType::HPQC => {
                // High advantage for specialized quantum computing
                2_f64.powf(workload.qubit_requirements as f64 / 4.0)
            },
            _ => 1.0, // No advantage assumed
        }
    }

    fn security_level_meets_requirement(&self, actual: &SecurityLevel, required: &SecurityLevel) -> bool {
        let actual_level = match actual {
            SecurityLevel::Development => 0,
            SecurityLevel::Staging => 1,
            SecurityLevel::Production => 2,
            SecurityLevel::Enterprise => 3,
            SecurityLevel::QuantumSecure => 4,
        };

        let required_level = match required {
            SecurityLevel::Development => 0,
            SecurityLevel::Staging => 1,
            SecurityLevel::Production => 2,
            SecurityLevel::Enterprise => 3,
            SecurityLevel::QuantumSecure => 4,
        };

        actual_level >= required_level
    }

    fn calculate_validation_score(&self, result: &ValidationResult) -> f64 {
        let mut score = 1.0;

        // Deduct for errors
        for error in &result.errors {
            let deduction = match error.severity {
                ErrorSeverity::Low => 0.05,
                ErrorSeverity::Medium => 0.1,
                ErrorSeverity::High => 0.2,
                ErrorSeverity::Critical => 0.4,
            };
            score -= deduction;
        }

        // Minor deduction for warnings
        score -= result.warnings.len() as f64 * 0.02;

        // Ensure score is not negative
        score.max(0.0)
    }

    pub async fn submit_workload(&self, workload: QuantumWorkloadSpec) -> Result<Uuid> {
        let validation_result = self.validate_workload(&workload).await?;

        if !validation_result.is_valid {
            return Err(anyhow!("Workload validation failed: {:?}", validation_result.errors));
        }

        let mut pool = self.resource_pool.write().await;

        // Reserve resources
        pool.available_qubits = pool.available_qubits.saturating_sub(workload.qubit_requirements);
        pool.available_memory_mb = pool.available_memory_mb.saturating_sub(workload.resource_limits.max_memory_mb);
        pool.available_compute_cores = pool.available_compute_cores.saturating_sub(workload.resource_limits.max_classical_compute_cores);

        // Add to active workloads
        pool.active_workloads.insert(workload.id, workload.clone());

        Ok(workload.id)
    }

    pub async fn get_workload_status(&self, workload_id: &Uuid) -> Result<Option<QuantumWorkloadSpec>> {
        let pool = self.resource_pool.read().await;
        Ok(pool.active_workloads.get(workload_id).cloned())
    }

    pub async fn cancel_workload(&self, workload_id: &Uuid) -> Result<()> {
        let mut pool = self.resource_pool.write().await;

        if let Some(workload) = pool.active_workloads.remove(workload_id) {
            // Release resources
            pool.available_qubits += workload.qubit_requirements;
            pool.available_memory_mb += workload.resource_limits.max_memory_mb;
            pool.available_compute_cores += workload.resource_limits.max_classical_compute_cores;
        }

        Ok(())
    }

    pub async fn get_resource_utilization(&self) -> Result<QuantumResourcePool> {
        let pool = self.resource_pool.read().await;
        Ok(QuantumResourcePool {
            total_qubits: pool.total_qubits,
            available_qubits: pool.available_qubits,
            total_memory_mb: pool.total_memory_mb,
            available_memory_mb: pool.available_memory_mb,
            total_compute_cores: pool.total_compute_cores,
            available_compute_cores: pool.available_compute_cores,
            active_workloads: pool.active_workloads.clone(),
            backend_availability: pool.backend_availability.clone(),
        })
    }
}

#[cfg(test)]
