        let validator = MockQuantumValidator::new();

        // Test high-scoring workload
        let good_workload = create_test_workload(
            QuantumAlgorithmType::VQE,
            20,
            SecurityLevel::Enterprise
        );
        let good_result = validator.validate_workload(&good_workload).await.unwrap();

        // Test low-scoring workload with multiple issues
        let mut bad_workload = create_test_workload(
            QuantumAlgorithmType::VQE,
            0, // Invalid qubits
            SecurityLevel::Development // Development security
        );
        bad_workload.fidelity_threshold = 1.5; // Invalid fidelity
        bad_workload.compliance_frameworks = vec![]; // No compliance frameworks

        let bad_result = validator.validate_workload(&bad_workload).await.unwrap();

        assert!(good_result.validation_score > bad_result.validation_score);
        assert!(good_result.validation_score >= 0.9);
        assert!(bad_result.validation_score < 0.7);
    }

    #[tokio::test]
    async fn test_validation_result_storage() {
        let validator = MockQuantumValidator::new();
        let workload = create_test_workload(
            QuantumAlgorithmType::VQE,
            20,
            SecurityLevel::Enterprise
        );
        let workload_id = workload.id;

        // Validate workload
        let result = validator.validate_workload(&workload).await.unwrap();

        // Retrieve stored result
        let stored_result = validator.get_validation_result(&workload_id).await;

        assert!(stored_result.is_some());
        let stored = stored_result.unwrap();
        assert_eq!(stored.workload_id, result.workload_id);
        assert_eq!(stored.is_valid, result.is_valid);
        assert!((stored.validation_score - result.validation_score).abs() < 0.001);
    }

    #[tokio::test]
    async fn test_concurrent_validations() {
        let validator = Arc::new(MockQuantumValidator::new());
        let mut handles = Vec::new();

        // Create multiple concurrent validation tasks
        for i in 0..10 {
            let validator_clone = validator.clone();
            let handle = tokio::spawn(async move {
                let workload = create_test_workload(
                    QuantumAlgorithmType::VQE,
                    20 + i, // Different qubit counts
                    SecurityLevel::Enterprise
                );
                let result = validator_clone.validate_workload(&workload).await.unwrap();
                (workload.id, result)
            });
            handles.push(handle);
        }

        // Wait for all validations to complete
        let mut results = Vec::new();
        for handle in handles {
            let (workload_id, result) = handle.await.unwrap();
            results.push((workload_id, result));
        }

        // Verify all validations completed successfully
        assert_eq!(results.len(), 10);
        for (workload_id, result) in results {
            assert!(result.is_valid);

            // Verify result was stored
            let stored = validator.get_validation_result(&workload_id).await;
            assert!(stored.is_some());
        }
    }
}

#[cfg(test)]
mod integration_tests {
    use super::*;

    #[tokio::test]
    async fn test_enterprise_quantum_workflow() {
        let validator = MockQuantumValidator::new();

        // Simulate enterprise quantum workflow
        let workloads = vec![
            create_test_workload(QuantumAlgorithmType::VQE, 50, SecurityLevel::Enterprise),
            create_test_workload(QuantumAlgorithmType::QAOA, 30, SecurityLevel::Enterprise),
            create_test_workload(QuantumAlgorithmType::HPQC, 100, SecurityLevel::QuantumSecure),
            create_test_workload(QuantumAlgorithmType::QML, 25, SecurityLevel::Production),
        ];

        let mut valid_workloads = 0;
        let mut total_warnings = 0;

        for workload in workloads {
            let result = validator.validate_workload(&workload).await.unwrap();

            if result.is_valid {
                valid_workloads += 1;
            }

            total_warnings += result.warnings.len();

            // All enterprise workloads should have high validation scores
            if matches!(workload.security_level, SecurityLevel::Enterprise | SecurityLevel::QuantumSecure) {
                assert!(result.validation_score >= 0.8, 
                    "Enterprise workloads should have high validation scores");
            }
        }

        assert_eq!(valid_workloads, 4, "All enterprise workloads should be valid");
        assert!(total_warnings <= 8, "Warnings should be minimal for enterprise workloads");
    }

    #[tokio::test]
    async fn test_manifolded_hpqc_integration() {
        let validator = MockQuantumValidator::new();

        // Test HPQC-specific validation
        let hpqc_workload = QuantumWorkloadSpec {
            id: Uuid::new_v4(),
            name: "Manifolded HPQC Test".to_string(),
            algorithm_type: QuantumAlgorithmType::HPQC,
            qubit_requirements: 200, // Large-scale quantum computation
            gate_depth: 1000,
            coherence_time_ms: 500.0,
            fidelity_threshold: 0.99, // High fidelity requirement
            security_level: SecurityLevel::QuantumSecure,
            compliance_frameworks: vec![
                ComplianceFramework::SOC2,
                ComplianceFramework::ISO27001,
                ComplianceFramework::GDPR,
                ComplianceFramework::QuantumReadiness,
            ],
        };

        let result = validator.validate_workload(&hpqc_workload).await.unwrap();

        assert!(result.is_valid, "HPQC workload should be valid");
        assert!(result.validation_score >= 0.95, "HPQC workload should have very high validation score");
        assert!(result.errors.is_empty(), "HPQC workload should have no errors");

        // Should have minimal warnings for well-configured HPQC workload
        assert!(result.warnings.len() <= 1, "Well-configured HPQC should have minimal warnings");
    }
}

fn create_test_workload(algorithm: QuantumAlgorithmType, qubits: u32, security: SecurityLevel) -> QuantumWorkloadSpec {
    QuantumWorkloadSpec {
        id: Uuid::new_v4(),
        name: format!("{:?} Test Workload", algorithm),
        algorithm_type: algorithm,
        qubit_requirements: qubits,
        gate_depth: 100,
        coherence_time_ms: 100.0,
        fidelity_threshold: 0.95,
        security_level: security,
        compliance_frameworks: vec![
            ComplianceFramework::SOC2,
            ComplianceFramework::ISO27001,
            ComplianceFramework::GDPR,
            ComplianceFramework::QuantumReadiness,
        ],
    }
}
