use anyhow::Result;
use clap::{Parser, Subcommand, CommandFactory};
use clap_complete::{generate, Generator, Shell};
use colored::*;
use console::Style;
use indicatif::{ProgressBar, ProgressStyle};
use std::io;
use tracing::{info, warn, error, debug};
use tracing_subscriber::EnvFilter;

mod commands;
mod config;
mod client;
mod sandbox;
mod project;
mod auth;
mod display;
mod interactive;

use commands::*;

#[derive(Parser)]
#[command(name = "manifolded")]
#[command(about = "Manifolded - The Quantum Realm for Multi-Dimensional AI")]
#[command(version)]
#[command(author)]
#[command(long_about = r#"
Manifolded is an enterprise-grade autonomous programming platform that provides
isolated development environments optimized for AI workloads with HPQC support.

Enter the quantum realm where AI agents can safely explore, learn, and evolve
across multiple dimensions of computation.
"#)]
#[command(after_help = r#"
EXAMPLES:
    # Initialize a new manifold
    manifolded init --name my-quantum-realm

    # Create a new manifold with Phoenix/Elixir for HPQC
    manifolded create --hpqc --phoenix --name quantum-processor

    # Deploy to Nomad cluster
    manifolded deploy --nomad --cluster production

    # Enter an existing manifold
    manifolded enter my-project

    # List all manifolds
    manifolded list --all

For more information, visit: https://manifolded.ai/docs
"#)]
struct Cli {
    #[command(subcommand)]
    command: Commands,

    /// Path to config file
    #[arg(short, long, global = true, env = "MANIFOLDED_CONFIG")]
    config: Option<String>,

    /// Enable verbose output
    #[arg(short, long, global = true)]
    verbose: bool,

    /// Output format (json, yaml, table)
    #[arg(short = 'o', long, global = true, default_value = "table")]
    output: OutputFormat,

    /// Disable color output
    #[arg(long, global = true)]
    no_color: bool,

    /// Quiet mode (minimal output)
    #[arg(short, long, global = true)]
    quiet: bool,
}

#[derive(Debug, Clone, clap::ValueEnum)]
enum OutputFormat {
    Table,
    Json,
    Yaml,
    Plain,
}

#[derive(Subcommand)]
enum Commands {
    /// Initialize Manifolded configuration
    #[command(visible_alias = "i")]
    Init(InitCommand),

    /// Create a new manifold (containerized environment)
    #[command(visible_alias = "c")]
    Create(CreateCommand),

    /// Enter an existing manifold
    #[command(visible_alias = "e")]
    Enter(EnterCommand),

    /// List manifolds and resources

