# Manifolded Production Nomad Job Specification
# Deploy with: nomad job run manifolded.nomad

variable "image_core" {
  type        = string
  description = "Docker image for Manifolded Core"
  default     = "registry.ovh.net/manifolded/core:latest"
}

variable "image_cli" {
  type        = string
  description = "Docker image for Manifolded CLI"
  default     = "registry.ovh.net/manifolded/cli:latest"
}

variable "image_wasm" {
  type        = string
  description = "Docker image for Vimmacs WASM runtime"
  default     = "registry.ovh.net/manifolded/wasm:latest"
}

variable "datacenter" {
  type        = string
  description = "Nomad datacenter"
  default     = "ovh-bhs1"
}

variable "environment" {
  type        = string
  description = "Deployment environment"
  default     = "production"
}

job "manifolded" {
  datacenters = [var.datacenter]
  type        = "service"
  namespace   = var.environment

  # Update strategy for zero-downtime deployments
  update {
    max_parallel      = 2
    min_healthy_time  = "30s"
    healthy_deadline  = "5m"
    progress_deadline = "10m"
    auto_revert       = true
    auto_promote      = true
    canary            = 2
    stagger           = "30s"
  }

  # Constraint to specific node types
  constraint {
    attribute = "${node.class}"
    value     = "compute"
  }

  # Spread across availability zones
  spread {
    attribute = "${node.datacenter}"
    weight    = 100
  }

  # Core Services Group
  group "core" {
    count = 3

    # Rolling updates
    update {
      max_parallel = 1
      canary       = 1
    }

    # Network configuration
    network {
      mode = "bridge"

      port "api" {
        static = 8080
        to     = 8080
      }

      port "grpc" {
        static = 50051
        to     = 50051
      }

      port "metrics" {
        static = 9090
        to     = 9090
      }
    }

    # Consul service mesh integration
    service {
      name = "manifolded-core"
      port = "api"
      tags = ["api", "production", "urlprefix-/api"]

      connect {
        sidecar_service {
          proxy {
            upstreams {
              destination_name = "manifolded-db"
              local_bind_port  = 5432
            }
            upstreams {
              destination_name = "manifolded-redis"
              local_bind_port  = 6379
            }
          }
        }
      }

      check {
        type     = "http"
        path     = "/health"
        interval = "10s"
        timeout  = "2s"
      }

      check_restart {
        limit = 3
        grace = "90s"
      }
    }

    service {
      name = "manifolded-grpc"
      port = "grpc"
      tags = ["grpc", "production"]

      check {
        type     = "grpc"
        interval = "10s"
        timeout  = "2s"
      }
    }

    service {
      name = "manifolded-metrics"
      port = "metrics"
      tags = ["metrics", "prometheus"]
    }

    # Core container task
    task "conductor" {
      driver = "docker"

      config {
        image = var.image_core
        ports = ["api", "grpc", "metrics"]

        # Security options
        security_opt = [
          "no-new-privileges:true",
          "apparmor:manifolded-strict"
        ]

        # Read-only root filesystem
        readonly_rootfs = true

        # Volume mounts
        volumes = [
          "local/config:/opt/manifolded/config:ro",
          "secrets/vault:/opt/manifolded/secrets:ro"
        ]

        # Health check
        healthchecks {
          disable = false
        }
      }

      # Vault integration for secrets
      vault {
        policies = ["manifolded-core"]

        change_mode   = "signal"
        change_signal = "SIGHUP"
      }

      # Template for configuration
      template {
        data = <<EOH
{{ with secret "secret/data/manifolded/production" }}
DATABASE_URL={{ .Data.data.database_url }}
REDIS_URL={{ .Data.data.redis_url }}
S3_BUCKET={{ .Data.data.s3_bucket }}
PHOENIX_SECRET={{ .Data.data.phoenix_secret }}
{{ end }}
EOH
        destination = "secrets/vault/.env"
        env         = true
      }

      # Template for config file
      template {
        data = file("../configs/production.yaml")
        destination = "local/config/manifolded.toml"
        change_mode = "restart"
      }

      # Environment variables
      env {
        MANIFOLDED_ENV        = var.environment
        RUST_LOG             = "info"
        RUST_BACKTRACE       = "1"
        NOMAD_ADDR           = "${NOMAD_ADDR}"
        CONSUL_HTTP_ADDR     = "${attr.consul.http_addr}"
      }

      # Resource allocation
      resources {
        cpu    = 2000  # MHz
        memory = 4096  # MB

        device "nvidia/gpu" {
          count = 0  # Set to 1 if GPU is needed
        }
      }

      # Restart policy
      restart {
        attempts = 3
        interval = "5m"
        delay    = "30s"
        mode     = "delay"
      }

      # Logs configuration
      logs {
        max_files     = 10
        max_file_size = 10  # MB
      }
    }

    # Sidecar for metrics aggregation
    task "metrics-exporter" {
      driver = "docker"

      lifecycle {
        hook    = "prestart"
        sidecar = true
      }

      config {
        image = "prom/node-exporter:latest"
        ports = ["metrics"]
      }

      resources {
        cpu    = 100
        memory = 128
      }
    }
  }

  # Phoenix/Elixir HPQC Group
  group "phoenix" {
    count = 2

    network {
      mode = "bridge"

      port "web" {
        static = 4000
        to     = 4000
      }
    }

    service {
      name = "manifolded-phoenix"
      port = "web"
      tags = ["web", "hpqc", "production", "urlprefix-/"]

      connect {
        sidecar_service {}
      }

      check {
        type     = "http"
        path     = "/health"
        interval = "10s"
        timeout  = "2s"
      }
    }

    task "phoenix" {
      driver = "docker"

      config {
        image = var.image_core
        ports = ["web"]

        command = "/opt/manifolded/phoenix/bin/cryptobox_phoenix"
        args    = ["start"]
      }

      vault {
        policies = ["manifolded-phoenix"]
      }

      template {
        data = <<EOH
{{ with secret "secret/data/manifolded/phoenix" }}
SECRET_KEY_BASE={{ .Data.data.secret_key_base }}
DATABASE_URL={{ .Data.data.database_url }}
PHX_HOST={{ env "attr.unique.network.ip-address" }}
{{ end }}
EOH
        destination = "secrets/.env"
        env         = true
      }

      env {
        PORT           = "4000"
        MIX_ENV       = "prod"
        PHX_SERVER    = "true"
        POOL_SIZE     = "10"
      }

      resources {
        cpu    = 1000
        memory = 2048
      }
    }
  }

  # WASM Runtime Group (Vimmacs Integration)
  group "wasm" {
    count = 3

    network {
      mode = "bridge"

      port "wasm" {
        static = 8081
        to     = 8081
      }

      port "wasm_grpc" {
        static = 50052
        to     = 50052
      }
    }

    # Volume for WASM modules
    volume "wasm-modules" {
      type      = "host"
      source    = "wasm-modules"
      read_only = true
    }

    service {
      name = "manifolded-wasm"
      port = "wasm"
      tags = ["wasm", "runtime", "production"]

      connect {
        sidecar_service {}
      }

      check {
        type     = "http"
        path     = "/health"
        interval = "10s"
        timeout  = "2s"
      }
    }

    task "wasm-runtime" {
      driver = "docker"

      config {
        image = var.image_wasm
        ports = ["wasm", "wasm_grpc"]

        # Enable Kata Containers for additional isolation
        runtime = "kata-runtime"

        # Security options
        security_opt = [
          "seccomp:strict.json"
        ]

        # Capabilities
        cap_drop = ["ALL"]
        cap_add  = ["NET_BIND_SERVICE"]

        volumes = [
          "wasm-modules:/opt/manifolded/wasm:ro"
        ]
      }

      volume_mount {
        volume      = "wasm-modules"
        destination = "/opt/manifolded/wasm"
        read_only   = true
      }

      vault {
        policies = ["manifolded-wasm"]
      }

      env {
        WASM_RUNTIME      = "wasmer"
        KATA_ENABLED      = "true"
        MAX_WASM_MEMORY   = "4Gi"
        WASM_CACHE_SIZE   = "10Gi"
      }

      resources {
        cpu    = 2000
        memory = 4096
      }

      # Artifact for WASM modules
      artifact {
        source      = "https://storage.ovh.net/manifolded/wasm-modules.tar.gz"
        destination = "local/wasm"
      }
    }
  }

  # Load Balancer Group
  group "gateway" {
    count = 2

    network {
      mode = "bridge"

      port "http" {
        static = 80
        to     = 80
      }

      port "https" {
        static = 443
        to     = 443
      }
    }

    service {
      name = "manifolded-gateway"
      port = "https"
      tags = ["gateway", "lb", "production"]

      check {
        type     = "tcp"
        interval = "10s"
        timeout  = "2s"
      }
    }

    task "nginx" {
      driver = "docker"

      config {
        image = "nginx:alpine"
        ports = ["http", "https"]

        volumes = [
          "local/nginx.conf:/etc/nginx/nginx.conf:ro",
          "secrets/certs:/etc/nginx/certs:ro"
        ]
      }

      template {
        data = file("../nginx/nginx.conf")
        destination = "local/nginx.conf"
        change_mode = "restart"
      }

      template {
        data = <<EOH
{{ with secret "secret/data/manifolded/tls" }}
{{ .Data.data.cert }}
{{ end }}
EOH
        destination = "secrets/certs/server.crt"
      }

      template {
        data = <<EOH
{{ with secret "secret/data/manifolded/tls" }}
{{ .Data.data.key }}
{{ end }}
EOH
        destination = "secrets/certs/server.key"
        perms = "600"
      }

      resources {
        cpu    = 500
        memory = 512
      }
    }
  }

  # Periodic batch job for maintenance
  group "maintenance" {
    task "backup" {
      driver = "docker"

      config {
        image = var.image_core
        command = "/opt/manifolded/bin/manifolded-backup"
      }

      # Run daily at 2 AM
      periodic {
        cron             = "0 2 * * *"
        prohibit_overlap = true
        time_zone        = "UTC"
      }

      vault {
        policies = ["manifolded-backup"]
      }

      resources {
        cpu    = 1000
        memory = 2048
      }
    }
  }
}

