#!/bin/bash
set -euo pipefail

# OVH Production Deployment Script
# Deploys Manifold platform to OVH Cloud using HashiCorp Nomad

SCRIPT_DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )"
PROJECT_ROOT="$(dirname "$SCRIPT_DIR")"

# Load configuration
if [ -f "${PROJECT_ROOT}/.env.registry" ]; then
    source "${PROJECT_ROOT}/.env.registry"
fi

if [ -f "${PROJECT_ROOT}/.env.ovh" ]; then
    source "${PROJECT_ROOT}/.env.ovh"
fi

# Configuration with defaults
OVH_REGION="${OVH_REGION:-GRA}"
NOMAD_NAMESPACE="${NOMAD_NAMESPACE:-manifold}"
VAULT_NAMESPACE="${VAULT_NAMESPACE:-manifold}"
DEPLOYMENT_ENVIRONMENT="${DEPLOYMENT_ENVIRONMENT:-production}"
VERSION="${VERSION:-latest}"

# OVH Cloud configuration
OVH_PROJECT_ID="${OVH_PROJECT_ID:-}"
OVH_CLUSTER_ID="${OVH_CLUSTER_ID:-}"
NOMAD_ADDR="${NOMAD_ADDR:-https://nomad.ovh.manifolded.com:4646}"
VAULT_ADDR="${VAULT_ADDR:-https://vault.ovh.manifolded.com:8200}"
CONSUL_HTTP_ADDR="${CONSUL_HTTP_ADDR:-https://consul.ovh.manifolded.com:8500}"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
PURPLE='\033[0;35m'
CYAN='\033[0;36m'
NC='\033[0m' # No Color

log_info() { echo -e "${GREEN}[INFO]${NC} $1"; }
log_warn() { echo -e "${YELLOW}[WARN]${NC} $1"; }
log_error() { echo -e "${RED}[ERROR]${NC} $1"; }
log_deploy() { echo -e "${BLUE}[DEPLOY]${NC} $1"; }
log_vault() { echo -e "${PURPLE}[VAULT]${NC} $1"; }
log_success() { echo -e "${CYAN}[SUCCESS]${NC} $1"; }

# Check prerequisites
check_prerequisites() {
    log_info "Checking deployment prerequisites..."

    # Check for required tools
    for tool in nomad vault consul curl jq; do
        if ! command -v "$tool" &> /dev/null; then
            log_error "$tool is not installed"
            exit 1
        fi
    done

    # Check for required environment variables
    if [ -z "$OVH_PROJECT_ID" ]; then
        log_error "OVH_PROJECT_ID not set"
        exit 1
    fi

    # Check Nomad connectivity
    if ! nomad server members &> /dev/null; then
        log_error "Cannot connect to Nomad cluster at ${NOMAD_ADDR}"
        log_error "Please check NOMAD_ADDR and authentication"
        exit 1
    fi

    # Check Vault connectivity
    if ! vault status &> /dev/null; then
        log_error "Cannot connect to Vault at ${VAULT_ADDR}"
        log_error "Please check VAULT_ADDR and authentication"
        exit 1
    fi

    # Check Consul connectivity
    if ! consul members &> /dev/null; then
        log_error "Cannot connect to Consul at ${CONSUL_HTTP_ADDR}"
        log_error "Please check CONSUL_HTTP_ADDR and authentication"
        exit 1
    fi

    log_success "Prerequisites check completed"
}

# Setup Vault secrets and policies
setup_vault_secrets() {
    log_vault "Setting up Vault secrets and policies..."

    # Enable secrets engines
    vault secrets enable -path=manifold/kv -version=2 kv || true
    vault secrets enable -path=manifold/database database || true
    vault secrets enable -path=manifold/transit transit || true

    # Create encryption key for sensitive data
    vault write -f manifold/transit/keys/manifold-data || true

    # Store container registry credentials
    vault kv put manifold/kv/registry \
        ovh_username="${OVH_REGISTRY_USERNAME}" \
        ovh_password="${OVH_REGISTRY_PASSWORD}" \
        dockerhub_username="${DOCKERHUB_USERNAME:-}" \
        dockerhub_token="${DOCKERHUB_TOKEN:-}"

    # Store OVH Cloud credentials
    vault kv put manifold/kv/ovh \
        project_id="${OVH_PROJECT_ID}" \
        cluster_id="${OVH_CLUSTER_ID}" \
        region="${OVH_REGION}"

    # Store application secrets
    vault kv put manifold/kv/app \
        jwt_secret="$(openssl rand -base64 32)" \
        encryption_key="$(openssl rand -base64 32)" \
        api_key="$(openssl rand -base64 24)"

    # Create Vault policy for Manifold services
    cat > /tmp/manifold-policy.hcl << 'EOF'
path "manifold/kv/data/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}

path "manifold/transit/encrypt/manifold-data" {
  capabilities = ["update"]
}

path "manifold/transit/decrypt/manifold-data" {
  capabilities = ["update"]
}

path "manifold/database/creds/manifold-app" {
  capabilities = ["read"]
}

path "auth/token/lookup-self" {
  capabilities = ["read"]
}

path "auth/token/renew-self" {
  capabilities = ["update"]
}
EOF

    vault policy write manifold-policy /tmp/manifold-policy.hcl
    rm /tmp/manifold-policy.hcl

    # Create service tokens
    VAULT_TOKEN_CORE=$(vault write -field=token auth/token/create \
        policies="manifold-policy" \
        ttl=8760h \
        renewable=true \
        display_name="manifold-core")

    VAULT_TOKEN_VIMMACS=$(vault write -field=token auth/token/create \
        policies="manifold-policy" \
        ttl=8760h \
        renewable=true \
        display_name="vimmacs-runtime")

    # Store service tokens securely
    vault kv put manifold/kv/tokens \
        core_token="${VAULT_TOKEN_CORE}" \
        vimmacs_token="${VAULT_TOKEN_VIMMACS}"

    log_success "Vault secrets and policies configured"
}

# Create Consul service mesh configuration
setup_consul_connect() {
    log_info "Setting up Consul Connect service mesh..."

    # Enable Connect
    consul connect ca set-config -config-file - << 'EOF'
{
  "Provider": "consul",
  "Config": {
    "LeafCertTTL": "72h",
    "RootCertTTL": "8760h"
  }
}
EOF

    # Create service intentions for secure communication
    consul intention create -allow manifold-cli manifold-core
    consul intention create -allow manifold-core vimmacs-runtime
    consul intention create -allow vimmacs-runtime manifold-core
    consul intention create -deny "*" manifold-core
    consul intention create -deny "*" vimmacs-runtime

    log_success "Consul Connect service mesh configured"
}

# Deploy Nomad job specifications
deploy_nomad_jobs() {
    log_deploy "Deploying Nomad job specifications..."

    # Create Nomad namespace
    nomad namespace apply -description "Manifold Production Environment" "$NOMAD_NAMESPACE" || true

    # Deploy core services job
    cat > /tmp/manifold-core.nomad << EOF
job "manifold-core" {
  namespace   = "${NOMAD_NAMESPACE}"
  region      = "${OVH_REGION}"
  datacenters = ["${OVH_REGION}"]
  type        = "service"
  priority    = 80

  constraint {
    attribute = "\${attr.kernel.name}"
    value     = "linux"
  }

  group "core" {
    count = 3

    network {
      mode = "bridge"
      port "grpc" {
        static = 50051
      }
      port "metrics" {
        static = 9090
      }
    }

    service {
      name = "manifold-core"
      port = "grpc"

      connect {
        sidecar_service {}
      }

      check {
        type     = "grpc"
        interval = "10s"
        timeout  = "3s"
      }
    }

    service {
      name = "manifold-metrics"
      port = "metrics"

      check {
        type     = "http"
        path     = "/metrics"
        interval = "15s"
        timeout  = "5s"
      }
    }

    vault {
      policies = ["manifold-policy"]
      change_mode = "restart"
    }

    task "core-engine" {
      driver = "docker"

      config {
        image = "${OVH_REGISTRY}/${MANIFOLD_NAMESPACE}/core:${VERSION}"

        auth {
          username = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_USERNAME}"
          password = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "metrics"]

        mount {
          type   = "volume"
          target = "/var/lib/manifold/containers"
          source = "manifold-containers"
        }

        logging {
          type = "journald"
          config {
            tag = "manifold-core"
          }
        }
      }

      template {
        data = <<-EOH
        {{- with secret "manifold/kv/tokens" -}}
        VAULT_TOKEN={{ .Data.data.core_token }}
        {{- end -}}
        {{- with secret "manifold/kv/app" -}}
        JWT_SECRET={{ .Data.data.jwt_secret }}
        ENCRYPTION_KEY={{ .Data.data.encryption_key }}
        {{- end -}}
        EOH
        destination = "secrets/app.env"
        env         = true
      }

      resources {
        cpu    = 2000
        memory = 4096
      }

      env {
        MANIFOLD_ENVIRONMENT = "${DEPLOYMENT_ENVIRONMENT}"
        MANIFOLD_REGION = "${OVH_REGION}"
        RUST_LOG = "info"
        RUST_BACKTRACE = "1"
      }
    }

    restart {
      attempts = 3
      interval = "5m"
      delay    = "15s"
      mode     = "delay"
    }

    reschedule {
      attempts  = 5
      interval  = "1h"
      unlimited = false
    }

    update {
      max_parallel     = 1
      min_healthy_time = "30s"
      healthy_deadline = "5m"
      canary           = 1
      auto_revert      = true
    }
  }
}
EOF

    # Deploy Vimmacs runtime job
    cat > /tmp/vimmacs-runtime.nomad << EOF
job "vimmacs-runtime" {
  namespace   = "${NOMAD_NAMESPACE}"
  region      = "${OVH_REGION}"
  datacenters = ["${OVH_REGION}"]
  type        = "service"
  priority    = 70

  group "runtime" {
    count = 2

    network {
      mode = "bridge"
      port "grpc" {
        static = 50052
      }
      port "http" {
        static = 8081
      }
      port "metrics" {
        static = 9091
      }
    }

    service {
      name = "vimmacs-runtime"
      port = "grpc"

      connect {
        sidecar_service {}
      }

      check {
        type     = "grpc"
        interval = "15s"
        timeout  = "5s"
      }
    }

    vault {
      policies = ["manifold-policy"]
      change_mode = "restart"
    }

    task "wasm-runtime" {
      driver = "docker"

      config {
        image = "${OVH_REGISTRY}/${VIMMACS_NAMESPACE}/runtime:${VERSION}"

        auth {
          username = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_USERNAME}"
          password = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "http", "metrics"]

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/cache"
          source = "vimmacs-cache"
        }

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/sessions"
          source = "vimmacs-sessions"
        }

        privileged = true
      }

      template {
        data = <<-EOH
        {{- with secret "manifold/kv/tokens" -}}
        VAULT_TOKEN={{ .Data.data.vimmacs_token }}
        {{- end -}}
        EOH
        destination = "secrets/app.env"
        env         = true
      }

      resources {
        cpu    = 1500
        memory = 3072
      }

      env {
        WASMER_CACHE_DIR = "/var/lib/vimmacs/cache"
        WASM_MAX_MEMORY = "4294967296"
        KATA_INTEGRATION = "enabled"
      }
    }

    restart {
      attempts = 3
      interval = "5m"
      delay    = "15s"
      mode     = "delay"
    }

    update {
      max_parallel     = 1
      min_healthy_time = "45s"
      healthy_deadline = "10m"
      canary           = 1
      auto_revert      = true
    }
  }
}
EOF

    # Deploy security services job
    cat > /tmp/manifold-security.nomad << EOF
job "manifold-security" {
  namespace   = "${NOMAD_NAMESPACE}"
  region      = "${OVH_REGION}"
  datacenters = ["${OVH_REGION}"]
  type        = "service"
  priority    = 90

  group "security" {
    count = 2

    network {
      mode = "bridge"
      port "https" {
        static = 8443
      }
    }

    service {
      name = "manifold-security"
      port = "https"

      check {
        type     = "https"
        path     = "/health"
        interval = "30s"
        timeout  = "10s"
      }
    }

    vault {
      policies = ["manifold-policy"]
      change_mode = "restart"
    }

    task "security-scanner" {
      driver = "docker"

      config {
        image = "${OVH_REGISTRY}/${MANIFOLD_NAMESPACE}/security:${VERSION}"

        auth {
          username = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_USERNAME}"
          password = "\${VAULT_KV_MANIFOLD_KV_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["https"]
      }

      resources {
        cpu    = 500
        memory = 1024
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }
}
EOF

    # Deploy load balancer job
    cat > /tmp/manifold-lb.nomad << EOF
job "manifold-lb" {
  namespace   = "${NOMAD_NAMESPACE}"
  region      = "${OVH_REGION}"
  datacenters = ["${OVH_REGION}"]
  type        = "service"
  priority    = 100

  group "traefik" {
    count = 2

    network {
      mode = "bridge"
      port "http" {
        static = 80
      }
      port "https" {
        static = 443
      }
      port "api" {
        static = 8080
      }
    }

    service {
      name = "traefik"
      port = "https"

      check {
        type     = "http"
        path     = "/ping"
        port     = "api"
        interval = "10s"
        timeout  = "3s"
      }
    }

    task "traefik" {
      driver = "docker"

      config {
        image = "traefik:3.0"
        ports = ["http", "https", "api"]

        args = [
          "--api.dashboard=true",
          "--api.insecure=false",
          "--entrypoints.web.address=:80",
          "--entrypoints.websecure.address=:443",
          "--providers.consul.rootkey=traefik",
          "--providers.consulcatalog.prefix=traefik",
          "--providers.consulcatalog.exposedbydefault=false",
          "--providers.consulcatalog.endpoint=\${CONSUL_HTTP_ADDR}",
          "--certificatesresolvers.letsencrypt.acme.tlschallenge=true",
          "--certificatesresolvers.letsencrypt.acme.email=admin@manifolded.com",
          "--certificatesresolvers.letsencrypt.acme.storage=/data/acme.json",
          "--log.level=INFO"
        ]

        mount {
          type   = "volume"
          target = "/data"
          source = "traefik-data"
        }
      }

      resources {
        cpu    = 500
        memory = 512
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }
}
EOF

    # Deploy the jobs
    log_deploy "Deploying Manifold Core service..."
    nomad job run /tmp/manifold-core.nomad

    log_deploy "Deploying Vimmacs Runtime service..."
    nomad job run /tmp/vimmacs-runtime.nomad

    log_deploy "Deploying Security services..."
    nomad job run /tmp/manifold-security.nomad

    log_deploy "Deploying Load Balancer..."
    nomad job run /tmp/manifold-lb.nomad

    # Cleanup temporary files
    rm -f /tmp/manifold-*.nomad

    log_success "Nomad jobs deployed successfully"
}

# Setup monitoring and alerting
setup_monitoring() {
    log_info "Setting up monitoring and alerting..."

    # Deploy Prometheus for metrics collection
    cat > /tmp/prometheus.nomad << EOF
job "prometheus" {
  namespace   = "${NOMAD_NAMESPACE}"
  region      = "${OVH_REGION}"
  datacenters = ["${OVH_REGION}"]
  type        = "service"

  group "prometheus" {
    count = 1

    network {
      mode = "bridge"
      port "prometheus" {
        static = 9090
      }
    }

    service {
      name = "prometheus"
      port = "prometheus"

      check {
        type     = "http"
        path     = "/-/healthy"
        interval = "30s"
        timeout  = "10s"
      }
    }

    task "prometheus" {
      driver = "docker"

      config {
        image = "prom/prometheus:latest"
        ports = ["prometheus"]

        args = [
          "--config.file=/etc/prometheus/prometheus.yml",
          "--storage.tsdb.path=/prometheus",
          "--web.console.libraries=/etc/prometheus/console_libraries",
          "--web.console.templates=/etc/prometheus/consoles",
          "--web.enable-lifecycle"
        ]

        mount {
          type   = "volume"
          target = "/prometheus"
          source = "prometheus-data"
        }
      }

      template {
        data = <<-EOH
global:
  scrape_interval: 15s

scrape_configs:
  - job_name: 'manifold-core'
    consul_sd_configs:
      - server: 'localhost:8500'
        services: ['manifold-metrics']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'vimmacs-runtime'
    consul_sd_configs:
      - server: 'localhost:8500'
        services: ['vimmacs-metrics']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'nomad'
    static_configs:
      - targets: ['localhost:4646']
EOH
        destination = "local/prometheus.yml"
        change_mode = "restart"
      }

      resources {
        cpu    = 1000
        memory = 2048
      }
    }

    update {
      max_parallel = 1
      auto_revert  = true
    }
  }
}
EOF

    nomad job run /tmp/prometheus.nomad
    rm -f /tmp/prometheus.nomad

    log_success "Monitoring setup completed"
}

# Validate deployment health
validate_deployment() {
    log_info "Validating deployment health..."

    # Wait for services to be healthy
    local max_wait=300  # 5 minutes
    local wait_time=0

    while [ $wait_time -lt $max_wait ]; do
        # Check Nomad job status
        local core_status=$(nomad job status -namespace="$NOMAD_NAMESPACE" manifold-core 2>/dev/null | grep "Status" | awk '{print $3}' || echo "unknown")
        local vimmacs_status=$(nomad job status -namespace="$NOMAD_NAMESPACE" vimmacs-runtime 2>/dev/null | grep "Status" | awk '{print $3}' || echo "unknown")

        if [ "$core_status" = "running" ] && [ "$vimmacs_status" = "running" ]; then
            log_success "All services are running"
            break
        fi

        log_info "Waiting for services to become healthy... (${wait_time}s/${max_wait}s)"
        sleep 15
        wait_time=$((wait_time + 15))
    done

    if [ $wait_time -ge $max_wait ]; then
        log_error "Deployment validation timeout reached"
        exit 1
    fi

    # Test service connectivity
    log_info "Testing service connectivity..."

    # Get service endpoints from Consul
    local core_endpoint=$(consul catalog service manifold-core -format=json | jq -r '.[0].ServiceAddress + ":" + (.[0].ServicePort | tostring)' 2>/dev/null || echo "")
    local vimmacs_endpoint=$(consul catalog service vimmacs-runtime -format=json | jq -r '.[0].ServiceAddress + ":" + (.[0].ServicePort | tostring)' 2>/dev/null || echo "")

    if [ -n "$core_endpoint" ]; then
        log_success "âœ“ Manifold Core reachable at: $core_endpoint"
    else
        log_warn "âš  Cannot determine Manifold Core endpoint"
    fi

    if [ -n "$vimmacs_endpoint" ]; then
        log_success "âœ“ Vimmacs Runtime reachable at: $vimmacs_endpoint"
    else
        log_warn "âš  Cannot determine Vimmacs Runtime endpoint"
    fi

    log_success "Deployment validation completed"
}

# Generate deployment report
generate_deployment_report() {
    log_info "Generating deployment report..."

    local report_file="${PROJECT_ROOT}/deployment-report-${VERSION}.json"

    cat > "$report_file" << EOF
{
  "deployment": {
    "version": "${VERSION}",
    "environment": "${DEPLOYMENT_ENVIRONMENT}",
    "region": "${OVH_REGION}",
    "timestamp": "$(date -u +%Y-%m-%dT%H:%M:%SZ)",
    "project_id": "${OVH_PROJECT_ID}",
    "cluster_id": "${OVH_CLUSTER_ID}"
  },
  "services": {
    "manifold_core": {
      "image": "${OVH_REGISTRY}/${MANIFOLD_NAMESPACE}/core:${VERSION}",
      "replicas": 3,
      "status": "$(nomad job status -namespace="$NOMAD_NAMESPACE" manifold-core 2>/dev/null | grep "Status" | awk '{print $3}' || echo "unknown")"
    },
    "vimmacs_runtime": {
      "image": "${OVH_REGISTRY}/${VIMMACS_NAMESPACE}/runtime:${VERSION}",
      "replicas": 2,
      "status": "$(nomad job status -namespace="$NOMAD_NAMESPACE" vimmacs-runtime 2>/dev/null | grep "Status" | awk '{print $3}' || echo "unknown")"
    },
    "security": {
      "image": "${OVH_REGISTRY}/${MANIFOLD_NAMESPACE}/security:${VERSION}",
      "replicas": 2,
      "status": "$(nomad job status -namespace="$NOMAD_NAMESPACE" manifold-security 2>/dev/null | grep "Status" | awk '{print $3}' || echo "unknown")"
    }
  },
  "infrastructure": {
    "nomad_namespace": "${NOMAD_NAMESPACE}",
    "vault_namespace": "${VAULT_NAMESPACE}",
    "consul_connect": "enabled",
    "monitoring": "enabled"
  },
  "endpoints": {
    "nomad": "${NOMAD_ADDR}",
    "vault": "${VAULT_ADDR}",
    "consul": "${CONSUL_HTTP_ADDR}"
  }
}
EOF

    log_success "Deployment report generated: $report_file"
}

# Main deployment orchestration
main() {
    log_info "Starting OVH production deployment..."
    echo
    echo "========================================="
    echo "    Manifold OVH Production Deploy     "
    echo "========================================="
    echo "Version: ${VERSION}"
    echo "Environment: ${DEPLOYMENT_ENVIRONMENT}"
    echo "Region: ${OVH_REGION}"
    echo "Namespace: ${NOMAD_NAMESPACE}"
    echo "========================================="
    echo

    # Execute deployment pipeline
    check_prerequisites

    log_info "Phase 1: Infrastructure setup..."
    setup_vault_secrets
    setup_consul_connect

    log_info "Phase 2: Service deployment..."
    deploy_nomad_jobs

    log_info "Phase 3: Monitoring and validation..."
    setup_monitoring
    validate_deployment
    generate_deployment_report

    echo
    echo "========================================="
    echo "      Deployment Complete!              "
    echo "========================================="
    echo "Environment: ${DEPLOYMENT_ENVIRONMENT}"
    echo "Version: ${VERSION}"
    echo "Region: ${OVH_REGION}"
    echo
    echo "Service Endpoints:"
    echo "  - Nomad UI: ${NOMAD_ADDR}/ui"
    echo "  - Vault UI: ${VAULT_ADDR}/ui"
    echo "  - Consul UI: ${CONSUL_HTTP_ADDR}/ui"
    echo
    echo "Management Commands:"
    echo "  nomad job status -namespace=${NOMAD_NAMESPACE}"
    echo "  vault kv list manifold/kv/"
    echo "  consul catalog services"
    echo
    echo "Next steps:"
    echo "  1. Configure DNS records for load balancer"
    echo "  2. Run integration tests: ./scripts/test-integration.sh"
    echo "  3. Set up monitoring alerts and dashboards"
    echo "  4. Configure backup and disaster recovery"
    echo "========================================="
}

# Handle script interruption
trap 'log_error "Deployment interrupted!"; exit 1' INT TERM

# Run main function
main "$@"

