input.content:
# Manifolded Production Deployment
# Production-split architecture with 8-layer sandboxing

variable "image_core" {
  description = "Manifold Core private container image"
  type        = string
}

variable "image_security" {
  description = "Manifold Security scanner container image"
  type        = string
}

variable "image_sandbox" {
  description = "Manifold WebAssembly sandbox container image"
  type        = string
}

variable "image_vimmacs" {
  description = "Vimmacs WebAssembly runtime container image"
  type        = string
}

variable "image_cli" {
  description = "Manifold CLI public container image"
  type        = string
}

variable "datacenter" {
  description = "Target datacenter for deployment"
  type        = string
  default     = "dc1"
}

variable "environment" {
  description = "Deployment environment"
  type        = string
  default     = "production"
}

variable "registry_ovh" {
  description = "OVH private registry"
  type        = string
  default     = "registry.ovh.net"
}

variable "registry_public" {
  description = "Public registry (Docker Hub)"
  type        = string
  default     = "docker.io"
}

job "manifolded-production" {
  datacenters = [var.datacenter]
  type        = "service"
  priority    = 80

  # Constraint to ensure deployment on Linux nodes
  constraint {
    attribute = "${attr.kernel.name}"
    value     = "linux"
  }

  # Constraint to ensure nodes have adequate resources for sandboxing
  constraint {
    attribute = "${attr.cpu.totalcompute}"
    operator  = ">="
    value     = "4000"
  }

  # Manifold Core Service (8-Layer Sandboxing)
  group "core" {
    count = 3

    # Spread across different nodes for high availability
    spread {
      attribute = "${node.unique.id}"
      weight    = 100
    }

    network {
      mode = "bridge"

      port "grpc" {
        static = 50051
      }

      port "metrics" {
        static = 9090
      }

      port "health" {
        static = 8080
      }
    }

    # Consul Connect service mesh
    service {
      name = "manifolded-core"
      port = "grpc"
      tags = ["production", "grpc", "8-layer-sandbox", "quantum"]

      meta {
        version     = "1.0.0"
        environment = var.environment
        security    = "maximum"
        layers      = "8"
      }

      connect {
        sidecar_service {
          proxy {
            upstreams {
              destination_name = "vimmacs-runtime"
              local_bind_port  = 50052
            }

            upstreams {
              destination_name = "manifolded-security"
              local_bind_port  = 8443
            }
          }
        }
      }

      check {
        type     = "grpc"
        interval = "10s"
        timeout  = "3s"
      }
    }

    # Metrics service for Prometheus
    service {
      name = "manifolded-metrics"
      port = "metrics"
      tags = ["metrics", "prometheus"]

      check {
        type     = "http"
        path     = "/metrics"
        interval = "15s"
        timeout  = "5s"
      }
    }

    # Vault integration for secrets
    vault {
      policies = ["manifolded-core"]
      change_mode = "restart"
    }

    task "core-engine" {
      driver = "docker"

      config {
        image = var.image_core

        # Authentication for private registry
        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "metrics", "health"]

        # Enable privileged mode for 8-layer sandboxing
        privileged = true

        # Mount volumes for container storage and security
        mount {
          type   = "volume"
          target = "/var/lib/manifold/containers"
          source = "manifold-containers"
          volume_options {
            no_copy = false
            driver_config {
              name = "local"
              options {
                type   = "ext4"
                device = "/dev/manifold-storage"
              }
            }
          }
        }

        mount {
          type   = "volume"
          target = "/var/lib/manifold/layers"
          source = "manifold-layers"
        }

        # Security configurations
        cap_add = ["SYS_ADMIN", "NET_ADMIN", "SYS_CHROOT"]

        security_opt = [
          "apparmor:manifold-profile",
          "seccomp:/opt/manifold/config/seccomp/manifold.json"
        ]

        # Logging configuration
        logging {
          type = "json-file"
          config {
            max-size = "10m"
            max-file = "5"
            labels   = "service,version,environment"
          }
        }
      }

      # Environment variables from Vault
      template {
        data = <<-EOH
        {{- with secret "secret/manifolded/production" -}}
        DATABASE_URL={{ .Data.data.database_url }}
        REDIS_URL={{ .Data.data.redis_url }}
        S3_BUCKET={{ .Data.data.s3_bucket }}
        PHOENIX_SECRET_KEY_BASE={{ .Data.data.phoenix_secret }}
        WASM_SIGNING_KEY={{ .Data.data.wasm_signing_key }}
        {{- end -}}

        {{- with secret "secret/manifolded/security" -}}
        MANIFOLD_SANDBOX_LAYERS={{ .Data.data.sandbox_layers }}
        MANIFOLD_ISOLATION_LEVEL={{ .Data.data.isolation_level }}
        KATA_ENABLED={{ .Data.data.kata_enabled }}
        QUANTUM_WASM_ENABLED={{ .Data.data.quantum_wasm_enabled }}
        AIR_GAP_ENABLED={{ .Data.data.air_gap_enabled }}
        {{- end -}}
        EOH
        destination = "secrets/app.env"
        env         = true
      }

      resources {
        cpu    = 2000
        memory = 4096
        device "nvidia/gpu" {
          count = 1
          constraint {
            attribute = "${device.attr.compute_capability}"
            operator  = ">="
            value     = "6.0"
          }
        }
      }

      env {
        MANIFOLD_ENVIRONMENT = var.environment
        MANIFOLD_DATACENTER  = var.datacenter
        RUST_LOG = "info"
        RUST_BACKTRACE = "1"
        NOMAD_ALLOC_ID = "${NOMAD_ALLOC_ID}"
        NOMAD_TASK_NAME = "${NOMAD_TASK_NAME}"
      }
    }

    # Restart and update policies
    restart {
      attempts = 3
      interval = "5m"
      delay    = "15s"
      mode     = "delay"
    }

    reschedule {
      attempts       = 5
      interval       = "1h"
      delay          = "30s"
      delay_function = "exponential"
      max_delay      = "10m"
      unlimited      = false
    }

    update {
      max_parallel      = 1
      min_healthy_time  = "30s"
      healthy_deadline  = "5m"
      progress_deadline = "10m"
      canary            = 1
      auto_revert       = true
      auto_promote      = true
    }
  }

  # Vimmacs WebAssembly Runtime Group
  group "vimmacs" {
    count = 2

    network {
      mode = "bridge"

      port "grpc" {
        static = 50052
      }

      port "http" {
        static = 8081
      }

      port "metrics" {
        static = 9091
      }
    }

    service {
      name = "vimmacs-runtime"
      port = "grpc"
      tags = ["wasm", "archlinux", "kata", "runtime"]

      meta {
        version = "1.0.0"
        runtime = "wasmer"
        os      = "archlinux"
      }

      connect {
        sidecar_service {
          proxy {
            upstreams {
              destination_name = "manifolded-core"
              local_bind_port  = 50051
            }
          }
        }
      }

      check {
        type     = "grpc"
        interval = "15s"
        timeout  = "5s"
      }
    }

    vault {
      policies = ["vimmacs-runtime"]
      change_mode = "restart"
    }

    task "wasm-runtime" {
      driver = "docker"

      config {
        image = var.image_vimmacs

        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "http", "metrics"]

        # Privileged for Kata containers
        privileged = true

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/cache"
          source = "vimmacs-cache"
        }

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/sessions"
          source = "vimmacs-sessions"
        }

        # WASM runtime capabilities
        cap_add = ["SYS_ADMIN", "NET_ADMIN"]
      }

      template {
        data = <<-EOH
        {{- with secret "secret/manifolded/security" -}}
        KATA_INTEGRATION={{ .Data.data.kata_enabled }}
        {{- end -}}
        EOH
        destination = "secrets/vimmacs.env"
        env         = true
      }

      resources {
        cpu    = 1500
        memory = 3072
      }

      env {
        WASMER_CACHE_DIR = "/var/lib/vimmacs/cache"
        WASM_MAX_MEMORY = "4294967296"
        WASM_STACK_SIZE = "8388608"
        WASM_COMPILER = "cranelift"
        WASM_ARCH_ROOT = "/opt/vimmacs/arch-root"
      }
    }

    update {
      max_parallel     = 1
      min_healthy_time = "45s"
      healthy_deadline = "10m"
      canary           = 1
      auto_revert      = true
    }
  }

  # Security Services Group
  group "security" {
    count = 2

    network {
      mode = "bridge"

      port "https" {
        static = 8443
      }

      port "metrics" {
        static = 9092
      }
    }

    service {
      name = "manifolded-security"
      port = "https"
      tags = ["security", "scanner", "compliance"]

      connect {
        sidecar_service {}
      }

      check {
        type     = "https"
        path     = "/health"
        interval = "30s"
        timeout  = "10s"
        tls_skip_verify = true
      }
    }

    vault {
      policies = ["manifolded-core"]
      change_mode = "restart"
    }

    task "security-scanner" {
      driver = "docker"

      config {
        image = var.image_security

        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["https", "metrics"]

        mount {
          type   = "volume"
          target = "/var/lib/security/reports"
          source = "security-reports"
        }
      }

      resources {
        cpu    = 500
        memory = 1024
      }

      env {
        SECURITY_SCAN_INTERVAL = "300"
        COMPLIANCE_MODE = "strict"
        REPORT_LEVEL = "detailed"
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }

  # Load Balancer and Ingress Group
  group "ingress" {
    count = 2

    network {
      mode = "bridge"

      port "http" {
        static = 80
        to     = 8080
      }

      port "https" {
        static = 443
        to     = 8443
      }

      port "admin" {
        static = 8080
        to     = 8080
      }
    }

    service {
      name = "manifolded-ingress"
      port = "https"
      tags = ["ingress", "traefik", "loadbalancer"]

      check {
        type     = "http"
        path     = "/ping"
        port     = "admin"
        interval = "10s"
        timeout  = "3s"
      }
    }

    task "traefik" {
      driver = "docker"

      config {
        image = "traefik:v3.0"
        ports = ["http", "https", "admin"]

        args = [
          "--api.dashboard=true",
          "--api.insecure=false",
          "--entrypoints.web.address=:8080",
          "--entrypoints.websecure.address=:8443",
          "--providers.consul.endpoints=${CONSUL_HTTP_ADDR}",
          "--providers.consulcatalog.prefix=traefik",
          "--providers.consulcatalog.exposedbydefault=false",
          "--certificatesresolvers.letsencrypt.acme.tlschallenge=true",
          "--certificatesresolvers.letsencrypt.acme.email=admin@manifolded.com",
          "--certificatesresolvers.letsencrypt.acme.storage=/data/acme.json",
          "--log.level=INFO",
          "--accesslog=true",
          "--metrics.prometheus=true"
        ]

        mount {
          type   = "volume"
          target = "/data"
          source = "traefik-data"
        }
      }

      resources {
        cpu    = 500
        memory = 512
      }

      env {
        CONSUL_HTTP_ADDR = "${CONSUL_HTTP_ADDR}"
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }

  # Monitoring Group
  group "monitoring" {
    count = 1

    network {
      mode = "bridge"

      port "prometheus" {
        static = 9090
      }

      port "grafana" {
        static = 3000
      }
    }

    service {
      name = "prometheus"
      port = "prometheus"
      tags = ["monitoring", "metrics"]

      check {
        type     = "http"
        path     = "/-/healthy"
        interval = "30s"
        timeout  = "10s"
      }
    }

    service {
      name = "grafana"
      port = "grafana"
      tags = ["monitoring", "dashboard"]

      check {
        type     = "http"
        path     = "/api/health"
        interval = "30s"
        timeout  = "10s"
      }
    }

    task "prometheus" {
      driver = "docker"

      config {
        image = "prom/prometheus:latest"
        ports = ["prometheus"]

        args = [
          "--config.file=/etc/prometheus/prometheus.yml",
          "--storage.tsdb.path=/prometheus",
          "--web.console.libraries=/etc/prometheus/console_libraries",
          "--web.console.templates=/etc/prometheus/consoles",
          "--web.enable-lifecycle",
          "--web.enable-admin-api"
        ]

        mount {
          type   = "volume"
          target = "/prometheus"
          source = "prometheus-data"
        }
      }

      template {
        data = <<-EOH
global:
  scrape_interval: 15s
  evaluation_interval: 15s

rule_files:
  - "/etc/prometheus/alerts/*.yml"

scrape_configs:
  - job_name: 'manifolded-core'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['manifolded-metrics']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'vimmacs-runtime'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['vimmacs-runtime']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'security-scanner'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['manifolded-security']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'nomad'
    static_configs:
      - targets: ['{{ env "NOMAD_ADDR" | replaceAll "https://" "" }}']

  - job_name: 'consul'
    static_configs:
      - targets: ['{{ env "CONSUL_HTTP_ADDR" | replaceAll "https://" "" | replaceAll "http://" "" }}']

alerting:
  alertmanagers:
    - consul_sd_configs:
        - server: '{{ env "CONSUL_HTTP_ADDR" }}'
          services: ['alertmanager']
EOH
        destination = "local/prometheus.yml"
        change_mode = "restart"
      }

      resources {
        cpu    = 1000
        memory = 2048
      }
    }

    task "grafana" {
      driver = "docker"

      config {
        image = "grafana/grafana:latest"
        ports = ["grafana"]

        mount {
          type   = "volume"
          target = "/var/lib/grafana"
          source = "grafana-data"
        }
      }

      env {
        GF_SECURITY_ADMIN_PASSWORD = "admin"
        GF_INSTALL_PLUGINS = "grafana-piechart-panel,grafana-clock-panel"
      }

      resources {
        cpu    = 500
        memory = 1024
      }
    }
  }
}

toolUseResult.content:
# Manifolded Production Deployment
# Production-split architecture with 8-layer sandboxing

variable "image_core" {
  description = "Manifold Core private container image"
  type        = string
}

variable "image_security" {
  description = "Manifold Security scanner container image"
  type        = string
}

variable "image_sandbox" {
  description = "Manifold WebAssembly sandbox container image"
  type        = string
}

variable "image_vimmacs" {
  description = "Vimmacs WebAssembly runtime container image"
  type        = string
}

variable "image_cli" {
  description = "Manifold CLI public container image"
  type        = string
}

variable "datacenter" {
  description = "Target datacenter for deployment"
  type        = string
  default     = "dc1"
}

variable "environment" {
  description = "Deployment environment"
  type        = string
  default     = "production"
}

variable "registry_ovh" {
  description = "OVH private registry"
  type        = string
  default     = "registry.ovh.net"
}

variable "registry_public" {
  description = "Public registry (Docker Hub)"
  type        = string
  default     = "docker.io"
}

job "manifolded-production" {
  datacenters = [var.datacenter]
  type        = "service"
  priority    = 80

  # Constraint to ensure deployment on Linux nodes
  constraint {
    attribute = "${attr.kernel.name}"
    value     = "linux"
  }

  # Constraint to ensure nodes have adequate resources for sandboxing
  constraint {
    attribute = "${attr.cpu.totalcompute}"
    operator  = ">="
    value     = "4000"
  }

  # Manifold Core Service (8-Layer Sandboxing)
  group "core" {
    count = 3

    # Spread across different nodes for high availability
    spread {
      attribute = "${node.unique.id}"
      weight    = 100
    }

    network {
      mode = "bridge"

      port "grpc" {
        static = 50051
      }

      port "metrics" {
        static = 9090
      }

      port "health" {
        static = 8080
      }
    }

    # Consul Connect service mesh
    service {
      name = "manifolded-core"
      port = "grpc"
      tags = ["production", "grpc", "8-layer-sandbox", "quantum"]

      meta {
        version     = "1.0.0"
        environment = var.environment
        security    = "maximum"
        layers      = "8"
      }

      connect {
        sidecar_service {
          proxy {
            upstreams {
              destination_name = "vimmacs-runtime"
              local_bind_port  = 50052
            }

            upstreams {
              destination_name = "manifolded-security"
              local_bind_port  = 8443
            }
          }
        }
      }

      check {
        type     = "grpc"
        interval = "10s"
        timeout  = "3s"
      }
    }

    # Metrics service for Prometheus
    service {
      name = "manifolded-metrics"
      port = "metrics"
      tags = ["metrics", "prometheus"]

      check {
        type     = "http"
        path     = "/metrics"
        interval = "15s"
        timeout  = "5s"
      }
    }

    # Vault integration for secrets
    vault {
      policies = ["manifolded-core"]
      change_mode = "restart"
    }

    task "core-engine" {
      driver = "docker"

      config {
        image = var.image_core

        # Authentication for private registry
        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "metrics", "health"]

        # Enable privileged mode for 8-layer sandboxing
        privileged = true

        # Mount volumes for container storage and security
        mount {
          type   = "volume"
          target = "/var/lib/manifold/containers"
          source = "manifold-containers"
          volume_options {
            no_copy = false
            driver_config {
              name = "local"
              options {
                type   = "ext4"
                device = "/dev/manifold-storage"
              }
            }
          }
        }

        mount {
          type   = "volume"
          target = "/var/lib/manifold/layers"
          source = "manifold-layers"
        }

        # Security configurations
        cap_add = ["SYS_ADMIN", "NET_ADMIN", "SYS_CHROOT"]

        security_opt = [
          "apparmor:manifold-profile",
          "seccomp:/opt/manifold/config/seccomp/manifold.json"
        ]

        # Logging configuration
        logging {
          type = "json-file"
          config {
            max-size = "10m"
            max-file = "5"
            labels   = "service,version,environment"
          }
        }
      }

      # Environment variables from Vault
      template {
        data = <<-EOH
        {{- with secret "secret/manifolded/production" -}}
        DATABASE_URL={{ .Data.data.database_url }}
        REDIS_URL={{ .Data.data.redis_url }}
        S3_BUCKET={{ .Data.data.s3_bucket }}
        PHOENIX_SECRET_KEY_BASE={{ .Data.data.phoenix_secret }}
        WASM_SIGNING_KEY={{ .Data.data.wasm_signing_key }}
        {{- end -}}

        {{- with secret "secret/manifolded/security" -}}
        MANIFOLD_SANDBOX_LAYERS={{ .Data.data.sandbox_layers }}
        MANIFOLD_ISOLATION_LEVEL={{ .Data.data.isolation_level }}
        KATA_ENABLED={{ .Data.data.kata_enabled }}
        QUANTUM_WASM_ENABLED={{ .Data.data.quantum_wasm_enabled }}
        AIR_GAP_ENABLED={{ .Data.data.air_gap_enabled }}
        {{- end -}}
        EOH
        destination = "secrets/app.env"
        env         = true
      }

      resources {
        cpu    = 2000
        memory = 4096
        device "nvidia/gpu" {
          count = 1
          constraint {
            attribute = "${device.attr.compute_capability}"
            operator  = ">="
            value     = "6.0"
          }
        }
      }

      env {
        MANIFOLD_ENVIRONMENT = var.environment
        MANIFOLD_DATACENTER  = var.datacenter
        RUST_LOG = "info"
        RUST_BACKTRACE = "1"
        NOMAD_ALLOC_ID = "${NOMAD_ALLOC_ID}"
        NOMAD_TASK_NAME = "${NOMAD_TASK_NAME}"
      }
    }

    # Restart and update policies
    restart {
      attempts = 3
      interval = "5m"
      delay    = "15s"
      mode     = "delay"
    }

    reschedule {
      attempts       = 5
      interval       = "1h"
      delay          = "30s"
      delay_function = "exponential"
      max_delay      = "10m"
      unlimited      = false
    }

    update {
      max_parallel      = 1
      min_healthy_time  = "30s"
      healthy_deadline  = "5m"
      progress_deadline = "10m"
      canary            = 1
      auto_revert       = true
      auto_promote      = true
    }
  }

  # Vimmacs WebAssembly Runtime Group
  group "vimmacs" {
    count = 2

    network {
      mode = "bridge"

      port "grpc" {
        static = 50052
      }

      port "http" {
        static = 8081
      }

      port "metrics" {
        static = 9091
      }
    }

    service {
      name = "vimmacs-runtime"
      port = "grpc"
      tags = ["wasm", "archlinux", "kata", "runtime"]

      meta {
        version = "1.0.0"
        runtime = "wasmer"
        os      = "archlinux"
      }

      connect {
        sidecar_service {
          proxy {
            upstreams {
              destination_name = "manifolded-core"
              local_bind_port  = 50051
            }
          }
        }
      }

      check {
        type     = "grpc"
        interval = "15s"
        timeout  = "5s"
      }
    }

    vault {
      policies = ["vimmacs-runtime"]
      change_mode = "restart"
    }

    task "wasm-runtime" {
      driver = "docker"

      config {
        image = var.image_vimmacs

        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["grpc", "http", "metrics"]

        # Privileged for Kata containers
        privileged = true

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/cache"
          source = "vimmacs-cache"
        }

        mount {
          type   = "volume"
          target = "/var/lib/vimmacs/sessions"
          source = "vimmacs-sessions"
        }

        # WASM runtime capabilities
        cap_add = ["SYS_ADMIN", "NET_ADMIN"]
      }

      template {
        data = <<-EOH
        {{- with secret "secret/manifolded/security" -}}
        KATA_INTEGRATION={{ .Data.data.kata_enabled }}
        {{- end -}}
        EOH
        destination = "secrets/vimmacs.env"
        env         = true
      }

      resources {
        cpu    = 1500
        memory = 3072
      }

      env {
        WASMER_CACHE_DIR = "/var/lib/vimmacs/cache"
        WASM_MAX_MEMORY = "4294967296"
        WASM_STACK_SIZE = "8388608"
        WASM_COMPILER = "cranelift"
        WASM_ARCH_ROOT = "/opt/vimmacs/arch-root"
      }
    }

    update {
      max_parallel     = 1
      min_healthy_time = "45s"
      healthy_deadline = "10m"
      canary           = 1
      auto_revert      = true
    }
  }

  # Security Services Group
  group "security" {
    count = 2

    network {
      mode = "bridge"

      port "https" {
        static = 8443
      }

      port "metrics" {
        static = 9092
      }
    }

    service {
      name = "manifolded-security"
      port = "https"
      tags = ["security", "scanner", "compliance"]

      connect {
        sidecar_service {}
      }

      check {
        type     = "https"
        path     = "/health"
        interval = "30s"
        timeout  = "10s"
        tls_skip_verify = true
      }
    }

    vault {
      policies = ["manifolded-core"]
      change_mode = "restart"
    }

    task "security-scanner" {
      driver = "docker"

      config {
        image = var.image_security

        auth {
          username = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_USERNAME}"
          password = "${VAULT_KV_MANIFOLDED_REGISTRY_OVH_PASSWORD}"
        }

        ports = ["https", "metrics"]

        mount {
          type   = "volume"
          target = "/var/lib/security/reports"
          source = "security-reports"
        }
      }

      resources {
        cpu    = 500
        memory = 1024
      }

      env {
        SECURITY_SCAN_INTERVAL = "300"
        COMPLIANCE_MODE = "strict"
        REPORT_LEVEL = "detailed"
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }

  # Load Balancer and Ingress Group
  group "ingress" {
    count = 2

    network {
      mode = "bridge"

      port "http" {
        static = 80
        to     = 8080
      }

      port "https" {
        static = 443
        to     = 8443
      }

      port "admin" {
        static = 8080
        to     = 8080
      }
    }

    service {
      name = "manifolded-ingress"
      port = "https"
      tags = ["ingress", "traefik", "loadbalancer"]

      check {
        type     = "http"
        path     = "/ping"
        port     = "admin"
        interval = "10s"
        timeout  = "3s"
      }
    }

    task "traefik" {
      driver = "docker"

      config {
        image = "traefik:v3.0"
        ports = ["http", "https", "admin"]

        args = [
          "--api.dashboard=true",
          "--api.insecure=false",
          "--entrypoints.web.address=:8080",
          "--entrypoints.websecure.address=:8443",
          "--providers.consul.endpoints=${CONSUL_HTTP_ADDR}",
          "--providers.consulcatalog.prefix=traefik",
          "--providers.consulcatalog.exposedbydefault=false",
          "--certificatesresolvers.letsencrypt.acme.tlschallenge=true",
          "--certificatesresolvers.letsencrypt.acme.email=admin@manifolded.com",
          "--certificatesresolvers.letsencrypt.acme.storage=/data/acme.json",
          "--log.level=INFO",
          "--accesslog=true",
          "--metrics.prometheus=true"
        ]

        mount {
          type   = "volume"
          target = "/data"
          source = "traefik-data"
        }
      }

      resources {
        cpu    = 500
        memory = 512
      }

      env {
        CONSUL_HTTP_ADDR = "${CONSUL_HTTP_ADDR}"
      }
    }

    update {
      max_parallel = 1
      canary       = 1
      auto_revert  = true
    }
  }

  # Monitoring Group
  group "monitoring" {
    count = 1

    network {
      mode = "bridge"

      port "prometheus" {
        static = 9090
      }

      port "grafana" {
        static = 3000
      }
    }

    service {
      name = "prometheus"
      port = "prometheus"
      tags = ["monitoring", "metrics"]

      check {
        type     = "http"
        path     = "/-/healthy"
        interval = "30s"
        timeout  = "10s"
      }
    }

    service {
      name = "grafana"
      port = "grafana"
      tags = ["monitoring", "dashboard"]

      check {
        type     = "http"
        path     = "/api/health"
        interval = "30s"
        timeout  = "10s"
      }
    }

    task "prometheus" {
      driver = "docker"

      config {
        image = "prom/prometheus:latest"
        ports = ["prometheus"]

        args = [
          "--config.file=/etc/prometheus/prometheus.yml",
          "--storage.tsdb.path=/prometheus",
          "--web.console.libraries=/etc/prometheus/console_libraries",
          "--web.console.templates=/etc/prometheus/consoles",
          "--web.enable-lifecycle",
          "--web.enable-admin-api"
        ]

        mount {
          type   = "volume"
          target = "/prometheus"
          source = "prometheus-data"
        }
      }

      template {
        data = <<-EOH
global:
  scrape_interval: 15s
  evaluation_interval: 15s

rule_files:
  - "/etc/prometheus/alerts/*.yml"

scrape_configs:
  - job_name: 'manifolded-core'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['manifolded-metrics']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'vimmacs-runtime'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['vimmacs-runtime']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'security-scanner'
    consul_sd_configs:
      - server: '{{ env "CONSUL_HTTP_ADDR" }}'
        services: ['manifolded-security']
    relabel_configs:
      - source_labels: [__meta_consul_service]
        target_label: job

  - job_name: 'nomad'
    static_configs:
      - targets: ['{{ env "NOMAD_ADDR" | replaceAll "https://" "" }}']

  - job_name: 'consul'
    static_configs:
      - targets: ['{{ env "CONSUL_HTTP_ADDR" | replaceAll "https://" "" | replaceAll "http://" "" }}']

alerting:
  alertmanagers:
    - consul_sd_configs:
        - server: '{{ env "CONSUL_HTTP_ADDR" }}'
          services: ['alertmanager']
EOH
        destination = "local/prometheus.yml"
        change_mode = "restart"
      }

      resources {
        cpu    = 1000
        memory = 2048
      }
    }

    task "grafana" {
      driver = "docker"

      config {
        image = "grafana/grafana:latest"
        ports = ["grafana"]

        mount {
          type   = "volume"
          target = "/var/lib/grafana"
          source = "grafana-data"
        }
      }

      env {
        GF_SECURITY_ADMIN_PASSWORD = "admin"
        GF_INSTALL_PLUGINS = "grafana-piechart-panel,grafana-clock-panel"
      }

      resources {
        cpu    = 500
        memory = 1024
      }
    }
  }
}

