{
  "title": "AutonogrammerOS Documentation",
  "description": "High-Performance Quantum Computing Framework for Frontier Terminal AI",
  "author": "AutonogrammerOS Contributors",
  "language": "en",
  "root": "./docs",
  "structure": {
    "readme": "README.md",
    "summary": "SUMMARY.md"
  },
  "plugins": [
    "theme-default",
    "search",
    "code",
    "expandable-chapters",
    "copy-code-button",
    "back-to-top-button",
    "page-toc",
    "anchors",
    "prism",
    "-highlight",
    "mermaid-gb3",
    "todo",
    "terminal",
    "include-codeblock"
  ],
  "pluginsConfig": {
    "theme-default": {
      "showLevel": true,
      "styles": {
        "website": "styles/website.css"
      }
    },
    "prism": {
      "css": [
        "prismjs/themes/prism-tomorrow.css"
      ],
      "lang": {
        "flow": "typescript"
      }
    },
    "page-toc": {
      "selector": ".markdown-section h1, .markdown-section h2, .markdown-section h3",
      "position": "before-first",
      "showByDefault": true
    }
  },
  "variables": {
    "version": "3.0.0-quantum",
    "github": "https://github.com/yourusername/autonogrammer-os",
    "website": "https://autonogrammer.ai"
  },
  "pdf": {
    "pageNumbers": true,
    "fontSize": 12,
    "fontFamily": "Arial",
    "paperSize": "a4",
    "margin": {
      "right": 62,
      "left": 62,
      "top": 56,
      "bottom": 56
    }
  }
}

