  label="Email"
  type="email"
  placeholder="Enter your email"
  value={email}
  onChange={(e) => setEmail(e.target.value)}
/>

// With validation
<Input
  label="Password"
  type="password"
  required
  minLength={8}
  pattern="^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$"
  error="Password must be at least 8 characters"
  helperText="Use 8+ characters with letters and numbers"
/>

// With addons
