  <Tabs.List>
    <Tabs.Tab value="home" icon={<IconHome />}>
      Home
    </Tabs.Tab>
    <Tabs.Tab value="profile" icon={<IconUser />}>
      Profile
    </Tabs.Tab>
  </Tabs.List>
</Tabs>

// Variants
