```typescript
import { Embeddings } from '@katalyst/ai';

const embeddings = new Embeddings();

// Generate embeddings
const vectors = await embeddings.create({
  model: 'bge-m3',
  texts: ['Document 1', 'Document 2'],
  normalize: true
});

// Semantic search
const results = await embeddings.search({
  model: 'bge-m3',
  query: 'user query',
  documents: documentArray,
  topK: 10
});

// Rerank results
const reranked = await embeddings.rerank({
  model: 'bge-reranker-v2-m3',
