import { Moderation } from '@katalyst/ai';

const moderation = new Moderation();

// Check content safety
const safety = await moderation.check({
  model: 'llama-guard-3-11b',
  content: userContent,
  categories: ['violence', 'hate', 'self-harm']
});

// Moderate conversation
const moderated = await moderation.conversation({
  model: 'llama-guard-3-11b',
  messages: chatHistory,
