### Database Operations

```typescript
import { D1Database } from '@katalyst/cloudflare';

export class ProductService {
  constructor(private db: D1Database) {}

  async createProduct(product: Product) {
    const result = await this.db
      .prepare('INSERT INTO products (name, price, description, stock) VALUES (?, ?, ?, ?)')
      .bind(product.name, product.price, product.description, product.stock)
      .run();

    return result.meta.last_row_id;
  }

  async getProducts(limit = 10, offset = 0) {
    const { results } = await this.db
      .prepare('SELECT * FROM products LIMIT ? OFFSET ?')
      .bind(limit, offset)
      .all();

    return results;
  }

  async updateStock(productId: number, quantity: number) {
    await this.db
      .prepare('UPDATE products SET stock = stock + ? WHERE id = ?')
      .bind(quantity, productId)
      .run();
  }

  // Batch operations
  async batchInsert(products: Product[]) {
    const statements = products.map(p =>
      this.db
        .prepare('INSERT INTO products (name, price) VALUES (?, ?)')
        .bind(p.name, p.price)
    );

    const results = await this.db.batch(statements);
    return results;
  }
}
```

### Transactions

```typescript
async function transferStock(db: D1Database, from: number, to: number, quantity: number) {
  const tx = await db.transaction(async (txn) => {
    // Decrease stock from source
    await txn
      .prepare('UPDATE products SET stock = stock - ? WHERE id = ? AND stock >= ?')
      .bind(quantity, from, quantity)
      .run();

    // Increase stock in destination
    await txn
      .prepare('UPDATE products SET stock = stock + ? WHERE id = ?')
      .bind(quantity, to)
      .run();

    // Log transaction
    await txn
      .prepare('INSERT INTO stock_transfers (from_id, to_id, quantity) VALUES (?, ?, ?)')
      .bind(from, to, quantity)
      .run();
  });

  return tx;
}
