## Vectorize Integration

### Vector Search Setup

```typescript
import { Vectorize } from '@katalyst/cloudflare';

class VectorSearchService {
  constructor(
    private vectorize: Vectorize,
    private ai: any
  ) {}

  // Index documents
  async indexDocuments(documents: Document[]) {
    const vectors = [];

    for (const doc of documents) {
      // Generate embeddings using AI
      const embedding = await this.ai.run(
        '@cf/baai/bge-m3',
        { text: doc.content }
      );

      vectors.push({
        id: doc.id,
        values: embedding.data[0],
        metadata: {
          title: doc.title,
          category: doc.category,
          timestamp: Date.now()
        }
      });
    }

    // Insert vectors into Vectorize
    await this.vectorize.insert(vectors);
  }

  // Semantic search
  async search(query: string, topK = 10) {
    // Generate query embedding
    const queryEmbedding = await this.ai.run(
      '@cf/baai/bge-m3',
      { text: query }
    );

    // Search for similar vectors
    const results = await this.vectorize.query(
      queryEmbedding.data[0],
      {
        topK,
        filter: {
          category: { $in: ['docs', 'articles'] }
        },
        includeMetadata: true
      }
    );

    return results.matches;
  }

  // Hybrid search (vector + keyword)
  async hybridSearch(query: string, keywords: string[]) {
    // Vector search
    const vectorResults = await this.search(query, 20);

    // Filter by keywords
    const filtered = vectorResults.filter(result =>
      keywords.some(keyword =>
        result.metadata.title.toLowerCase().includes(keyword.toLowerCase())
      )
    );

    return filtered.slice(0, 10);
  }

  // Update vector
  async updateVector(id: string, content: string, metadata: any) {
    const embedding = await this.ai.run(
      '@cf/baai/bge-m3',
      { text: content }
    );

    await this.vectorize.upsert([{
      id,
      values: embedding.data[0],
      metadata
    }]);
  }

  // Delete vectors
  async deleteVectors(ids: string[]) {
    await this.vectorize.delete(ids);
  }
}
