### Consumer

```typescript
export default {
  async queue(batch: MessageBatch<QueueMessage>, env: Env) {
    for (const message of batch.messages) {
      try {
        await processMessage(message.body, env);
        message.ack();
      } catch (error) {
        console.error('Failed to process message:', error);
        message.retry();
      }
    }
  }
};

async function processMessage(message: QueueMessage, env: Env) {
  switch (message.type) {
    case 'email':
      await sendEmail(message.payload);
      break;
    case 'notification':
      await sendNotification(message.payload);
      break;
    case 'analytics':
      await trackAnalytics(message.payload, env);
      break;
  }
}
