  async pageView(url: string, userId?: string) {
    await this.analytics.writeDataPoint({
      blobs: ['pageview', url],
      doubles: [Date.now()],
      indexes: [userId || 'anonymous']
    });
  }

  async error(error: Error, context: any = {}) {
    await this.analytics.writeDataPoint({
      blobs: [
        'error',
        error.message,
        error.stack || '',
        JSON.stringify(context)
      ],
      doubles: [Date.now()],
      indexes: [context.userId || 'anonymous']
    });
  }

  async performance(metric: string, value: number) {
    await this.analytics.writeDataPoint({
      blobs: ['performance', metric],
      doubles: [Date.now(), value],
      indexes: ['system']
    });
  }
}
