## R2 Storage Integration

### File Upload

```typescript
import { R2Bucket } from '@katalyst/cloudflare';

export async function handleUpload(request: Request, env: Env) {
  const formData = await request.formData();
  const file = formData.get('file') as File;

  if (!file) {
    return Response.json({ error: 'No file provided' }, { status: 400 });
  }

  const buffer = await file.arrayBuffer();
  const key = `uploads/${Date.now()}-${file.name}`;

  // Upload to R2
  await env.STORAGE.put(key, buffer, {
    metadata: {
      contentType: file.type,
      uploadedBy: request.headers.get('user-id'),
      originalName: file.name
    },
    httpMetadata: {
      contentType: file.type,
      cacheControl: 'public, max-age=3600'
    }
  });

  return Response.json({
    success: true,
    key,
    url: `https://storage.katalyst.dev/${key}`
  });
}
