  const key = url.searchParams.get('key');
  const width = parseInt(url.searchParams.get('width') || '0');
  const height = parseInt(url.searchParams.get('height') || '0');

  // Get original image from R2
  const object = await env.STORAGE.get(key);
  if (!object) {
    return new Response('Not found', { status: 404 });
  }

  // Apply transformations using Cloudflare Image Resizing
  const resized = await fetch(`https://images.katalyst.dev/${key}`, {
