          },
          {
            role: 'user',
            content: `Context:\n${documents.join('\n\n')}\n\nQuestion: ${question}`
          }
        ]
      }
    );

    return {
      answer: response.response,
      sources: searchResults.map(r => ({
        id: r.id,
        title: r.metadata.title,
        score: r.score
      }))
    };
  }
}
```

## Durable Objects Integration

### Durable Object Class

```typescript
import { DurableObject } from '@katalyst/cloudflare';

export class RoomManager extends DurableObject {
  private connections: Map<string, WebSocket> = new Map();
  private messages: Message[] = [];

  async fetch(request: Request) {
    const url = new URL(request.url);

    switch (url.pathname) {
      case '/websocket':
        return this.handleWebSocket(request);
      case '/messages':
        return this.getMessages();
      case '/broadcast':
        return this.broadcast(request);
      default:
        return new Response('Not found', { status: 404 });
    }
  }

  async handleWebSocket(request: Request) {
    const pair = new WebSocketPair();
    const [client, server] = Object.values(pair);

    await this.handleSession(server, request);

    return new Response(null, {
      status: 101,
      webSocket: client
    });
  }

  async handleSession(webSocket: WebSocket, request: Request) {
    const id = crypto.randomUUID();
    this.connections.set(id, webSocket);

    webSocket.accept();

    webSocket.addEventListener('message', async (event) => {
      const message = JSON.parse(event.data as string);
      await this.handleMessage(id, message);
    });

    webSocket.addEventListener('close', () => {
      this.connections.delete(id);
    });
  }

  async handleMessage(senderId: string, message: any) {
    // Store message
    this.messages.push({
      id: crypto.randomUUID(),
      senderId,
      content: message.content,
      timestamp: Date.now()
    });

    // Broadcast to all connections
    for (const [id, ws] of this.connections) {
      if (id !== senderId && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: 'message',
          senderId,
          content: message.content,
          timestamp: Date.now()
        }));
      }
    }
  }

  async getMessages() {
    return Response.json(this.messages);
  }

  async broadcast(request: Request) {
    const { message } = await request.json();

    for (const ws of this.connections.values()) {
      if (ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: 'broadcast',
          content: message,
          timestamp: Date.now()
        }));
      }
    }

    return Response.json({ success: true });
  }
}
```

