  // Create basic project structure
  const dirs = [
    'src',
    'src/components',
    'src/pages',
    'src/hooks',
    'src/utils',
    'src/styles',
    'public'
  ];

  for (const dir of dirs) {
    await fs.ensureDir(path.join(projectPath, dir));
