  profile: '${profileName}',
  features: {
    ai: ${profile.packages.includes('@katalyst/ai')},
    pwa: ${profile.packages.includes('@katalyst/pwa')},
    analytics: ${profile.packages.includes('@katalyst/analytics')},
    payments: ${profile.packages.includes('@katalyst/payments')}
