      content += `##  Bug Fixes\n\n`;
      for (const item of entry.changes.fixed) {
        content += `- ${item}\n`;
      }
      content += '\n';
    }

    if (entry.breaking && entry.breaking.length > 0) {
      content += `##  Breaking Changes\n\n`;
      for (const breaking of entry.breaking) {
