      console.log(`- Generated ${docs.length} documentation files`);
      console.log(`- Created ${entries.filter(e => e.breaking && e.breaking.length > 0).length} migration guides`);

    } catch (error) {
      console.error(chalk.red(' Error:'), error);
      process.exit(1);
    }
  }
