/**
 * Katalyst Design System - Design Tokens
 * 
 * A comprehensive token system for consistent theming across all components.
 * Based on Ant Design 5.0 token system with Radix UI color scales.
 */

import * as colors from '@radix-ui/colors';

// ============================================================================
// Core Token Types
// ============================================================================

export interface ColorTokens {
  // Primary Colors
  primary: string;
  primaryHover: string;
  primaryActive: string;
  primaryBg: string;
  primaryBgHover: string;
  primaryBorder: string;
  primaryBorderHover: string;
  primaryText: string;
  primaryTextHover: string;
  primaryTextActive: string;

  // Success Colors
  success: string;
  successHover: string;
  successActive: string;
  successBg: string;
  successBgHover: string;
  successBorder: string;
  successBorderHover: string;
  successText: string;
  successTextHover: string;
  successTextActive: string;

  // Warning Colors
  warning: string;
  warningHover: string;
  warningActive: string;
  warningBg: string;
  warningBgHover: string;
  warningBorder: string;
  warningBorderHover: string;
  warningText: string;
  warningTextHover: string;
  warningTextActive: string;

  // Error Colors
  error: string;
  errorHover: string;
  errorActive: string;
  errorBg: string;
  errorBgHover: string;
  errorBorder: string;
  errorBorderHover: string;
  errorText: string;
  errorTextHover: string;
  errorTextActive: string;

  // Info Colors
  info: string;
  infoHover: string;
  infoActive: string;
  infoBg: string;
  infoBgHover: string;
  infoBorder: string;
  infoBorderHover: string;
  infoText: string;
  infoTextHover: string;
  infoTextActive: string;

  // Neutral Colors
  text: string;
  textSecondary: string;
  textTertiary: string;
  textQuaternary: string;
  textDisabled: string;
  textPlaceholder: string;

  bg: string;
  bgContainer: string;
  bgElevated: string;
  bgSpotlight: string;
  bgMask: string;

  border: string;
  borderSecondary: string;
  divider: string;

  // Semantic Colors
  link: string;
  linkHover: string;
  linkActive: string;
  linkVisited: string;

  highlight: string;
  highlightBg: string;
}

export interface SizeTokens {
  // Font Sizes
  fontSizeXS: number;
  fontSizeSM: number;
  fontSize: number;
  fontSizeLG: number;
  fontSizeXL: number;
  fontSizeXXL: number;
  fontSizeXXXL: number;

  // Font Weights
  fontWeightLight: number;
  fontWeightRegular: number;
  fontWeightMedium: number;
  fontWeightSemibold: number;
  fontWeightBold: number;

  // Line Heights
  lineHeightXS: number;
  lineHeightSM: number;
  lineHeight: number;
  lineHeightLG: number;
  lineHeightXL: number;
  lineHeightXXL: number;

  // Heading Line Heights
  lineHeightHeading1: number;
  lineHeightHeading2: number;
  lineHeightHeading3: number;
  lineHeightHeading4: number;
  lineHeightHeading5: number;
}

export interface SpacingTokens {
  // Margin/Padding Scale
  marginXXS: number;
  marginXS: number;
  marginSM: number;
  margin: number;
  marginMD: number;
  marginLG: number;
  marginXL: number;
  marginXXL: number;

  paddingXXS: number;
  paddingXS: number;
  paddingSM: number;
  padding: number;
  paddingMD: number;
  paddingLG: number;
  paddingXL: number;
  paddingXXL: number;

  // Component Specific
  paddingContentHorizontalLG: number;
  paddingContentHorizontal: number;
  paddingContentHorizontalSM: number;
  paddingContentVerticalLG: number;
  paddingContentVertical: number;
  paddingContentVerticalSM: number;
}

export interface MotionTokens {
  // Duration
  motionDurationFast: string;
  motionDurationMid: string;
  motionDurationSlow: string;

  // Easing
  motionEaseInOut: string;
  motionEaseOut: string;
  motionEaseIn: string;
  motionEaseInBack: string;
  motionEaseOutBack: string;
  motionEaseInOutCirc: string;
  motionEaseOutCirc: string;
}

export interface RadiusTokens {
  borderRadiusXS: number;
  borderRadiusSM: number;
  borderRadius: number;
  borderRadiusLG: number;
  borderRadiusXL: number;
  borderRadiusOuter: number;
}

export interface ShadowTokens {
  boxShadow: string;
  boxShadowSecondary: string;
  boxShadowTertiary: string;
  boxShadowPopover: string;
  boxShadowCard: string;
  boxShadowDrawerRight: string;
  boxShadowDrawerLeft: string;
  boxShadowDrawerUp: string;
  boxShadowDrawerDown: string;
  boxShadowTabsOverflowLeft: string;
  boxShadowTabsOverflowRight: string;
