// ============================================================================

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
  loading?: boolean;
  loadingText?: string;
  icon?: React.ReactNode;
  iconPosition?: 'left' | 'right';
  htmlType?: 'button' | 'submit' | 'reset';
  href?: string;
  target?: string;
  rel?: string;
  download?: boolean | string;
  shape?: 'default' | 'circle' | 'round';
  danger?: boolean;
  block?: boolean;
  ghost?: boolean;
}

// ============================================================================
// Button Component
// ============================================================================

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant,
      size,
      fullWidth,
      asChild = false,
      loading = false,
      loadingText,
      icon,
      iconPosition = 'left',
      htmlType = 'button',
      href,
      target,
      rel,
      download,
      shape = 'default',
      danger = false,
      block = false,
      ghost = false,
      children,
      disabled,
      onClick,
      ...props
    },
    ref
  ) => {
    const token = useToken();
    const Comp = asChild ? Slot : 'button';

    // Handle danger variant
    const actualVariant = danger ? 'destructive' : ghost ? 'ghost' : variant;

    // Handle block/fullWidth
    const isFullWidth = block || fullWidth;

    // Shape classes
    const shapeClasses = {
      default: '',
      circle: '!rounded-full aspect-square',
      round: '!rounded-full',
    };

    // Loading spinner
    const LoadingSpinner = () => (
      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
    );

    // Button content
    const buttonContent = (
      <>
        {loading && <LoadingSpinner />}
        {!loading && icon && iconPosition === 'left' && (
          <span className="mr-2">{icon}</span>
        )}
        <span className={cn(loading && !loadingText && 'opacity-0')}>
          {loading && loadingText ? loadingText : children}
        </span>
        {!loading && icon && iconPosition === 'right' && (
          <span className="ml-2">{icon}</span>
        )}
      </>
    );

    // If href is provided, render as anchor
    if (href && !disabled) {
      return (
        <a
          href={href}
          target={target}
          rel={rel}
          download={download}
          className={cn(
            buttonVariants({ 
              variant: actualVariant, 
              size, 
              fullWidth: isFullWidth,
              loading,
              className 
            }),
            shapeClasses[shape],
            className
          )}
          onClick={onClick as any}
          {...(props as any)}
        >
          {buttonContent}
        </a>
      );
    }

    return (
      <Comp
        className={cn(
          buttonVariants({ 
            variant: actualVariant, 
            size, 
            fullWidth: isFullWidth,
            loading,
            className 
          }),
          shapeClasses[shape],
          className
        )}
        ref={ref}
        disabled={disabled || loading}
        type={htmlType}
        onClick={onClick}
        {...props}
      >
        {buttonContent}
      </Comp>
    );
  }
);

Button.displayName = 'Button';

// ============================================================================
// Button Group Component
// ============================================================================

export interface ButtonGroupProps {
  children: React.ReactNode;
  size?: ButtonProps['size'];
  variant?: ButtonProps['variant'];
  vertical?: boolean;
  className?: string;
}

export const ButtonGroup = React.forwardRef<HTMLDivElement, ButtonGroupProps>(
  ({ children, size, variant, vertical = false, className, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          'inline-flex',
          vertical ? 'flex-col' : 'flex-row',
          '[&>*:not(:first-child)]:rounded-l-none',
          '[&>*:not(:last-child)]:rounded-r-none',
          '[&>*:not(:first-child)]:border-l-0',
          vertical && '[&>*:not(:first-child)]:rounded-t-none',
          vertical && '[&>*:not(:last-child)]:rounded-b-none',
          vertical && '[&>*:not(:first-child)]:border-t-0',
          vertical && '[&>*:not(:first-child)]:border-l',
          className
        )}
        {...props}
      >
        {React.Children.map(children, (child) => {
          if (React.isValidElement(child)) {
            return React.cloneElement(child as any, {
              size: size || (child.props as any).size,
              variant: variant || (child.props as any).variant,
            });
          }
          return child;
        })}
      </div>
    );
  }
);

ButtonGroup.displayName = 'ButtonGroup';

// ============================================================================
// Icon Button Component
// ============================================================================

export interface IconButtonProps extends Omit<ButtonProps, 'icon' | 'iconPosition'> {
  icon: React.ReactNode;
  'aria-label': string;
}

export const IconButton = React.forwardRef<HTMLButtonElement, IconButtonProps>(
  ({ icon, size = 'icon', className, ...props }, ref) => {
    return (
      <Button
        ref={ref}
        size={size as any}
        className={cn('p-0', className)}
        {...props}
      >
        {icon}
      </Button>
    );
  }
);

IconButton.displayName = 'IconButton';

// ============================================================================
// Split Button Component
// ============================================================================

export interface SplitButtonProps extends ButtonProps {
  menu: React.ReactNode;
  menuTrigger?: React.ReactNode;
}

export const SplitButton = React.forwardRef<HTMLButtonElement, SplitButtonProps>(
  ({ children, menu, menuTrigger, className, ...props }, ref) => {
    return (
      <ButtonGroup className={className}>
        <Button ref={ref} {...props}>
          {children}
        </Button>
        <Button {...props} className="px-2">
          {menuTrigger || ''}
        </Button>
      </ButtonGroup>
    );
  }
);

SplitButton.displayName = 'SplitButton';

// ============================================================================
// Exports
// ============================================================================

export { Button, buttonVariants };
export default Button;

