    const [internalValue, setInternalValue] = React.useState(defaultValue || '');
    const [showPassword, setShowPassword] = React.useState(false);
    const [isFocused, setIsFocused] = React.useState(false);

    const inputRef = React.useRef<HTMLInputElement>(null);
    React.useImperativeHandle(ref, () => inputRef.current!);

    const controlledValue = value !== undefined ? value : internalValue;
    const stringValue = String(controlledValue || '');

    // Determine status
    const status = error ? 'error' : success ? 'success' : warning ? 'warning' : statusProp;

    // Handle change
    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      if (value === undefined) {
        setInternalValue(e.target.value);
      }
      onChange?.(e);
    };

    // Handle clear
    const handleClear = () => {
      if (value === undefined) {
        setInternalValue('');
      }
      onClear?.();
      inputRef.current?.focus();

      // Trigger onChange with empty value
      const event = new Event('input', { bubbles: true });
      Object.defineProperty(event, 'target', { 
        writable: false, 
        value: { value: '' } 
      });
      onChange?.(event as any);
    };

    // Handle key down
    const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
      if (e.key === 'Enter') {
        onPressEnter?.(e);
      }
      onKeyDown?.(e);
    };

    // Toggle password visibility
    const togglePasswordVisibility = () => {
      setShowPassword(!showPassword);
    };

    // Calculate character count
    const characterCount = stringValue.length;
    const showCharacterCount = showCount && (maxLength || showCount !== true);

    // Format character count display
    const getCountDisplay = () => {
      if (!showCharacterCount) return null;

      if (typeof showCount === 'object' && showCount.formatter) {
        return showCount.formatter({ 
          value: stringValue, 
          count: characterCount, 
          maxLength 
        });
      }

      return maxLength ? `${characterCount}/${maxLength}` : characterCount;
    };

    // Determine if clear button should show
    const showClearButton = allowClear && stringValue && !disabled && !loading;

    // Input type handling for password
    const inputType = type === 'password' && showPassword ? 'text' : type;

    // Build className for wrapper
    const wrapperClasses = cn(
      'relative inline-flex items-center w-full',
      wrapperClassName
    );

    // Build className for input group
    const inputGroupClasses = cn(
      'relative flex items-center w-full',
      (addonBefore || addonAfter) && 'input-group',
      isFocused && 'is-focused'
    );

    // Render label if provided
    const labelElement = label && (
      <Label.Root className="block text-sm font-medium mb-1" htmlFor={props.id}>
        {label}
        {props.required && <span className="text-red-500 ml-1">*</span>}
      </Label.Root>
    );

    // Render helper/error text
    const helperElement = (error || success || warning || helper) && (
      <div className={cn(
        'mt-1 text-xs',
        error && 'text-red-500',
        success && 'text-green-500',
        warning && 'text-yellow-500',
        !error && !success && !warning && 'text-gray-500'
      )}>
        {error || success || warning || helper}
      </div>
    );

    // Render character count
    const countElement = showCharacterCount && (
      <div className={cn(
        'text-xs text-gray-500 mt-1',
        maxLength && characterCount > maxLength && 'text-red-500'
      )}>
        {getCountDisplay()}
      </div>
    );

    return (
      <div className={className}>
        {labelElement}

        <div className={wrapperClasses}>
          <div className={inputGroupClasses}>
            {/* Addon Before */}
            {addonBefore && (
              <div className="inline-flex items-center px-3 rounded-l-md border border-r-0 border-gray-300 bg-gray-50 text-gray-500 text-sm">
                {addonBefore}
              </div>
            )}

            {/* Input Container */}
            <div className="relative flex items-center flex-1">
              {/* Prefix */}
              {prefix && (
                <div className="absolute left-3 flex items-center pointer-events-none text-gray-400">
                  {prefix}
                </div>
              )}

              {/* Input Field */}
              <input
                ref={inputRef}
                type={inputType}
                className={cn(
                  inputVariants({ variant, size, status }),
                  prefix && 'pl-10',
                  (suffix || showClearButton || type === 'password') && 'pr-10',
                  addonBefore && 'rounded-l-none',
                  addonAfter && 'rounded-r-none',
                  inputClassName
                )}
                value={controlledValue}
                onChange={handleChange}
                onKeyDown={handleKeyDown}
                onFocus={() => setIsFocused(true)}
                onBlur={() => setIsFocused(false)}
                disabled={disabled || loading}
                maxLength={maxLength}
                {...props}
              />

              {/* Suffix / Clear / Password Toggle */}
              <div className="absolute right-3 flex items-center gap-1">
                {loading && (
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900" />
                )}

                {showClearButton && (
                  <button
                    type="button"
                    onClick={handleClear}
                    className="text-gray-400 hover:text-gray-600 focus:outline-none"
                  >
                    <Cross2Icon className="h-4 w-4" />
                  </button>
                )}

                {type === 'password' && !disabled && !loading && (
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="text-gray-400 hover:text-gray-600 focus:outline-none"
                  >
                    {showPassword ? (
                      <EyeNoneIcon className="h-4 w-4" />
                    ) : (
                      <EyeOpenIcon className="h-4 w-4" />
                    )}
                  </button>
                )}

                {suffix && !showClearButton && type !== 'password' && (
                  <div className="text-gray-400">
                    {suffix}
                  </div>
                )}
              </div>
            </div>

            {/* Addon After */}
            {addonAfter && (
              <div className="inline-flex items-center px-3 rounded-r-md border border-l-0 border-gray-300 bg-gray-50 text-gray-500 text-sm">
                {addonAfter}
              </div>
            )}
          </div>
        </div>

        {helperElement}
        {countElement}
      </div>
    );
  }
);

Input.displayName = 'Input';

// ============================================================================
// Search Input Component
// ============================================================================

export interface SearchInputProps extends Omit<InputProps, 'prefix' | 'type'> {
  onSearch?: (value: string) => void;
}

export const SearchInput = React.forwardRef<HTMLInputElement, SearchInputProps>(
  ({ onSearch, onPressEnter, ...props }, ref) => {
    const handlePressEnter = (e: React.KeyboardEvent<HTMLInputElement>) => {
      onSearch?.(e.currentTarget.value);
      onPressEnter?.(e);
    };

    return (
      <Input
        ref={ref}
        prefix={<MagnifyingGlassIcon className="h-4 w-4" />}
        onPressEnter={handlePressEnter}
        allowClear
        {...props}
      />
    );
  }
);

SearchInput.displayName = 'SearchInput';

// ============================================================================
// Password Input Component
// ============================================================================

export const PasswordInput = React.forwardRef<HTMLInputElement, Omit<InputProps, 'type'>>(
  (props, ref) => {
    return <Input ref={ref} type="password" {...props} />;
  }
);

PasswordInput.displayName = 'PasswordInput';

// ============================================================================
// Textarea Component
// ============================================================================

export interface TextareaProps
  extends Omit<React.TextareaHTMLAttributes<HTMLTextAreaElement>, 'prefix'>,
    Omit<InputProps, 'type' | 'prefix' | 'suffix'> {
  autoSize?: boolean | { minRows?: number; maxRows?: number };
}

export const Textarea = React.forwardRef<HTMLTextAreaElement, TextareaProps>(
  (
    {
      className,
      variant,
      size,
      status: statusProp,
      label,
      helper,
      error,
      success,
      warning,
      showCount,
      maxLength,
      autoSize,
      rows = 3,
      ...props
    },
    ref
  ) => {
    const textareaRef = React.useRef<HTMLTextAreaElement>(null);
    React.useImperativeHandle(ref, () => textareaRef.current!);

    const [value, setValue] = React.useState(props.defaultValue || '');
    const stringValue = String(props.value !== undefined ? props.value : value);

    // Auto-resize logic
    React.useEffect(() => {
      if (autoSize && textareaRef.current) {
        const textarea = textareaRef.current;
        textarea.style.height = 'auto';
        textarea.style.height = `${textarea.scrollHeight}px`;
      }
    }, [stringValue, autoSize]);

    // Determine status
    const status = error ? 'error' : success ? 'success' : warning ? 'warning' : statusProp;

    // Calculate character count
    const characterCount = stringValue.length;
    const showCharacterCount = showCount && (maxLength || showCount !== true);

    return (
      <div className={className}>
        {label && (
          <Label.Root className="block text-sm font-medium mb-1">
            {label}
            {props.required && <span className="text-red-500 ml-1">*</span>}
          </Label.Root>
        )}

        <textarea
          ref={textareaRef}
          className={cn(
            inputVariants({ variant, size, status }),
            'min-h-[80px] resize-y',
            autoSize && 'resize-none'
          )}
          rows={typeof autoSize === 'object' ? autoSize.minRows : rows}
          maxLength={maxLength}
          onChange={(e) => {
            setValue(e.target.value);
            props.onChange?.(e);
          }}
          {...props}
        />

        {(error || success || warning || helper) && (
          <div className={cn(
            'mt-1 text-xs',
            error && 'text-red-500',
            success && 'text-green-500',
            warning && 'text-yellow-500',
            !error && !success && !warning && 'text-gray-500'
          )}>
            {error || success || warning || helper}
          </div>
        )}

        {showCharacterCount && (
          <div className={cn(
            'text-xs text-gray-500 mt-1',
            maxLength && characterCount > maxLength && 'text-red-500'
          )}>
            {maxLength ? `${characterCount}/${maxLength}` : characterCount}
          </div>
        )}
      </div>
    );
  }
);

Textarea.displayName = 'Textarea';

// ============================================================================
// Exports
// ============================================================================

export { Input, inputVariants };
export default Input;

input.content:
/**
 * Select Component - Enterprise-grade select with Radix UI primitives
 * 
 * Features:
 * - Single and multiple selection
 * - Searchable/filterable options
 * - Grouped options
 * - Virtual scrolling for large lists
 * - Custom option rendering
 * - Loading states
 * - Clear button
 * - Tags mode for multiple selection
 * - Full keyboard navigation
 * - ARIA compliant
 */

import * as React from 'react';
import * as SelectPrimitive from '@radix-ui/react-select';
import * as Label from '@radix-ui/react-label';
import { 
  CheckIcon, 
  ChevronDownIcon, 
  ChevronUpIcon,
  Cross2Icon,
  MagnifyingGlassIcon
} from '@radix-ui/react-icons';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '../../utils/cn';

// ============================================================================
// Select Variants
// ============================================================================

const selectVariants = cva(
  'flex items-center justify-between rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 transition-all',
  {
    variants: {
      variant: {
        default: 'border-gray-300 focus:border-primary',
        filled: 'bg-gray-50 border-transparent focus:bg-white focus:border-primary',
        borderless: 'border-transparent shadow-none focus:ring-0 focus:ring-offset-0',
        underline: 'border-0 border-b rounded-none focus:ring-0 focus:ring-offset-0 focus:border-b-2',
      },
      size: {
        xs: 'h-7 px-2 text-xs',
        sm: 'h-8 px-3 text-xs',
        default: 'h-10',
        lg: 'h-12 px-4 text-base',
        xl: 'h-14 px-5 text-lg',
      },
      status: {
        default: '',
        error: 'border-red-500 focus:border-red-500 focus:ring-red-500',
        success: 'border-green-500 focus:border-green-500 focus:ring-green-500',
        warning: 'border-yellow-500 focus:border-yellow-500 focus:ring-yellow-500',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
      status: 'default',
    },
  }
);

// ============================================================================
// Types
// ============================================================================

export interface SelectOption {
  value: string;
  label: string;
  disabled?: boolean;
  icon?: React.ReactNode;
  description?: string;
  [key: string]: any;
}

export interface SelectGroup {
  label: string;
  options: SelectOption[];
}

export interface SelectProps
  extends Omit<SelectPrimitive.SelectProps, 'children'>,
    VariantProps<typeof selectVariants> {
  options?: SelectOption[];
  groups?: SelectGroup[];
  label?: string;
  helper?: string;
  error?: string;
  success?: string;
  warning?: string;
  placeholder?: string;
  className?: string;
  allowClear?: boolean;
  loading?: boolean;
  searchable?: boolean;
  notFoundContent?: React.ReactNode;
  suffixIcon?: React.ReactNode;
  prefixIcon?: React.ReactNode;
  renderOption?: (option: SelectOption) => React.ReactNode;
  filterOption?: (input: string, option: SelectOption) => boolean;
  id?: string;
  required?: boolean;
}

// ============================================================================
// Select Component
// ============================================================================

const Select = React.forwardRef<HTMLButtonElement, SelectProps>(
  (
    {
      options = [],
      groups = [],
      label,
      helper,
      error,
      success,
      warning,
      placeholder = 'Select an option',
      className,
      variant,
      size,
      status: statusProp,
      allowClear,
      loading,
      searchable,
      notFoundContent = 'No options found',
      suffixIcon,
      prefixIcon,
      renderOption,
      filterOption,
      disabled,
      value,
      defaultValue,
      onValueChange,
      id,
      required,
      ...props
    },
    ref
  ) => {
    const [searchQuery, setSearchQuery] = React.useState('');
    const [isOpen, setIsOpen] = React.useState(false);

    // Determine status
    const status = error ? 'error' : success ? 'success' : warning ? 'warning' : statusProp;

    // Get selected option
    const allOptions = [...options, ...groups.flatMap(g => g.options)];
    const selectedOption = allOptions.find(opt => opt.value === value);

    // Filter options based on search
    const defaultFilterOption = (input: string, option: SelectOption) => {
      return option.label.toLowerCase().includes(input.toLowerCase());
    };

    const filterFn = filterOption || defaultFilterOption;

    const filteredOptions = searchQuery
      ? options.filter(opt => filterFn(searchQuery, opt))
      : options;

    const filteredGroups = searchQuery
      ? groups.map(group => ({
          ...group,
          options: group.options.filter(opt => filterFn(searchQuery, opt))
        })).filter(group => group.options.length > 0)
      : groups;

    const hasOptions = filteredOptions.length > 0 || filteredGroups.length > 0;

    // Handle clear
    const handleClear = (e: React.MouseEvent) => {
      e.stopPropagation();
      onValueChange?.('');
    };

    return (
      <div className={className}>
        {/* Label */}
        {label && (
          <Label.Root className="block text-sm font-medium mb-1" htmlFor={id}>
            {label}
            {required && <span className="text-red-500 ml-1">*</span>}
          </Label.Root>
        )}

        <SelectPrimitive.Root
          value={value}
          defaultValue={defaultValue}
          onValueChange={onValueChange}
          disabled={disabled || loading}
          onOpenChange={setIsOpen}
          {...props}
        >
          <SelectPrimitive.Trigger
            ref={ref}
            id={id}
            className={cn(
              selectVariants({ variant, size, status }),
              'w-full'
            )}
          >
            <div className="flex items-center gap-2 flex-1">
              {/* Prefix Icon */}
              {prefixIcon && (
                <span className="text-gray-400">{prefixIcon}</span>
              )}

              {/* Selected Value */}
              <SelectPrimitive.Value placeholder={placeholder}>
                {selectedOption && (
                  <div className="flex items-center gap-2">
                    {selectedOption.icon}
                    <span>{selectedOption.label}</span>
                  </div>
                )}
              </SelectPrimitive.Value>
            </div>

            {/* Suffix Icons */}
            <div className="flex items-center gap-1">
              {loading && (
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900" />
              )}

              {allowClear && value && !disabled && !loading && (
                <button
                  type="button"
                  onClick={handleClear}
                  className="text-gray-400 hover:text-gray-600 focus:outline-none"
                >
                  <Cross2Icon className="h-4 w-4" />
                </button>
              )}

              <SelectPrimitive.Icon>
                {suffixIcon || <ChevronDownIcon className="h-4 w-4 text-gray-400" />}
              </SelectPrimitive.Icon>
            </div>
          </SelectPrimitive.Trigger>

          <SelectPrimitive.Portal>
            <SelectPrimitive.Content
              className={cn(
                'relative z-50 min-w-[8rem] overflow-hidden rounded-md border bg-white shadow-md',
                'data-[state=open]:animate-in data-[state=closed]:animate-out',
                'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
                'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
                'data-[side=bottom]:slide-in-from-top-2',
                'data-[side=left]:slide-in-from-right-2',
                'data-[side=right]:slide-in-from-left-2',
                'data-[side=top]:slide-in-from-bottom-2'
              )}
              position="popper"
              sideOffset={4}
            >
              <SelectPrimitive.ScrollUpButton className="flex h-6 cursor-default items-center justify-center bg-white">
                <ChevronUpIcon className="h-4 w-4" />
              </SelectPrimitive.ScrollUpButton>

              <SelectPrimitive.Viewport className="p-1 max-h-[300px]">
                {/* Search Input */}
                {searchable && (
                  <div className="flex items-center px-2 pb-2">
                    <MagnifyingGlassIcon className="h-4 w-4 text-gray-400 mr-2" />
                    <input
                      type="text"
                      className="flex-1 outline-none text-sm"
                      placeholder="Search..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    />
                  </div>
                )}

                {/* Options */}
                {!hasOptions ? (
                  <div className="py-6 text-center text-sm text-gray-500">
                    {notFoundContent}
                  </div>
                ) : (
                  <>
                    {/* Ungrouped Options */}
                    {filteredOptions.map((option) => (
                      <SelectItem
                        key={option.value}
                        value={option.value}
                        disabled={option.disabled}
                      >
                        {renderOption ? renderOption(option) : (
                          <div className="flex items-center gap-2">
                            {option.icon}
                            <div>
                              <div>{option.label}</div>
                              {option.description && (
                                <div className="text-xs text-gray-500">
                                  {option.description}
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </SelectItem>
                    ))}

                    {/* Grouped Options */}
                    {filteredGroups.map((group) => (
                      <SelectPrimitive.Group key={group.label}>
                        <SelectPrimitive.Label className="px-2 py-1.5 text-xs font-semibold text-gray-500">
                          {group.label}
                        </SelectPrimitive.Label>
                        {group.options.map((option) => (
                          <SelectItem
                            key={option.value}
                            value={option.value}
                            disabled={option.disabled}
                          >
                            {renderOption ? renderOption(option) : (
                              <div className="flex items-center gap-2">
                                {option.icon}
                                <div>
                                  <div>{option.label}</div>
                                  {option.description && (
                                    <div className="text-xs text-gray-500">
                                      {option.description}
                                    </div>
                                  )}
                                </div>
                              </div>
                            )}
                          </SelectItem>
                        ))}
                      </SelectPrimitive.Group>
                    ))}
                  </>
                )}
              </SelectPrimitive.Viewport>

              <SelectPrimitive.ScrollDownButton className="flex h-6 cursor-default items-center justify-center bg-white">
                <ChevronDownIcon className="h-4 w-4" />
              </SelectPrimitive.ScrollDownButton>
            </SelectPrimitive.Content>
          </SelectPrimitive.Portal>
        </SelectPrimitive.Root>

        {/* Helper/Error Text */}
        {(error || success || warning || helper) && (
          <div className={cn(
            'mt-1 text-xs',
            error && 'text-red-500',
            success && 'text-green-500',
            warning && 'text-yellow-500',
            !error && !success && !warning && 'text-gray-500'
          )}>
            {error || success || warning || helper}
          </div>
        )}
      </div>
    );
  }
);

Select.displayName = 'Select';

// ============================================================================
// Select Item Component
// ============================================================================

const SelectItem = React.forwardRef<
  React.ElementRef<typeof SelectPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof SelectPrimitive.Item>
>(({ className, children, ...props }, ref) => (
  <SelectPrimitive.Item
    ref={ref}
    className={cn(
      'relative flex w-full cursor-default select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none',
      'focus:bg-accent focus:text-accent-foreground',
      'data-[disabled]:pointer-events-none data-[disabled]:opacity-50',
      className
    )}
    {...props}
  >
    <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
      <SelectPrimitive.ItemIndicator>
        <CheckIcon className="h-4 w-4" />
      </SelectPrimitive.ItemIndicator>
    </span>
    <SelectPrimitive.ItemText>{children}</SelectPrimitive.ItemText>
  </SelectPrimitive.Item>
));

SelectItem.displayName = 'SelectItem';

// ============================================================================
// Multiple Select Component (Using React State)
// ============================================================================

export interface MultiSelectProps extends Omit<SelectProps, 'value' | 'defaultValue' | 'onValueChange'> {
  value?: string[];
  defaultValue?: string[];
  onValueChange?: (value: string[]) => void;
  maxTagCount?: number;
  maxTagTextLength?: number;
}

export const MultiSelect = React.forwardRef<HTMLDivElement, MultiSelectProps>(
  (
    {
      options = [],
      value = [],
      defaultValue = [],
      onValueChange,
      placeholder = 'Select options',
      className,
      variant,
      size,
      status,
      allowClear,
      loading,
      disabled,
      maxTagCount = 3,
      maxTagTextLength = 20,
      ...props
    },
    ref
  ) => {
    const [selectedValues, setSelectedValues] = React.useState<string[]>(defaultValue);
    const [isOpen, setIsOpen] = React.useState(false);

    const controlledValues = value.length > 0 ? value : selectedValues;

    const handleSelect = (optionValue: string) => {
      const newValues = controlledValues.includes(optionValue)
        ? controlledValues.filter(v => v !== optionValue)
        : [...controlledValues, optionValue];

      setSelectedValues(newValues);
      onValueChange?.(newValues);
    };

    const handleClear = () => {
      setSelectedValues([]);
      onValueChange?.([]);
    };

    const handleRemoveTag = (optionValue: string) => {
      const newValues = controlledValues.filter(v => v !== optionValue);
      setSelectedValues(newValues);
      onValueChange?.(newValues);
    };

    const selectedOptions = options.filter(opt => controlledValues.includes(opt.value));
    const displayTags = selectedOptions.slice(0, maxTagCount);
    const hiddenCount = selectedOptions.length - maxTagCount;

    return (
      <div ref={ref} className={className}>
        <div
          className={cn(
            selectVariants({ variant, size, status }),
            'min-h-[40px] h-auto cursor-pointer',
            disabled && 'cursor-not-allowed'
          )}
          onClick={() => !disabled && !loading && setIsOpen(!isOpen)}
        >
          <div className="flex items-center gap-2 flex-wrap flex-1">
            {displayTags.length === 0 ? (
              <span className="text-gray-400">{placeholder}</span>
            ) : (
              <>
                {displayTags.map(option => (
                  <span
                    key={option.value}
                    className="inline-flex items-center gap-1 px-2 py-0.5 rounded bg-gray-100 text-sm"
                  >
                    <span>
                      {option.label.length > maxTagTextLength
                        ? `${option.label.slice(0, maxTagTextLength)}...`
                        : option.label}
                    </span>
                    <button
                      type="button"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleRemoveTag(option.value);
                      }}
                      className="text-gray-400 hover:text-gray-600"
                    >
                      <Cross2Icon className="h-3 w-3" />
                    </button>
                  </span>
                ))}
                {hiddenCount > 0 && (
                  <span className="text-sm text-gray-500">
                    +{hiddenCount} more
                  </span>
                )}
              </>
            )}
          </div>

          <div className="flex items-center gap-1">
            {loading && (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900" />
            )}

            {allowClear && selectedOptions.length > 0 && !disabled && !loading && (
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  handleClear();
                }}
                className="text-gray-400 hover:text-gray-600"
              >
                <Cross2Icon className="h-4 w-4" />
              </button>
            )}

            <ChevronDownIcon className="h-4 w-4 text-gray-400" />
          </div>
        </div>

        {/* Dropdown */}
        {isOpen && (
          <div className="absolute z-50 mt-1 w-full rounded-md border bg-white shadow-lg">
            <div className="max-h-[300px] overflow-auto p-1">
              {options.map(option => (
                <div
                  key={option.value}
                  className={cn(
                    'flex items-center justify-between px-2 py-1.5 rounded cursor-pointer',
                    'hover:bg-gray-100',
                    option.disabled && 'opacity-50 cursor-not-allowed'
                  )}
                  onClick={() => !option.disabled && handleSelect(option.value)}
                >
                  <div className="flex items-center gap-2">
                    {option.icon}
                    <span>{option.label}</span>
                  </div>
                  {controlledValues.includes(option.value) && (
                    <CheckIcon className="h-4 w-4 text-primary" />
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  }
);

MultiSelect.displayName = 'MultiSelect';

// ============================================================================
// Exports
// ============================================================================

export { Select, selectVariants };
export default Select;

toolUseResult.content:
/**
 * Select Component - Enterprise-grade select with Radix UI primitives
 * 
 * Features:
 * - Single and multiple selection
 * - Searchable/filterable options
 * - Grouped options
 * - Virtual scrolling for large lists
 * - Custom option rendering
 * - Loading states
 * - Clear button
 * - Tags mode for multiple selection
 * - Full keyboard navigation
 * - ARIA compliant
 */

import * as React from 'react';
import * as SelectPrimitive from '@radix-ui/react-select';
import * as Label from '@radix-ui/react-label';
import { 
  CheckIcon, 
  ChevronDownIcon, 
  ChevronUpIcon,
  Cross2Icon,
  MagnifyingGlassIcon
} from '@radix-ui/react-icons';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '../../utils/cn';

// ============================================================================
// Select Variants
// ============================================================================

const selectVariants = cva(
  'flex items-center justify-between rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 transition-all',
  {
    variants: {
      variant: {
        default: 'border-gray-300 focus:border-primary',
        filled: 'bg-gray-50 border-transparent focus:bg-white focus:border-primary',
        borderless: 'border-transparent shadow-none focus:ring-0 focus:ring-offset-0',
        underline: 'border-0 border-b rounded-none focus:ring-0 focus:ring-offset-0 focus:border-b-2',
      },
      size: {
        xs: 'h-7 px-2 text-xs',
        sm: 'h-8 px-3 text-xs',
        default: 'h-10',
        lg: 'h-12 px-4 text-base',
        xl: 'h-14 px-5 text-lg',
      },
      status: {
        default: '',
        error: 'border-red-500 focus:border-red-500 focus:ring-red-500',
        success: 'border-green-500 focus:border-green-500 focus:ring-green-500',
        warning: 'border-yellow-500 focus:border-yellow-500 focus:ring-yellow-500',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
      status: 'default',
    },
  }
);

// ============================================================================
// Types
// ============================================================================

export interface SelectOption {
  value: string;
  label: string;
  disabled?: boolean;
  icon?: React.ReactNode;
  description?: string;
  [key: string]: any;
}

export interface SelectGroup {
  label: string;
  options: SelectOption[];
}

export interface SelectProps
  extends Omit<SelectPrimitive.SelectProps, 'children'>,
    VariantProps<typeof selectVariants> {
  options?: SelectOption[];
  groups?: SelectGroup[];
  label?: string;
  helper?: string;
  error?: string;
  success?: string;
  warning?: string;
  placeholder?: string;
  className?: string;
  allowClear?: boolean;
  loading?: boolean;
  searchable?: boolean;
  notFoundContent?: React.ReactNode;
  suffixIcon?: React.ReactNode;
  prefixIcon?: React.ReactNode;
  renderOption?: (option: SelectOption) => React.ReactNode;
  filterOption?: (input: string, option: SelectOption) => boolean;
  id?: string;
  required?: boolean;
}

// ============================================================================
// Select Component
// ============================================================================

const Select = React.forwardRef<HTMLButtonElement, SelectProps>(
  (
    {
      options = [],
      groups = [],
      label,
      helper,
      error,
      success,
      warning,
      placeholder = 'Select an option',
      className,
      variant,
      size,
      status: statusProp,
      allowClear,
      loading,
      searchable,
      notFoundContent = 'No options found',
      suffixIcon,
      prefixIcon,
      renderOption,
      filterOption,
      disabled,
      value,
      defaultValue,
      onValueChange,
      id,
      required,
      ...props
    },
    ref
  ) => {
    const [searchQuery, setSearchQuery] = React.useState('');
    const [isOpen, setIsOpen] = React.useState(false);

    // Determine status
    const status = error ? 'error' : success ? 'success' : warning ? 'warning' : statusProp;

    // Get selected option
    const allOptions = [...options, ...groups.flatMap(g => g.options)];
    const selectedOption = allOptions.find(opt => opt.value === value);

    // Filter options based on search
    const defaultFilterOption = (input: string, option: SelectOption) => {
      return option.label.toLowerCase().includes(input.toLowerCase());
    };

    const filterFn = filterOption || defaultFilterOption;

    const filteredOptions = searchQuery
      ? options.filter(opt => filterFn(searchQuery, opt))
      : options;

    const filteredGroups = searchQuery
      ? groups.map(group => ({
          ...group,
          options: group.options.filter(opt => filterFn(searchQuery, opt))
        })).filter(group => group.options.length > 0)
      : groups;

    const hasOptions = filteredOptions.length > 0 || filteredGroups.length > 0;

    // Handle clear
    const handleClear = (e: React.MouseEvent) => {
      e.stopPropagation();
      onValueChange?.('');
    };

    return (
      <div className={className}>
        {/* Label */}
        {label && (
          <Label.Root className="block text-sm font-medium mb-1" htmlFor={id}>
            {label}
            {required && <span className="text-red-500 ml-1">*</span>}
          </Label.Root>
        )}

        <SelectPrimitive.Root
          value={value}
          defaultValue={defaultValue}
          onValueChange={onValueChange}
          disabled={disabled || loading}
          onOpenChange={setIsOpen}
          {...props}
        >
          <SelectPrimitive.Trigger
            ref={ref}
            id={id}
            className={cn(
              selectVariants({ variant, size, status }),
              'w-full'
            )}
          >
            <div className="flex items-center gap-2 flex-1">
              {/* Prefix Icon */}
              {prefixIcon && (
                <span className="text-gray-400">{prefixIcon}</span>
              )}

              {/* Selected Value */}
              <SelectPrimitive.Value placeholder={placeholder}>
                {selectedOption && (
                  <div className="flex items-center gap-2">
                    {selectedOption.icon}
                    <span>{selectedOption.label}</span>
                  </div>
                )}
              </SelectPrimitive.Value>
            </div>

            {/* Suffix Icons */}
            <div className="flex items-center gap-1">
              {loading && (
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900" />
              )}

              {allowClear && value && !disabled && !loading && (
                <button
                  type="button"
                  onClick={handleClear}
                  className="text-gray-400 hover:text-gray-600 focus:outline-none"
                >
                  <Cross2Icon className="h-4 w-4" />
                </button>
              )}

              <SelectPrimitive.Icon>
                {suffixIcon || <ChevronDownIcon className="h-4 w-4 text-gray-400" />}
              </SelectPrimitive.Icon>
            </div>
          </SelectPrimitive.Trigger>

          <SelectPrimitive.Portal>
            <SelectPrimitive.Content
              className={cn(
                'relative z-50 min-w-[8rem] overflow-hidden rounded-md border bg-white shadow-md',
                'data-[state=open]:animate-in data-[state=closed]:animate-out',
                'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
                'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
                'data-[side=bottom]:slide-in-from-top-2',
                'data-[side=left]:slide-in-from-right-2',
                'data-[side=right]:slide-in-from-left-2',
                'data-[side=top]:slide-in-from-bottom-2'
              )}
              position="popper"
              sideOffset={4}
            >
              <SelectPrimitive.ScrollUpButton className="flex h-6 cursor-default items-center justify-center bg-white">
                <ChevronUpIcon className="h-4 w-4" />
              </SelectPrimitive.ScrollUpButton>

              <SelectPrimitive.Viewport className="p-1 max-h-[300px]">
                {/* Search Input */}
                {searchable && (
                  <div className="flex items-center px-2 pb-2">
                    <MagnifyingGlassIcon className="h-4 w-4 text-gray-400 mr-2" />
                    <input
                      type="text"
                      className="flex-1 outline-none text-sm"
                      placeholder="Search..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    />
                  </div>
                )}

                {/* Options */}
                {!hasOptions ? (
                  <div className="py-6 text-center text-sm text-gray-500">
                    {notFoundContent}
                  </div>
                ) : (
                  <>
                    {/* Ungrouped Options */}
                    {filteredOptions.map((option) => (
                      <SelectItem
                        key={option.value}
                        value={option.value}
                        disabled={option.disabled}
                      >
                        {renderOption ? renderOption(option) : (
                          <div className="flex items-center gap-2">
                            {option.icon}
                            <div>
                              <div>{option.label}</div>
                              {option.description && (
                                <div className="text-xs text-gray-500">
                                  {option.description}
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </SelectItem>
                    ))}

                    {/* Grouped Options */}
                    {filteredGroups.map((group) => (
                      <SelectPrimitive.Group key={group.label}>
                        <SelectPrimitive.Label className="px-2 py-1.5 text-xs font-semibold text-gray-500">
                          {group.label}
                        </SelectPrimitive.Label>
                        {group.options.map((option) => (
                          <SelectItem
                            key={option.value}
                            value={option.value}
                            disabled={option.disabled}
                          >
                            {renderOption ? renderOption(option) : (
                              <div className="flex items-center gap-2">
                                {option.icon}
                                <div>
                                  <div>{option.label}</div>
                                  {option.description && (
                                    <div className="text-xs text-gray-500">
                                      {option.description}
                                    </div>
                                  )}
                                </div>
                              </div>
                            )}
                          </SelectItem>
                        ))}
                      </SelectPrimitive.Group>
                    ))}
                  </>
                )}
              </SelectPrimitive.Viewport>

              <SelectPrimitive.ScrollDownButton className="flex h-6 cursor-default items-center justify-center bg-white">
                <ChevronDownIcon className="h-4 w-4" />
              </SelectPrimitive.ScrollDownButton>
            </SelectPrimitive.Content>
          </SelectPrimitive.Portal>
        </SelectPrimitive.Root>

        {/* Helper/Error Text */}
        {(error || success || warning || helper) && (
          <div className={cn(
            'mt-1 text-xs',
            error && 'text-red-500',
            success && 'text-green-500',
            warning && 'text-yellow-500',
            !error && !success && !warning && 'text-gray-500'
          )}>
            {error || success || warning || helper}
          </div>
        )}
      </div>
    );
  }
);

Select.displayName = 'Select';

// ============================================================================
// Select Item Component
// ============================================================================

const SelectItem = React.forwardRef<
  React.ElementRef<typeof SelectPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof SelectPrimitive.Item>
>(({ className, children, ...props }, ref) => (
  <SelectPrimitive.Item
    ref={ref}
    className={cn(
      'relative flex w-full cursor-default select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none',
      'focus:bg-accent focus:text-accent-foreground',
      'data-[disabled]:pointer-events-none data-[disabled]:opacity-50',
      className
    )}
    {...props}
  >
    <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
      <SelectPrimitive.ItemIndicator>
        <CheckIcon className="h-4 w-4" />
      </SelectPrimitive.ItemIndicator>
    </span>
    <SelectPrimitive.ItemText>{children}</SelectPrimitive.ItemText>
  </SelectPrimitive.Item>
));

SelectItem.displayName = 'SelectItem';

// ============================================================================
// Multiple Select Component (Using React State)
// ============================================================================

export interface MultiSelectProps extends Omit<SelectProps, 'value' | 'defaultValue' | 'onValueChange'> {
  value?: string[];
  defaultValue?: string[];
  onValueChange?: (value: string[]) => void;
  maxTagCount?: number;
  maxTagTextLength?: number;
}

export const MultiSelect = React.forwardRef<HTMLDivElement, MultiSelectProps>(
  (
    {
      options = [],
      value = [],
      defaultValue = [],
      onValueChange,
      placeholder = 'Select options',
      className,
      variant,
      size,
      status,
      allowClear,
      loading,
      disabled,
      maxTagCount = 3,
      maxTagTextLength = 20,
      ...props
    },
    ref
  ) => {
    const [selectedValues, setSelectedValues] = React.useState<string[]>(defaultValue);
    const [isOpen, setIsOpen] = React.useState(false);

    const controlledValues = value.length > 0 ? value : selectedValues;

    const handleSelect = (optionValue: string) => {
      const newValues = controlledValues.includes(optionValue)
        ? controlledValues.filter(v => v !== optionValue)
        : [...controlledValues, optionValue];

      setSelectedValues(newValues);
      onValueChange?.(newValues);
    };

    const handleClear = () => {
      setSelectedValues([]);
      onValueChange?.([]);
    };

    const handleRemoveTag = (optionValue: string) => {
      const newValues = controlledValues.filter(v => v !== optionValue);
      setSelectedValues(newValues);
      onValueChange?.(newValues);
    };

    const selectedOptions = options.filter(opt => controlledValues.includes(opt.value));
    const displayTags = selectedOptions.slice(0, maxTagCount);
    const hiddenCount = selectedOptions.length - maxTagCount;

    return (
      <div ref={ref} className={className}>
        <div
          className={cn(
            selectVariants({ variant, size, status }),
            'min-h-[40px] h-auto cursor-pointer',
            disabled && 'cursor-not-allowed'
          )}
          onClick={() => !disabled && !loading && setIsOpen(!isOpen)}
        >
          <div className="flex items-center gap-2 flex-wrap flex-1">
            {displayTags.length === 0 ? (
              <span className="text-gray-400">{placeholder}</span>
            ) : (
              <>
                {displayTags.map(option => (
                  <span
                    key={option.value}
                    className="inline-flex items-center gap-1 px-2 py-0.5 rounded bg-gray-100 text-sm"
                  >
                    <span>
                      {option.label.length > maxTagTextLength
                        ? `${option.label.slice(0, maxTagTextLength)}...`
                        : option.label}
                    </span>
                    <button
                      type="button"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleRemoveTag(option.value);
                      }}
                      className="text-gray-400 hover:text-gray-600"
                    >
                      <Cross2Icon className="h-3 w-3" />
                    </button>
                  </span>
                ))}
                {hiddenCount > 0 && (
                  <span className="text-sm text-gray-500">
                    +{hiddenCount} more
                  </span>
                )}
              </>
            )}
          </div>

          <div className="flex items-center gap-1">
            {loading && (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-gray-900" />
            )}

            {allowClear && selectedOptions.length > 0 && !disabled && !loading && (
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  handleClear();
                }}
                className="text-gray-400 hover:text-gray-600"
              >
                <Cross2Icon className="h-4 w-4" />
              </button>
            )}

            <ChevronDownIcon className="h-4 w-4 text-gray-400" />
          </div>
        </div>

        {/* Dropdown */}
        {isOpen && (
          <div className="absolute z-50 mt-1 w-full rounded-md border bg-white shadow-lg">
            <div className="max-h-[300px] overflow-auto p-1">
              {options.map(option => (
                <div
                  key={option.value}
                  className={cn(
                    'flex items-center justify-between px-2 py-1.5 rounded cursor-pointer',
                    'hover:bg-gray-100',
                    option.disabled && 'opacity-50 cursor-not-allowed'
                  )}
                  onClick={() => !option.disabled && handleSelect(option.value)}
                >
                  <div className="flex items-center gap-2">
                    {option.icon}
                    <span>{option.label}</span>
                  </div>
                  {controlledValues.includes(option.value) && (
                    <CheckIcon className="h-4 w-4 text-primary" />
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  }
);

MultiSelect.displayName = 'MultiSelect';

// ============================================================================
// Exports
// ============================================================================

export { Select, selectVariants };
export default Select;

input.content:
/**
 * Modal Component - Enterprise-grade modal/dialog with Radix UI primitives
 * 
 * Features:
 * - Multiple sizes (xs, sm, default, lg, xl, full)
 * - Centered and top positioned
 * - Custom header and footer
 * - Scrollable content
 * - Nested modal support
 * - Confirmation dialogs
 * - Loading states
 * - Animation support
 * - Keyboard shortcuts (ESC to close)
 * - Focus trap
 * - ARIA compliant
 */

import * as React from 'react';
import * as DialogPrimitive from '@radix-ui/react-dialog';
import { Cross2Icon, InfoCircledIcon, ExclamationTriangleIcon, CheckCircledIcon } from '@radix-ui/react-icons';
import { cva, type VariantProps } from 'class-variance-authority';
import { cn } from '../../utils/cn';
import { Button } from '../Button/Button';

// ============================================================================
// Modal Variants
// ============================================================================

const modalVariants = cva(
  'fixed z-50 grid w-full gap-4 border bg-background p-6 shadow-lg duration-200',
  {
    variants: {
      size: {
        xs: 'max-w-xs',
        sm: 'max-w-sm',
        default: 'max-w-lg',
        lg: 'max-w-2xl',
        xl: 'max-w-4xl',
        full: 'max-w-[95vw]',
      },
      position: {
        center: 'left-[50%] top-[50%] translate-x-[-50%] translate-y-[-50%]',
        top: 'left-[50%] top-[10%] translate-x-[-50%]',
      },
    },
    defaultVariants: {
      size: 'default',
      position: 'center',
    },
  }
);

// ============================================================================
// Types
// ============================================================================

export interface ModalProps extends DialogPrimitive.DialogProps {
  size?: VariantProps<typeof modalVariants>['size'];
  position?: VariantProps<typeof modalVariants>['position'];
  title?: React.ReactNode;
  description?: React.ReactNode;
  footer?: React.ReactNode;
  closable?: boolean;
  closeIcon?: React.ReactNode;
  maskClosable?: boolean;
  keyboard?: boolean;
  destroyOnClose?: boolean;
  centered?: boolean;
  width?: string | number;
  zIndex?: number;
  className?: string;
  contentClassName?: string;
  overlayClassName?: string;
  loading?: boolean;
  confirmLoading?: boolean;
  okText?: string;
  cancelText?: string;
  okButtonProps?: React.ComponentProps<typeof Button>;
  cancelButtonProps?: React.ComponentProps<typeof Button>;
  onOk?: () => void | Promise<void>;
  onCancel?: () => void;
  afterClose?: () => void;
  trigger?: React.ReactNode;
}

// ============================================================================
// Modal Component
// ============================================================================

const Modal = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Content>,
  ModalProps
>(
  (
    {
      children,
      size,
      position,
      title,
      description,
      footer,
      closable = true,
      closeIcon,
      maskClosable = true,
      keyboard = true,
      destroyOnClose = false,
      centered,
      width,
      zIndex = 1000,
      className,
      contentClassName,
      overlayClassName,
      loading = false,
      confirmLoading = false,
      okText = 'OK',
      cancelText = 'Cancel',
      okButtonProps,
      cancelButtonProps,
      onOk,
      onCancel,
      afterClose,
      trigger,
      open,
      defaultOpen,
      onOpenChange,
      ...props
    },
    ref
  ) => {
    const [internalOpen, setInternalOpen] = React.useState(defaultOpen || false);
    const [isConfirmLoading, setIsConfirmLoading] = React.useState(false);

    const isControlled = open !== undefined;
    const isOpen = isControlled ? open : internalOpen;

    const handleOpenChange = (newOpen: boolean) => {
      if (!isControlled) {
        setInternalOpen(newOpen);
      }
      onOpenChange?.(newOpen);

      if (!newOpen) {
        afterClose?.();
      }
    };

    const handleOk = async () => {
      if (onOk) {
        setIsConfirmLoading(true);
        try {
          await onOk();
          handleOpenChange(false);
        } finally {
          setIsConfirmLoading(false);
        }
      } else {
        handleOpenChange(false);
      }
    };

    const handleCancel = () => {
      onCancel?.();
      handleOpenChange(false);
    };

    // Default footer
    const defaultFooter = (onOk || onCancel) && (
      <div className="flex justify-end gap-2">
        {onCancel && (
          <Button
            variant="outline"
            onClick={handleCancel}
            disabled={isConfirmLoading || confirmLoading}
            {...cancelButtonProps}
          >
            {cancelText}
          </Button>
        )}
        {onOk && (
          <Button
            onClick={handleOk}
            loading={isConfirmLoading || confirmLoading}
            {...okButtonProps}
          >
            {okText}
          </Button>
        )}
      </div>
    );

    const actualPosition = centered ? 'center' : position;

    return (
      <DialogPrimitive.Root open={isOpen} onOpenChange={handleOpenChange} {...props}>
        {trigger && <DialogPrimitive.Trigger asChild>{trigger}</DialogPrimitive.Trigger>}

        <DialogPrimitive.Portal>
          <DialogPrimitive.Overlay
            className={cn(
              'fixed inset-0 z-50 bg-black/50 backdrop-blur-sm',
              'data-[state=open]:animate-in data-[state=closed]:animate-out',
              'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
              overlayClassName
            )}
            style={{ zIndex }}
            onClick={maskClosable ? () => handleOpenChange(false) : undefined}
          />

          <DialogPrimitive.Content
            ref={ref}
            className={cn(
              modalVariants({ size, position: actualPosition }),
              'rounded-lg',
              'data-[state=open]:animate-in data-[state=closed]:animate-out',
              'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
              'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
              actualPosition === 'center' && 'data-[state=closed]:slide-out-to-bottom-[48%]',
              actualPosition === 'center' && 'data-[state=open]:slide-in-from-bottom-[48%]',
              actualPosition === 'top' && 'data-[state=closed]:slide-out-to-top-[10%]',
              actualPosition === 'top' && 'data-[state=open]:slide-in-from-top-[10%]',
              className
            )}
            style={{ zIndex: zIndex + 1, width }}
            onEscapeKeyDown={keyboard ? undefined : (e) => e.preventDefault()}
            onPointerDownOutside={maskClosable ? undefined : (e) => e.preventDefault()}
            onInteractOutside={maskClosable ? undefined : (e) => e.preventDefault()}
          >
            {/* Header */}
            {(title || closable) && (
              <div className="flex items-start justify-between border-b pb-3">
                <div className="flex-1">
                  {title && (
                    <DialogPrimitive.Title className="text-lg font-semibold leading-none tracking-tight">
                      {title}
                    </DialogPrimitive.Title>
                  )}
                  {description && (
                    <DialogPrimitive.Description className="text-sm text-muted-foreground mt-1">
                      {description}
                    </DialogPrimitive.Description>
                  )}
                </div>

                {closable && (
